INSERT INTO enzyme VALUES ( 'EC 3.5.1.1','Asparaginase','L-asparaginase','NULL','NULL','L-asparagine + H(2)O = L-aspartate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.2','Glutaminase','L-glutamine amidohydrolase','NULL','NULL','L-glutamine + H(2)O = L-glutamate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.3','Omega-amidase','NULL','NULL','Acts on glutaramate, succinamate and their 2-oxo derivatives.','A monoamide of a dicarboxylic acid + H(2)O = a dicarboxylate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.4','Amidase','Acylamidase','NULL','NULL','A monocarboxylic acid amide + H(2)O = a monocarboxylate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.5','Urease','NULL','Nickel.','NULL','Urea + H(2)O = CO(2) + 2 NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.6','Beta-ureidopropionase','NULL','NULL','The animal enzyme also acts on beta-ureidoisobutyrate.','N-carbamoyl-beta-alanine + H(2)O = beta-alanine + CO(2) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.7','Ureidosuccinase','NULL','NULL','NULL','N-carbamoyl-L-aspartate + H(2)O = L-aspartate + CO(2) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.8','Formylaspartate deformylase','NULL','NULL','NULL','N-formyl-L-aspartate + H(2)O = formate + L-aspartate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.9','Arylformamidase','Formylase','NULL','Also acts on other aromatic formylamines.','N-formyl-L-kynurenine + H(2)O = formate + L-kynurenine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.10','Formyltetrahydrofolate deformylase','Formyl-FH(4) hydrolase','NULL','NULL','10-formyltetrahydrofolate + H(2)O = formate + tetrahydrofolate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.11','Penicillin amidase','Penicillin acylase','NULL','NULL','Penicillin + H(2)O = a carboxylate + 6-aminopenicillanate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.12','Biotinidase','Biotinase','NULL','Also acts on biotin esters.','Biotin amide + H(2)O = biotin + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.13','Aryl-acylamidase','NULL','NULL','Also acts on 4-substituted anilides.','An anilide + H(2)O = a carboxylate + aniline.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.14','N-acyl-aliphatic-L-amino acid amidohydrolase','Acylase I','Zn(2+).','The enzyme is found in animals and is involved in the hydrolysis ofN-acylated or N-acetylated amino acids (except L-aspartate).It acts on mercapturic acids (S-conjugates of N-acetyl-L-cysteine)and neutral aliphatic N-acyl-alpha-amino acids.Some bacterial aminoacylases demonstrate substrate specificity ofboth EC 3.5.1.14 and EC 3.5.1.114.Cf. EC 3.5.1.15 and EC 3.5.1.114.','(1) An N-acyl-aliphatic-L-amino acid + H(2)O = an aliphatic L-amino acid+ a carboxylate.(2) An N-acetyl-L-cysteine-S-conjugate + H(2)O = an L-cysteine-S-conjugate + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.15','Aspartoacylase','Aminoacylase II','NULL','NULL','N-acyl-L-aspartate + H(2)O = a carboxylate + L-aspartate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.16','Acetylornithine deacetylase','Acetylornithinase','NULL','Also hydrolyzes N-acetylmethionine.','N(2)-acetyl-L-ornithine + H(2)O = acetate + L-ornithine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.17','Acyl-lysine deacylase','Epsilon-lysine acylase','NULL','NULL','N(6)-acyl-L-lysine + H(2)O = a carboxylate + L-lysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.18','Succinyl-diaminopimelate desuccinylase','NULL','NULL','NULL','N-succinyl-LL-2,6-diaminoheptanedioate + H(2)O = succinate + LL-2,6-diaminoheptanedioate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.19','Nicotinamidase','NAMase','NULL','NULL','Nicotinamide + H(2)O = nicotinate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.20','Citrullinase','NULL','NULL','NULL','L-citrulline + H(2)O = L-ornithine + CO(2) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.21','N-acetyl-beta-alanine deacetylase','NULL','NULL','NULL','N-acetyl-beta-alanine + H(2)O = acetate + beta-alanine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.22','Pantothenase','NULL','NULL','NULL','(R)-pantothenate + H(2)O = (R)-pantoate + beta-alanine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.23','Ceramidase','Acylsphingosine deacylase','NULL','NULL','N-acylsphingosine + H(2)O = a carboxylate + sphingosine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.24','Choloylglycine hydrolase','Bile salt hydrolase','NULL','Also acts on the 3-alpha,12-alpha-dihydroxy-derivative, and oncholoyl-taurine.','3-alpha,7-alpha,12-alpha-trihydroxy-5-beta-cholan-24-oylglycine + H(2)O =3-alpha,7-alpha,12-alpha-trihydroxy-5-beta-cholanate + glycine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.25','N-acetylglucosamine-6-phosphate deacetylase','NULL','NULL','Formerly EC 3.5.1.80.','N-acetyl-D-glucosamine 6-phosphate + H(2)O = D-glucosamine 6-phosphate +acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.26','N(4)-(beta-N-acetylglucosaminyl)-L-asparaginase','Aspartylglucosaminidase','NULL','Acts only on asparagine-oligosaccharides containing one amino acid,i.e. the asparagine has free alpha-amino and alpha-carboxyl groups(cf. EC 3.5.1.52).Formerly EC 3.5.1.37.','N(4)-(beta-N-acetyl-D-glucosaminyl)-L-asparagine + H(2)O = N-acetyl-beta-D-glucosaminylamine + L-aspartate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.27','N-formylmethionylaminoacyl-tRNA deformylase','NULL','NULL','NULL','N-formyl-L-methionylaminoacyl-tRNA + H(2)O = formate +L-methionylaminoacyl-tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.28','N-acetylmuramoyl-L-alanine amidase','NULL','NULL','Formerly EC 3.4.12.5, EC 3.4.17.7 and EC 3.4.19.10.','Hydrolyzes the link between N-acetylmuramoyl residues and L-amino acidresidues in certain cell-wall glycopeptides.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.29','2-(acetamidomethylene)succinate hydrolase','NULL','NULL','Involved in the degradation of pyridoxin in Pseudomonas.','2-(acetamidomethylene)succinate + 2 H(2)O = acetate + succinatesemialdehyde + NH(3) + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.30','5-aminopentanamidase','5-aminovaleramidase','NULL','The enzyme from Pseudomonas putida also acts on 4-aminobutanamideand, more slowly, on 6-aminohexanamide.','5-aminopentanamide + H(2)O = 5-aminopentanoate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.31','Formylmethionine deformylase','NULL','NULL','NULL','N-formyl-L-methionine + H(2)O = formate + L-methionine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.32','Hippurate hydrolase','Benzoylglycine amidohydrolase','NULL','Acts on various N-benzoylamino acids.','Hippurate + H(2)O = benzoate + glycine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.33','N-acetylglucosamine deacetylase','NULL','NULL','NULL','N-acetyl-D-glucosamine + H(2)O = D-glucosamine + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.34','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.35','D-glutaminase','NULL','NULL','NULL','D-glutamine + H(2)O = D-glutamate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.36','N-methyl-2-oxoglutaramate hydrolase','5-hydroxy-N-methylpyroglutamate synthase','NULL','In the reverse reaction, the product cyclizes non-enzymically to2-hydroxy-N-methyl-5-oxoproline.','N-methyl-2-oxoglutaramate + H(2)O = 2-oxoglutarate + methylamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.37','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.38','Glutamin-(asparagin-)ase','Glutaminase-asparaginase','NULL','L-asparagine is hydrolyzed at 0.8 of the rate of L-glutamine.The D-isomers are also hydrolyzed, more slowly.Cf. EC 3.5.1.1 and EC 3.5.1.2.','(1) L-glutamine + H(2)O = L-glutamate + NH(3).(2) L-asparagine + H(2)O = L-aspartate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.39','Alkylamidase','NULL','NULL','The enzyme hydrolyzes N-monosubstituted and N,N-disubstituted amides,and there is some activity toward primary amides.It has little or no activity toward short-chain substrates.','N-methylhexanamide + H(2)O = hexanoate + methylamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.40','Acylagmatine amidase','NULL','NULL','Also acts on acetylagmatine, propanoylagmatine and bleomycin B(2).','Benzoylagmatine + H(2)O = benzoate + agmatine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.41','Chitin deacetylase','NULL','NULL','Hydrolyzes the N-acetamido groups of N-acetyl-D-glucosamine residuesin chitin.','Chitin + H(2)O = chitosan + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.42','Nicotinamide-nucleotide amidase','NULL','NULL','Also acts more slowly on beta-nicotinamide D-ribonucleoside.','Beta-nicotinamide D-ribonucleotide + H(2)O = beta-nicotinateD-ribonucleotide + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.43','Peptidyl-glutaminase','Peptidoglutaminase I','NULL','Specific for the hydrolysis of the gamma-amide of glutaminesubstituted at the alpha-amino group, e.g., glycyl-L-glutamine,N-acetyl-L-glutamine and L-leucylglycyl-L-glutamine.','Alpha-N-peptidyl-L-glutamine + H(2)O = alpha-N-peptidyl-L-glutamate +NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.44','Protein-glutamine glutaminase','Glutaminylpeptide glutaminase','NULL','Specific for the hydrolysis of the gamma-amide of glutaminesubstituted at the carboxyl position or both the alpha-amino andcarboxyl positions, e.g., L-glutaminylglycine and L-phenylalanyl-L-glutaminylglycine.','Protein L-glutamine + H(2)O = protein L-glutamate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.45','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.46','6-aminohexanoate-dimer hydrolase','NULL','NULL','Also hydrolyzes, more slowly, oligomers of 6-aminohexanoatecontaining up to 6 residues.The residues are removed sequentially from the N-terminus.','N-(6-aminohexanoyl)-6-aminohexanoate + H(2)O = 2 6-aminohexanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.47','N-acetyldiaminopimelate deacetylase','NULL','NULL','Formerly EC 3.1.1.62.','N-acetyl-LL-2,6-diaminoheptanedioate + H(2)O = acetate + LL-2,6-diaminoheptanedioate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.48','Acetylspermidine deacetylase','N(1)-acetylspermidine amidohydrolase','NULL','It was initially thought that N(1)-acetylspermidine was the substratefor this deacetylase reaction but this has since been disproved.','N(8)-acetylspermidine + H(2)O = acetate + spermidine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.49','Formamidase','Formamide amidohydrolase','NULL','Also acts, more slowly, on acetamide, propanamide and butanamide.','Formamide + H(2)O = formate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.50','Pentanamidase','Valeramidase','NULL','Also acts, more slowly, on other short-chain aliphatic amides.Different from EC 3.5.1.49.','Pentanamide + H(2)O = pentanoate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.51','4-acetamidobutyryl-CoA deacetylase','Aminobutyryl-CoA thiolesterase','NULL','The enzyme also hydrolyzes 4-aminobutanoyl-CoA to aminobutanoate andcoenzyme A.Formerly EC 3.2.2.18.','4-acetamidobutanoyl-CoA + H(2)O = acetate + 4-aminobutanoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.52','Peptide-N(4)-(N-acetyl-beta-glucosaminyl)asparagine amidase','Glycopeptidase','NULL','Does not act on (GlcNAc)-Asn, because it requires the presence ofmore than two amino-acid residues in the substrate (cf. EC 3.5.1.26).Formerly EC 3.2.2.18.','Hydrolysis of an N(4)-(acetyl-beta-D-glucosaminyl)asparagine residue inwhich the glucosamine residue may be further glycosylated, to yield a(substituted) N-acetyl-beta-D-glucosaminylamine and a peptide containingan aspartate residue.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.53','N-carbamoylputrescine amidase','NULL','NULL','NULL','N-carbamoylputrescine + H(2)O = putrescine + CO(2) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.54','Allophanate hydrolase','NULL','NULL','Along with EC 3.5.2.15 and EC 3.5.1.84, forms part of the cyanuric-acid metabolism pathway, which degrades s-triazide herbicides, suchas atrazine (2-chloro-4-(ethylamino)-6-(isopropylamino)-1,3,5-triazine), in bacteria.The Saccharomyces cerevisiae enzyme (but not that from green algae)also catalyzes the reaction of EC 6.3.4.6, thus bringing about thehydrolysis of urea to CO(2) and NH(3) in the presence of ATP andbicarbonate.The enzyme from Pseudomonas sp. strain ADP has a narrow substratespecificity, being unable to use the structurally analogous compoundsurea, hydroxyurea or methylcarbamate as substrate.','Urea-1-carboxylate + H(2)O = 2 CO(2) + 2 NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.55','Long-chain-fatty-acyl-glutamate deacylase','Long-chain aminoacylase','NULL','Does not act on acyl derivatives of other amino acids.Optimum chain length of acyl residue is 12 to 16.','N-long-chain-fatty-acyl-L-glutamate + H(2)O = a long-chain carboxylate +L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.56','N,N-dimethylformamidase','NULL','Iron.','Also acts on N-ethylformamide and N-methylformamide and, more slowly,on N,N-diethylformamide, N,N-dimethylacetamide and unsubstituted acylamides.','N,N-dimethylformamide + H(2)O = dimethylamine + formate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.57','Tryptophanamidase','NULL','Manganese.','Also acts on N-ethylformamide and L-tyrosinamide, and on sometryptophan dipeptides.','L-tryptophanamide + H(2)O = L-tryptophan + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.58','N-benzyloxycarbonylglycine hydrolase','NULL','Cobalt; Zinc.','Also acts, more slowly, on N-benzyloxycarbonylalanine, but not on thecorresponding derivatives of other amino acids or onN-benzyloxycarbonylpeptides (cf. EC 3.5.1.64).','N-benzyloxycarbonylglycine + H(2)O = benzyl alcohol + CO(2) + glycine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.59','N-carbamoylsarcosine amidase','CSHase','NULL','NULL','N-carbamoylsarcosine + H(2)O = sarcosine + CO(2) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.60','N-(long-chain-acyl)ethanolamine deacylase','N-acylethanolamine amidohydrolase','NULL','Does not act on N-acylsphingosine or N,O-diacylethanolamine.','N-(long-chain-acyl)ethanolamine + H(2)O = a long-chain carboxylate +ethanolamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.61','Mimosinase','Mimosine amidohydrolase','NULL','An enzyme from Leucaena leucocephala leaf, which also contains thetoxic amino acid, mimosine.','(S)-2-amino-3-(3-hydroxy-4-oxo-4H-pyridin-1-yl)propanoate + H(2)O =3-hydroxy-4H-pyrid-4-one + L-serine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.62','Acetylputrescine deacetylase','NULL','NULL','The enzyme from Micrococcus luteus also acts on N(8)-acetylspermidineand acetylcadaverine, but more slowly.','N-acetylputrescine + H(2)O = acetate + putrescine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.63','4-acetamidobutyrate deacetylase','NULL','NULL','Also acts on N-acetyl-beta-alanine and 5-acetamidopentanoate.','4-acetamidobutanoate + H(2)O = acetate + 4-aminobutanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.64','N(alpha)-benzyloxycarbonylleucine hydrolase','NULL','NULL','Also acts on N(alpha)-t-butoxycarbonyl-L-leucine, and, more slowly,on the corresponding derivatives of L-aspartate, L-methionine,L-glutamate and L-alanine (cf. EC 3.5.1.58).','N(alpha)-benzyloxycarbonyl-L-leucine + H(2)O = benzyl alcohol + CO(2) +L-leucine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.65','Theanine hydrolase','NULL','NULL','Also acts on other N-alkyl-L-glutamines.','N(5)-ethyl-L-glutamine + H(2)O = L-glutamate + ethylamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.66','2-(hydroxymethyl)-3-(acetamidomethylene)succinate hydrolase','NULL','NULL','Involved in the degradation of pyridoxin by Pseudomonas andArthrobacter.','2-(hydroxymethyl)-3-(acetamidomethylene)succinate + 2 H(2)O = acetate +2-(hydroxymethyl)-4-oxobutanoate + NH(3) + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.67','4-methyleneglutaminase','4-methyleneglutamine deamidase','NULL','NULL','4-methylene-L-glutamine + H(2)O = 4-methylene-L-glutamate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.68','N-formylglutamate deformylase','Beta-citryl-L-glutamate hydrolase','NULL','The animal enzyme also acts on beta-citryl-L-glutamate and beta-citryl-L-glutamine.','N-formyl-L-glutamate + H(2)O = formate + L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.69','Glycosphingolipid deacylase','Glycosphingolipid ceramide deacylase','NULL','Does not act on sphingolipids such as ceramide.Not identical with EC 3.5.1.23.','Hydrolysis of gangliosides and neutral glycosphingolipids, releasingfatty acids to form the lyso-derivatives.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.70','Aculeacin-A deacylase','NULL','NULL','NULL','Hydrolysis of the amide bond in aculeacin A and related neutrallipopeptide antibiotics, releasing the long-chain fatty acid side-chain.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.71','N-feruloylglycine deacylase','NULL','NULL','Hydrolyzes a range of L-amino acids from the cinnamoyl andsubstituted cinnamoyl series.Not identical with EC 3.5.1.14.','N-feruloylglycine + H(2)O = ferulate + glycine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.72','D-benzoylarginine-4-nitroanilide amidase','NULL','NULL','NULL','N-benzoyl-D-arginine-4-nitroanilide + H(2)O = N-benzoyl-D-arginine +4-nitroaniline.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.73','Carnitinamidase','NULL','NULL','Does not act on D-carnitinamide.','L-carnitinamide + H(2)O = L-carnitine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.74','Chenodeoxycholoyltaurine hydrolase','NULL','NULL','Some other taurine conjugates are hydrolyzed, but not glycineconjugates of bile acids.','Chenodeoxycholoyltaurine + H(2)O = chenodeoxycholate + taurine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.75','Urethanase','Urethane hydrolase','NULL','NULL','Urethane + H(2)O = ethanol + CO(2) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.76','Arylalkyl acylamidase','Aralkyl acylamidase','NULL','Strict specificity for N-acetyl arylalkylamines, including N-acetyl-2-phenylethylamine, N-acetyl-3-phenylpropylamine, N-acetyldopamine,N-acetyl-serotonin and melatonin.It also accepts arylalkyl acetates but not acetanilide derivatives,which are common substrates of EC 3.5.1.13.','N-acetylarylalkylamine + H(2)O = arylalkylamine + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.77','N-carbamoyl-D-amino-acid hydrolase','D-N-carbamoylase','NULL','This enzyme, along with EC 3.5.1.87, EC 5.1.99.5 and EC 3.5.2.2,forms part of the reaction cascade known as the hydantoinaseprocess, which allows the total conversion of D,L-5-monosubstitutedhydantoins into optically pure D- or L-amino acids.Has strict stereospecificity for N-carbamoyl-D-amino acids and doesnot act upon the corresponding L-amino acids or N-formyl amino acids,N-carbamoyl-sarcosine, -citrulline, -allantoin and -ureidopropanoate,which are substrates for other amidohydrolases.','N-carbamoyl-D-amino acid + H(2)O = D-amino acid + NH(3) + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.78','Glutathionylspermidine amidase','Glutathionylspermidine amidohydrolase (spermidine-forming)','NULL','Transforms glutathionylspermidine into glutathione and spermidine.The enzyme from Escherichia coli is bifunctional and also catalyzesthe reaction of EC 6.3.1.8, resulting in a net hydrolysis of ATP.','Glutathionylspermidine + H(2)O = glutathione + spermidine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.79','Phthalyl amidase','o-phthalyl amidase','NULL','Phthalyl is used to mean 2-carboxybenzoyl.The enzyme from Xanthobacter agilis hydrolyzes phthalylated aminoacids, peptides, beta-lactams, aromatic and aliphatic amines.The substituent on nitrogen may be an alkyl group, but may also becomplex, giving an amino acid or peptide derivative.Substitutions on the phthalyl ring include 6-F, 6-NH(2), 3-OH, and anitrogen in the aromatic ring ortho to the carboxy group attached tothe amine.','A phthalylamide + H(2)O = phthalic acid + a substituted amine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.80','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.81','N-acyl-D-amino-acid deacylase','D-aminoacylase','Zinc.','Has a wide specificity; hydrolyzes N-acyl derivative of neutralD-amino acids.Used in separating D- and L-amino acids.','N-acyl-D-amino acid + H(2)O = a carboxylate + D-amino acid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.82','N-acyl-D-glutamate deacylase','N-acyl-D-glutamate amidohydrolase','Zinc.','NULL','N-acyl-D-glutamate + H(2)O = a carboxylate + D-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.83','N-acyl-D-aspartate deacylase','N-acyl-D-aspartate amidohydrolase','Zinc.','NULL','N-acyl-D-aspartate + H(2)O = a carboxylate + D-aspartate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.84','Biuret amidohydrolase','NULL','NULL','Along with EC 3.5.2.15 and EC 3.5.1.54, this enzyme forms part of thecyanuric-acid metabolism pathway, which degrades s-triazideherbicides, such as atrazine (2-chloro-4-(ethylamino)-6-(isopropylamino)-1,3,5-triazine), in bacteria.Urea-1-carboxylate rather than urea (as was thought previously) isthe 2-nitrogen intermediate in cyanuric-acid metabolism in bacteria.The product, urea-1-carboxylate, can spontaneously decarboxylateunder acidic conditions to form urea but, under physiologicalconditions, it can be converted into CO(2) and ammonia by the actionof EC 3.5.1.54.','Biuret + H(2)O = urea-1-carboxylate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.85','(S)-N-acetyl-1-phenylethylamine hydrolase','(S)-N-acetyl-1-phenylethylamine amidohydrolase','NULL','Inhibited by phenylmethanesulfonyl fluoride.Some related acetylated compounds are hydrolyzed with variableenantiomeric selectivities.','N-acetylphenylethylamine + H(2)O = phenylethylamine + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.86','Mandelamide amidase','Mandelamide hydrolase','NULL','NULL','(R)-mandelamide + H(2)O = (R)-mandelate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.87','N-carbamoyl-L-amino-acid hydrolase','L-carbamoylase','Cobalt or nickel or manganese.','This enzyme, along with EC 3.5.1.77, EC 5.1.99.5 and EC 3.5.2.2,forms part of the reaction cascade known as the hydantoinaseprocess, which allows the total conversion of D,L-5-monosubstitutedhydantoins into optically pure D- or L-amino acids.The enzyme from Alcaligenes xylosoxidans has broad specificity forcarbamoyl-L-amino acids, although it is inactive on the carbamoylderivatives of glutamate, aspartate, arginine, tyrosine ortryptophan.The enzyme from Sinorhizobium meliloti requires a divalent cation foractivity and can hydrolyze N-carbamoyl-L-tryptophan as well asN-carbamoyl L-amino acids with aliphatic substituents.The enzyme is inactive on derivatives of D-amino acids.In addition to N-carbamoyl L-amino acids, the enzyme can alsohydrolyze formyl and acetyl derivatives to varying degrees.','N-carbamoyl-L-2-amino acid (a 2-ureido carboxylate) + H(2)O = L-2-aminoacid + NH(3) + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.88','Peptide deformylase','PDF','Fe(2+).','Requires at least a dipeptide for an efficient rate of reaction.N-terminal L-methionine is a prerequisite for activity but the enzymehas broad specificity at other positions.Differs in substrate specificity from EC 3.5.1.27 and EC 3.5.1.31.','Formyl-L-methionyl peptide + H(2)O = formate + methionyl peptide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.89','N-acetylglucosaminylphosphatidylinositol deacetylase','Acetylglucosaminylphosphatidylinositol deacetylase','NULL','Involved in the second step of glycosylphosphatidylinositol (GPI)anchor formation in all eukaryotes.The enzyme appears to be composed of a single subunit (PIG-L inmammalian cells and GPI12 in Saccharomyces cerevisiae).In some species, the long-chain sn-1-acyl group of the phosphatidylgroup is replaced by a long-chain alkyl or alk-1-enyl group.Formerly EC 3.1.1.69.','6-(N-acetyl-alpha-D-glucosaminyl)-1-phosphatidyl-1D-myo-inositol + H(2)O= 6-(alpha-D-glucosaminyl)-1-phosphatidyl-1D-myo-inositol + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.90','Adenosylcobinamide hydrolase','AdoCbi amidohydrolase','NULL','Involved in the salvage pathway of cobinamide in archaea.Archaea convert adenosylcobinamide (AdoCbi) into adenosylcobinamidephosphate (AdoCbi-P) in two steps.First, the amidohydrolase activity of cbiZ cleaves off theaminopropanol moiety of AdoCbi yielding adenosylcobyric acid(AdoCby); second, AdoCby is converted into AdoCbi-P by the action ofEC 6.3.1.10 (CbiB).','Adenosylcobinamide + H(2)O = adenosylcobyric acid + (R)-1-aminopropan-2-ol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.91','N-substituted formamide deformylase','NULL','Zinc.','While N-benzylformamide is the best substrate, the enzyme fromArthrobacter pascens can also act on the N-substituted formamidesN-butylformamide, N-allylformamide, N-(2-(cyclohex-1-enyl)ethyl)formamide and N-(1-phenylethyl)formamide, but much moreslowly.Amides of other acids do not act as substrates.','N-benzylformamide + H(2)O = formate + benzylamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.92','Pantetheine hydrolase','Pantetheinase','NULL','Hydrolyzes only one of the amide bonds of pantetheine.The substrate analogs phosphopantetheine and CoA are not substrates.Recycles pantothenate (vitamin B(5)) and produces cysteamine,a potent anti-oxidant.','(R)-pantetheine + H(2)O = (R)-pantothenate + 2-aminoethanethiol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.93','Glutaryl-7-aminocephalosporanic-acid acylase','7-beta-(4-carboxybutanamido)cephalosporanic acid acylase','NULL','Forms 7-aminocephalosporanic acid, a key intermediate in thesynthesis of cephem antibiotics.It reacts only weakly with cephalosporin C.','(7R)-7-(4-carboxybutanamido)cephalosporanate + H(2)O = (7R)-7-aminocephalosporanate + glutarate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.94','Gamma-glutamyl-gamma-aminobutyrate hydrolase','Gamma-glutamyl-GABA hydrolase','NULL','Forms part of a novel putrescine-utilizing pathway in Escherichiacoli, in which it has been hypothesized that putrescine is firstglutamylated to form gamma-glutamylputrescine, which is oxidized to4-(gamma-glutamylamino)butanal and then to 4-(gamma-glutamylamino)butanoate.Also can catalyze the reactions of EC 3.5.1.35 and EC 3.5.1.65.','4-(L-gamma-glutamylamino)butanoate + H(2)O = 4-aminobutanoate +L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.95','N-malonylurea hydrolase','Ureidomalonase','NULL','Forms part of the oxidative pyrimidine-degrading pathway in somemicroorganisms, along with EC 1.17.99.4 and EC 3.5.2.1.','3-oxo-3-ureidopropanoate + H(2)O = malonate + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.96','Succinylglutamate desuccinylase','N(2)-succinylglutamate desuccinylase','Cobalt.','N(2)-acetylglutamate is not a substrate.The final enzyme in the arginine succinyltransferase (AST) pathwayfor the catabolism of arginine.This pathway converts the carbon skeleton of arginine into glutamate,with the concomitant production of ammonia and conversion ofsuccinyl-CoA into succinate and CoA.The five enzymes involved in this pathway are EC 2.3.1.109,EC 3.5.3.23, EC 2.6.1.11, EC 1.2.1.71 and EC 3.5.1.96.','N-succinyl-L-glutamate + H(2)O = succinate + L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.97','Acyl-homoserine-lactone acylase','Acyl-homoserine lactone acylase','NULL','Acyl-homoserine lactones (AHLs) are produced by a number of bacterialspecies and are used by them to regulate the expression of virulencegenes in a process known as quorum-sensing.Each bacterial cell has a basal level of AHL and, once the populationdensity reaches a critical level, it triggers AHL-signaling which,in turn, initiates the expression of particular virulence genes.Plants or animals capable of degrading AHLs would have a therapeuticadvantage in avoiding bacterial infection as they could prevent AHL-signaling and the expression of virulence genes in quorum-sensingbacteria.This quorum-quenching enzyme removes the fatty-acid side chain fromthe homoserine lactone ring of AHL-dependent quorum-sensing signalmolecules.It has broad specificity for AHLs with side changes ranging in lengthfrom 11 to 14 carbons.Substituents at the 3-position, as found in N-(3-oxododecanoyl)-L-homoserine lactone, do not affect this activity.','An N-acyl-L-homoserine lactone + H(2)O = L-homoserine lactone +a carboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.98','Histone deacetylase','HDAC','NULL','A class of enzymes that remove acetyl groups from N(6)-acetyl-lysineresidues on a histone.The reaction of this enzyme is opposite to that of EC 2.3.1.48.Histone deacetylases (HDACs) can be organized into three classesdepending on sequence similarity and domain organization.Histone acetylation plays an important role in regulation of geneexpression.In eukaryotes, HDACs play a key role in the regulation oftranscription and cell proliferation.May be identical to EC 3.5.1.17.','Hydrolysis of an N(6)-acetyl-lysine residue of a histone to yield adeacetylated histone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.99','Fatty acid amide hydrolase','Anandamide amidohydrolase','NULL','Integral membrane protein, the enzyme is responsible for thecatabolism of neuromodulatory fatty acid amides, including anandamideand oleamide, occurs in mammalia.Formerly EC 3.5.1.n2.','(1) Anandamide + H(2)O = arachidonic acid + ethanolamine.(2) Oleamide + H(2)O = oleic acid + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.100','(R)-amidase','R-amidase','NULL','In addition (R)-piperidine-3-carboxamide is hydrolyzed to (R)-piperidine-3-carboxylic acid and NH(3), and (R)-N-tert-butylpiperazine-2-carboxamide is hydrolyzed to (R)-piperazine-2-carboxylic acid and tert-butylamine with lower activity.The enzyme does not act on the other amide substrates which arehydrolyzed by EC 3.5.1.4 (amidase).','(1) (R)-piperazine-2-carboxamide + H(2)O = (R)-piperazine-2-carboxylicacid + NH(3).(2) Beta-alaninamide + H(2)O = beta-alanine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.101','L-proline amide hydrolase','L-amino acid amidase','NULL','NULL','(1) (S)-piperidine-2-carboxamide + H(2)O = (S)-piperidine-2-carboxylicacid + NH(3).(2) L-prolinamide + H(2)O = L-proline + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.102','2-amino-5-formylamino-6-ribosylaminopyrimidin-4(3H)-one 5-monophosphatedeformylase','NULL','NULL','The enzyme catalyzes the second step in archaeal riboflavin and 7,8-didemethyl-8-hydroxy-5-deazariboflavin biosynthesis.The first step is catalyzed by EC 3.5.4.29.The bacterial enzyme, EC 3.5.4.25 catalyzes both reactions.','2-amino-5-formylamino-6-(5-phospho-D-ribosylamino)pyrimidin-4(3H)-one +H(2)O = 2,5-diamino-6-(5-phospho-D-ribosylamino)pyrimidin-4(3H)-one +formate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.103','N-acetyl-1-D-myo-inositol-2-amino-2-deoxy-alpha-D-glucopyranosidedeacetylase','1D-myo-inositol 2-acetamido-2-deoxy-alpha-D-glucopyranoside deacetylase','Zinc.','This enzyme is considered the key enzyme and rate limiting step inthe mycothiol biosynthesis pathway.In addition to acetylase activity, the enzyme possesses weakmycothiol conjugate amidase activity, and shares sequence similaritywith mycothiol S-conjugate amidase.Formerly EC 3.1.1.n1.','1-O-(2-acetamido-2-deoxy-alpha-D-glucopyranosyl)-1D-myo-inositol + H(2)O= 1-O-(2-amino-2-deoxy-alpha-D-glucopyranosyl)-1D-myo-inositol + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.104','Peptidoglycan-N-acetylglucosamine deacetylase','N-acetylglucosamine deacetylase','NULL','Modification of peptidoglycan by N-deacetylation is an importantfactor in virulence of Helicobacter pylori, Listeria monocytogenesand Streptococcus suis.The enzyme from Streptococcus pneumoniae is a metalloenzyme using aHis-His-Asp zinc-binding triad with a nearby aspartic acid andhistidine acting as the catalytic base and acid, respectively.','Peptidoglycan-N-acetyl-D-glucosamine + H(2)O = peptidoglycan-D-glucosamine + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.105','Chitin disaccharide deacetylase','Chitin oligosaccharide amidohydolase','NULL','Chitin oligosaccharide deacetylase is a key enzyme in the chitincatabolic cascade of chitinolytic Vibrio strains.Besides being a nutrient, the heterodisaccharide product 4-O-(N-acetyl-beta-D-glucosaminyl)-D-glucosamine is a unique inducer ofchitinase production in Vibrio parahemolyticus.In contrast to EC 3.5.1.41 this enzyme is specific for the chitindisaccharide.It also deacetylates the chitin trisaccharide with lower efficiency.No activity with higher polymers of GlcNAc.','2-(acetylamino)-4-O-(2-(acetylamino)-2-deoxy-beta-D-glucopyranosyl)-2-deoxy-beta-D-glucopyranose + H(2)O = 2-(acetylamino)-4-O-(2-amino-2-deoxy-beta-D-glucopyranosyl)-2-deoxy-beta-D-glucopyranose + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.106','N-formylmaleamate deformylase','NULL','NULL','The reaction is involved in the aerobic catabolism of nicotinic acid.','N-formylmaleamic acid + H(2)O = maleamate + formate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.107','Maleamate amidohydrolase','NULL','NULL','The reaction is involved in the aerobic catabolism of nicotinic acid.','Maleamate + H(2)O = maleate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.108','UDP-3-O-acyl-N-acetylglucosamine deacetylase','UDP-3-O-((R)-3-hydroxymyristoyl)-N-acetylglucosamine deacetylase','Zinc.','The enzyme catalyzes a committed step in the biosynthesis of lipid A.Formerly EC 3.5.1.n1.','UDP-3-O-((3R)-3-hydroxytetradecanoyl)-N-acetyl-alpha-D-glucosamine +H(2)O = UDP-3-O-((3R)-3-hydroxytetradecanoyl)-alpha-D-glucosamine +acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.109','Sphingomyelin deacylase','Glucosylceramide sphingomyelin deacylase','NULL','The enzyme is involved in the sphingolipid metabolism in theepidermis.','(1) An N-acyl-sphingosylphosphorylcholine + H(2)O = a fatty acid +sphingosylphosphorylcholine.(2) A D-glucosyl-N-acylsphingosine + H(2)O = a fatty acid + D-glucosyl-sphingosine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.110','Peroxyureidoacrylate/ureidoacrylate amidohydrolase','NULL','NULL','The enzyme also shows activity toward ureidoacrylate.Part of the Rut pyrimidine catabolic pathway.Formerly EC 3.5.1.n4.','(1) (Z)-3-ureidoacrylate peracid + H(2)O = (Z)-3-peroxyaminoacrylate +NH(3).(2) (Z)-3-ureidoacrylate + H(2)O = (Z)-3-aminoacrylate + CO(2) + NH(3).(3) (Z)-2-methyl-ureidoacrylate peracid + H(2)O = (Z)-2-methyl-peroxyaminoacrylate + CO(2) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.111','2-oxoglutaramate amidase','Omega-amidase','NULL','The enzyme, which is highly specific for its substrate, participatesin the nicotine degradation pathway of several Gram-positivebacteria.','2-oxoglutaramate + H(2)O = 2-oxoglutarate + ammonia.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.112','2-N-acetylparomamine deacetylase','NULL','NULL','Involved in the biosynthetic pathways of several clinically importantaminocyclitol antibiotics, including kanamycin, butirosin, neomycinand ribostamycin.The enzyme from the bacterium Streptomyces fradiae can also accept2-acetyl-6-hydroxyneomycin C as substrate, cf. EC 3.5.1.113.','2-N-acetylparomamine + H(2)O = paromamine + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.113','2-acetyl-6-hydroxyneomycin C deacetylase','NULL','NULL','Involved in the biosynthetic pathway of aminoglycoside antibiotics ofthe neomycin family.The enzyme from the bacterium Streptomyces fradiae also catalyzesEC 3.5.1.112.','2-acetyl-6-deamino-6-hydroxyneomycin C + H(2)O = 6-deamino-6-hydroxyneomycin C + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.114','N-acyl-aromatic-L-amino acid amidohydrolase','Aminoacylase 3','Zinc.','This enzyme is found in animals and is involved in the hydrolysis ofN-acylated or N-acetylated amino acids (except L-aspartate).It preferentially deacetylates N(alpha)-acetylated aromatic aminoacids and mercapturic acids (S-conjugates of N-acetyl-L-cysteine)that are usually not deacetylated by EC 3.5.1.14.Some bacterial aminoacylases demonstrate substrate specificity forboth EC 3.5.1.14 and EC 3.5.1.114.Cf. EC 3.5.1.14 and EC 3.5.1.15.','(1) An N-acyl-aromatic-L-amino acid + H(2)O = an aromatic-L-amino acid +a carboxylate.(2) An N-acetyl-L-cysteine-S-conjugate + H(2)O = an L-cysteine-S-conjugate + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.115','Mycothiol S-conjugate amidase','NULL','NULL','The enzyme that is found in actinomycetes is involved in thedetoxification of oxidizing agents and electrophilic antibiotics.The enzyme has low activity with 1-O-(2-acetamido-2-deoxy-alpha-D-glucopyranosyl)-1D-myo-inositol as substrate (cf. EC 3.5.1.103).','A mycothiol S-conjugate + H(2)O = an N-acetyl L-cysteine-S-conjugate +1-O-(2-amino-2-deoxy-alpha-D-glucopyranosyl)-1D-myo-inositol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.116','Ureidoglycolate amidohydrolase','Ureidoglycolate hydrolase','NULL','This plant enzyme is involved in the degradation of ureidoglycolate,an intermediate of purine degradation.Not to be confused with EC 4.3.2.3, which releases urea rather thanammonia.Formerly EC 3.5.3.19.','(S)-ureidoglycolate + H(2)O = glyoxylate + 2 NH(3) + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.n1','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.n2','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.n3','4-deoxy-4-formamido-L-arabinose-phosphoundecaprenol deformylase','Polymyxin resistance protein PmrJ','NULL','NULL','4-deoxy-4-formamido-beta-L-arabinose di-trans,poly-cis-undecaprenylphosphate + H(2)O = 4-amino-4-deoxy-alpha-L-arabinose di-trans,poly-cis-undecaprenyl phosphate + formate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.1.n4','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.1','Barbiturase','NULL','Zinc.','Specific for barbiturate as substrate.Forms part of the oxidative pyrimidine-degrading pathway in somemicroorganisms, along with EC 1.17.99.4 and EC 3.5.1.95.It was previously thought that the end-products of the reaction weremalonate and urea.May be involved in the regulation of pyrimidine metabolism, alongwith EC 2.4.2.9.','Barbiturate + H(2)O = 3-oxo-3-ureidopropanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.2','Dihydropyrimidinase','Hydantoinase','NULL','Also acts on dihydrothymine and hydantoin.','5,6-dihydrouracil + H(2)O = 3-ureidopropanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.3','Dihydroorotase','Carbamoylaspartic dehydrase','NULL','NULL','(S)-dihydroorotate + H(2)O = N-carbamoyl-L-aspartate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.4','Carboxymethylhydantoinase','NULL','NULL','NULL','L-5-carboxymethylhydantoin + H(2)O = N-carbamoyl-L-aspartate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.5','Allantoinase','NULL','NULL','NULL','(S)-allantoin + H(2)O = allantoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.6','Beta-lactamase','Cephalosporinase','Zinc.','Zinc is only requires in class-B enzymes.A group of enzymes of varying specificity hydrolyzing beta-lactams;some act more rapidly on penicillins, some more rapidly oncephalosporins.Formerly EC 3.5.2.8.','A beta-lactam + H(2)O = a substituted beta-amino acid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.7','Imidazolonepropionase','4(5)-imidazolone-5(4)-propionic acid hydrolase','NULL','NULL','(S)-3-(5-oxo-4,5-dihydro-3H-imidazol-4-yl)propanoate + H(2)O =N-formimidoyl-L-glutamate + H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.8','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.9','5-oxoprolinase (ATP-hydrolyzing)','5-OPase','NULL','NULL','ATP + 5-oxo-L-proline + 2 H(2)O = ADP + phosphate + L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.10','Creatininase','Creatinine amidohydrolase','NULL','NULL','Creatinine + H(2)O = creatine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.11','L-lysine-lactamase','L-alpha-aminocaprolactam hydrolase','NULL','Also hydrolyzes L-lysinamide.','(S)-2-aminohexano-6-lactam + H(2)O = L-lysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.12','6-aminohexanoate-cyclic-dimer hydrolase','NULL','NULL','The cyclic dimer of 6-aminohexanoate is converted to the lineardimer.','1,8-diazacyclotetradecane-2,9-dione + H(2)O = N-(6-aminohexanoyl)-6-aminohexanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.13','2,5-dioxopiperazine hydrolase','Cyclo(Gly-Gly) hydrolase','NULL','Highly specific; does not hydrolyze other dioxopiperazines,glycylglycine, proteins or barbiturates.','2,5-dioxopiperazine + H(2)O = glycylglycine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.14','N-methylhydantoinase (ATP-hydrolyzing)','N-methylhydantoin amidohydrolase','NULL','NULL','ATP + N-methylimidazolidine-2,4-dione + 2 H(2)O = ADP + phosphate +N-carbamoylsarcosine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.15','Cyanuric acid amidohydrolase','NULL','NULL','Along with EC 3.5.1.54 and EC 3.5.1.84, forms part of the cyanuric-acid metabolism pathway, which degrades s-triazide herbicides, suchas atrazine (2-chloro-4-(ethylamino)-6-(isopropylamino)-1,3,5-triazine), in bacteria.A key enzyme in the pathway, catalyzing the ring cleavage of cyanuricacid.Specific for cyanuric acid as substrate as neither the structurallyrelated compounds ammeline (2,4-diamino-6-hydroxy-s-triazine) andammelide (2-amino-4,6-dihydroxy-s-triazine) nor a number ofpyrimidine compounds, such as uracil and cytosine, can act assubstrates.','Cyanuric acid + H(2)O = biuret + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.16','Maleimide hydrolase','Cyclic imide hydrolase','NULL','Succinimide and glutarimide, and sulfur-containing cyclic imides,such as rhodanine, can also act as substrates for the enzyme fromBlastobacter sp. A17p-4.The reverse, cyclization, reaction is also catalyzed, but much moreslowly.It has lower activity toward cyclic ureides, which are the substratesof EC 3.5.2.2.','Maleimide + H(2)O = maleamic acid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.17','Hydroxyisourate hydrolase','5-hydroxyisourate hydrolase','NULL','The reaction is the first stage in the conversion of5-hydroxyisourate into (S)-allantoin.This reaction will also occur spontaneously, but more slowly.','5-hydroxyisourate + H(2)O = 5-hydroxy-2-oxo-4-ureido-2,5-dihydro-1H-imidazole-5-carboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.18','Enamidase','NULL','Iron; Zinc.','Forms part of the nicotinate-fermentation catabolism pathway inEubacterium barkeri.Other enzymes involved in this pathway are EC 1.17.1.5, EC 1.3.7.1,EC 1.1.1.291, EC 5.4.99.4, EC 5.3.3.6, EC 4.2.1.85 and EC 4.1.3.32.','6-oxo-1,4,5,6-tetrahydronicotinate + 2 H(2)O = 2-formylglutarate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.2.19','Streptothricin hydrolase','NULL','NULL','The enzyme also catalyzes the hydrolysis of streptothricin-D tostreptothricin-D acid.The enzyme is responsible for streptothricin resistance inStreptomyces albulus and Streptomyces noursei.','Streptothricin-F + H(2)O = streptothricin-F acid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.1','Arginase','Arginine amidinase','Manganese.','Also hydrolyzes alpha-N-substituted L-arginines and canavanine.','L-arginine + H(2)O = L-ornithine + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.2','Guanidinoacetase','Glycocyaminase','Manganese.','NULL','Guanidinoacetate + H(2)O = glycine + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.3','Creatinase','Creatine amidinohydrolase','NULL','NULL','Creatine + H(2)O = sarcosine + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.4','Allantoicase','Allantoate amidinohydrolase','NULL','Also hydrolyzes (R)-ureidoglycolate to glyoxylate and urea.','Allantoate + H(2)O = (S)-ureidoglycolate + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.5','Formimidoylaspartate deiminase','Formiminoaspartate deiminase','NULL','NULL','N-formimidoyl-L-aspartate + H(2)O = N-formyl-L-aspartate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.6','Arginine deiminase','Arginine dihydrolase','NULL','Also acts on canavanine.','L-arginine + H(2)O = L-citrulline + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.7','Guanidinobutyrase','NULL','Manganese.','Also acts, very slowly, on 5-guanidinopentanoate and 6-guanidino-hexanoate.','4-guanidinobutanoate + H(2)O = 4-aminobutanoate + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.8','Formimidoylglutamase','Formiminoglutamase','NULL','NULL','N-formimidoyl-L-glutamate + H(2)O = L-glutamate + formamide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.9','Allantoate deiminase','NULL','Manganese.','This enzyme is part of the ureide pathway, which permits certainorganisms to recycle the nitrogen in purine compounds.This enzyme, which liberates ammonia from allantoate, is present inplants and bacteria.In plants it is localized in the endoplasmic reticulum.Requires manganese.','Allantoate + H(2)O = (S)-ureidoglycine + NH(3) + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.10','D-arginase','NULL','NULL','NULL','D-arginine + H(2)O = D-ornithine + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.11','Agmatinase','Agmatine ureohydrolase','NULL','NULL','Agmatine + H(2)O = putrescine + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.12','Agmatine deiminase','NULL','NULL','The plant enzyme also catalyzes the reactions of EC 2.1.3.3,EC 2.1.3.6 and EC 2.7.2.2, thus functioning as a putrescine synthase,converting agmatine and ornithine into putrescine and citrulline,respectively.','Agmatine + H(2)O = N-carbamoylputrescine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.13','Formimidoylglutamate deiminase','Formiminoglutamate deiminase','NULL','NULL','N-formimidoyl-L-glutamate + H(2)O = N-formyl-L-glutamate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.14','Amidinoaspartase','NULL','NULL','Also acts slowly on N-amidino-L-glutamate.','N-amidino-L-aspartate + H(2)O = L-aspartate + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.15','Protein-arginine deiminase','NULL','NULL','Also acts on N-acyl-L-arginine and, more slowly, on L-arginineesters.','Protein L-arginine + H(2)O = protein L-citrulline + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.16','Methylguanidinase','NULL','NULL','Acts on other alkylguanidines, very slowly.','Methylguanidine + H(2)O = methylamine + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.17','Guanidinopropionase','NULL','Manganese.','Also acts, more slowly, on taurocyamine and 4-guanidinobutanoate.','3-guanidinopropanoate + H(2)O = beta-alanine + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.18','Dimethylargininase','Dimethylarginine dimethylaminohydrolase','NULL','Also acts on N(omega)-methyl-L-arginine.','N(omega),N(omega)-dimethyl-L-arginine + H(2)O = dimethylamine +L-citrulline.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.19','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.20','Diguanidinobutanase','NULL','NULL','Other diguanidinoalkanes with 3 to 10 methylene groups can act, moreslowly.','1,4-diguanidinobutane + H(2)O = agmatine + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.21','Methylenediurea deaminase','Methylenediurease','NULL','The methylenediurea is hydrolyzed and decarboxylated to give anaminated methylurea, which then spontaneously hydrolyzes tohydroxymethylurea.The enzyme from Ochrobactrum anthropi also hydrolyzesdimethylenetriurea and trimethylenetetraurea as well asureidoglycolate, which is hydrolyzed to urea and glyoxylate,and allantoate, which is hydrolyzed to ureidoglycolate, ammonia andcarbon dioxide.','NH(2)-CO-NH-CH(2)-NH-CO-NH(2) + 2 H(2)O = N-(hydroxymethyl)urea + 2 NH(3)+ CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.22','Proclavaminate amidinohydrolase','PAH','Manganese.','Forms part of the pathway for the biosythesis of the beta-lactamaseinhibitor clavulanate in Streptomyces clavuligerus.It carries out an intermediary reaction between the first reaction ofEC 1.14.11.21, and the second and third reactions of that enzyme.','Amidinoproclavaminate + H(2)O = proclavaminate + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.23','N-succinylarginine dihydrolase','Arginine succinylhydrolase','NULL','Arginine, N(2)-acetylarginine and N(2)-glutamylarginine do not act assubstrates.The second enzyme in the arginine succinyltransferase (AST) pathwayfor the catabolism of arginine.This pathway converts the carbon skeleton of arginine into glutamate,with the concomitant production of ammonia and conversion ofsuccinyl-CoA into succinate and CoA.The five enzymes involved in this pathway are EC 2.3.1.109,EC 3.5.3.23, EC 2.6.1.11, EC 1.2.1.71 and EC 3.5.1.96.','N(2)-succinyl-L-arginine + 2 H(2)O = N(2)-succinyl-L-ornithine + 2 NH(3)+ CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.24','N(1)-aminopropylagmatine ureohydrolase','NULL','NULL','The enzyme, which has been characterized from the hyperthermophilicarchaeon Pyrococcus kodakarensis and the thermophilic Gram-negativebacterium Thermus thermophilus, is involved in the biosynthesis ofspermidine.','N(1)-aminopropylagmatine + H(2)O = spermidine + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.25','N(omega)-hydroxy-L-arginine amidinohydrolase','NULL','NULL','The enzyme participates in the biosynthetic pathway of D-cycloserine,an antibiotic substance produced by several Streptomyces species.','N(omega)-hydroxy-L-arginine + H(2)O = L-ornithine + hydroxyurea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.3.26','(S)-ureidoglycine aminohydrolase','NULL','Manganese.','This enzyme, found in plants and bacteria, is part of the ureidepathway, which enables the recycling of the nitrogen in purinecompounds.In plants it is localized in the endoplasmic reticulum.','(S)-2-ureidoglycine + H(2)O = (S)-ureidoglycolate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.1','Cytosine deaminase','Cytosine aminohydrolase','NULL','Also acts on 5-methylcytosine.','Cytosine + H(2)O = uracil + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.2','Adenine deaminase','Adenase','NULL','NULL','Adenine + H(2)O = hypoxanthine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.3','Guanine deaminase','GAH','NULL','NULL','Guanine + H(2)O = xanthine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.4','Adenosine deaminase','Adenosine aminohydrolase','NULL','NULL','Adenosine + H(2)O = inosine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.5','Cytidine deaminase','(Deoxy)cytidine deaminase','Zinc.','Catalyzes the deamination of cytidine and 2-deoxycytidine withsimilar efficiencies.The enzyme, which is widely distributed among organisms, is involvedin salvage of both exogenous and endogenous cytidine and2-deoxycytidine for UMP synthesis.Formerly EC 3.5.4.14.','(1) Cytidine + H(2)O = uridine + NH(3).(2) 2-deoxycytidine + H(2)O = 2-deoxyuridine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.6','AMP deaminase','5-AMP deaminase','NULL','Cf. EC 3.5.4.17.','AMP + H(2)O = IMP + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.7','ADP deaminase','NULL','NULL','NULL','ADP + H(2)O = IDP + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.8','Aminoimidazolase','4-aminoimidazole deaminase','Fe(2+).','Forms part of the xanthine-degradation pathway in some bacteria.The product of the reaction, imidazol-4-one, can be converted non-enzymically into formiminoglycine.','4-aminoimidazole + H(2)O = imidazol-4-one + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.9','Methenyltetrahydrofolate cyclohydrolase','NULL','NULL','NULL','5,10-methenyltetrahydrofolate + H(2)O = 10-formyltetrahydrofolate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.10','IMP cyclohydrolase','IMP synthetase','NULL','NULL','IMP + H(2)O = 5-formamido-1-(5-phospho-D-ribosyl)imidazole-4-carboxamide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.11','Pterin deaminase','NULL','NULL','The animal enzyme is specific for pterin, isoxanthopterin andtetrahydropterin.','A 2-amino-4-hydroxypteridine + H(2)O = a 2,4-dihydroxypteridine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.12','dCMP deaminase','Deoxycytidylate deaminase','NULL','Also acts on some 5-substituted dCMPs.','dCMP + H(2)O = dUMP + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.13','dCTP deaminase','Deoxycytidine triphosphate deaminase','NULL','NULL','dCTP + H(2)O = dUTP + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.14','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.15','Guanosine deaminase','Guanosine aminase','NULL','NULL','Guanosine + H(2)O = xanthosine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.16','GTP cyclohydrolase I','NULL','NULL','The reaction involves hydrolysis of two C-N bonds and isomerizationof the pentose unit; the recyclization may be non-enzymic.Involved in the de novo synthesis of tetrahydrobiopterin from GTP,with the other enzymes involved being EC 1.1.1.153 and EC 4.2.3.12.','GTP + H(2)O = formate + 2-amino-4-hydroxy-6-(erythro-1,2,3-trihydroxypropyl)-dihydropteridine triphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.17','Adenosine-phosphate deaminase','NULL','NULL','Acts on 5-AMP, ADP, ATP, NAD(+) and adenosine, in decreasing orderof activity.The bacterial enzyme also acts on the deoxy derivatives (cf.EC 3.5.4.6).','5-AMP + H(2)O = 5-IMP + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.18','ATP deaminase','NULL','NULL','NULL','ATP + H(2)O = ITP + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.19','Phosphoribosyl-AMP cyclohydrolase','NULL','NULL','The Neurospora crassa enzyme also catalyzes the reactions ofEC 1.1.1.23 and EC 3.6.1.31.','1-(5-phospho-beta-D-ribosyl)-AMP + H(2)O = 1-(5-phospho-beta-D-ribosyl)-5-((5-phospho-beta-D-ribosylamino)methylideneamino)imidazole-4-carboxamide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.20','Pyrithiamine deaminase','NULL','NULL','NULL','1-(4-amino-2-methylpyrimid-5-ylmethyl)-3-(beta-hydroxyethyl)-2-methylpyridinium bromide + H(2)O = 1-(4-hydroxy-2-methylpyrimid-5-ylmethyl)-3-(beta-hydroxyethyl)-2-methylpyridinium bromide + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.21','Creatinine deaminase','NULL','NULL','NULL','Creatinine + H(2)O = N-methylhydantoin + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.22','1-pyrroline-4-hydroxy-2-carboxylate deaminase','NULL','NULL','NULL','1-pyrroline-4-hydroxy-2-carboxylate + H(2)O = 2,5-dioxopentanoate +NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.23','Blasticidin-S deaminase','NULL','NULL','Catalyzes the deamination of the cytosine group of the antibioticsblasticidin S, cytomycin and acetylblasticidin S.','Blasticidin S + H(2)O = deaminohydroxyblasticidin S + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.24','Sepiapterin deaminase','NULL','NULL','Also acts on isosepiapterin, more slowly.','Sepiapterin + H(2)O = xanthopterin-B(2) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.25','GTP cyclohydrolase II','NULL','NULL','Two C-N bonds are hydrolyzed, releasing formate, with simultaneousremoval of the terminal diphosphate.','GTP + 3 H(2)O = formate + 2,5-diamino-6-hydroxy-4-(5-phospho-D-ribosylamino)pyrimidine + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.26','Diaminohydroxyphosphoribosylaminopyrimidine deaminase','NULL','NULL','The substrate is the product of EC 3.5.4.25.','2,5-diamino-6-hydroxy-4-(5-phosphoribosylamino)pyrimidine + H(2)O =5-amino-6-(5-phosphoribosylamino)uracil + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.27','Methenyltetrahydromethanopterin cyclohydrolase','Methenyl-H(4)MPT cyclohydrolase','NULL','Methanopterin is a pterin analog.Involved in the formation of methane from CO(2) in Methanobacteriumthermoautotrophicum.','5,10-methenyl-5,6,7,8-tetrahydromethanopterin + H(2)O = 5-formyl-5,6,7,8-tetrahydromethanopterin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.28','S-adenosylhomocysteine deaminase','NULL','NULL','NULL','S-adenosyl-L-homocysteine + H(2)O = S-inosyl-L-homocysteine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.29','GTP cyclohydrolase IIa','GTP cyclohydrolase III','Magnesium.','This enzyme catalyzes the hydrolysis of the imidazole ring ofguanosine 5-triphosphate, N(7)-methylguanosine 5-triphosphate orinosine 5-triphosphate.Xanthosine 5-triphosphate and ATP are not substrates.It also catalyzes the hydrolysis of diphosphate to form twoequivalents of phosphate.Unlike EC 3.5.4.25, this enzyme does not release formate, but doeshydrolyze the diphosphate from GTP to phosphate.','GTP + 3 H(2)O = 2-amino-5-formylamino-6-(5-phospho-D-ribosylamino)pyrimidin-4(3H)-one + 2 phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.30','dCTP deaminase (dUMP-forming)','NULL','Magnesium.','Is highly specific for dCTP as substrate as dCMP, CTP, CDP, CMP,cytosine or deoxycytosine are not deaminated.While most bacteria require two enzymes to form dUMP from dCTP(EC 3.5.4.13 and EC 3.6.1.23), the archaeon Methanocaldococcusjannaschii uses a single enzyme to carry out both functions.This enzyme can also act as a dUTP diphosphatase, but more slowly.','dCTP + 2 H(2)O = dUMP + diphosphate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.31','S-methyl-5-thioadenosine deaminase','5-methylthioadenosine deaminase','NULL','The enzyme from Thermotoga maritima also functions asS-adenosylhomocysteine deaminase (EC 3.5.4.28) and has some activityagainst adenosine.Adenosine-5-monophosphate (AMP) and S-adenosyl-L-methionine (SAM) arenot substrates.Formerly EC 3.5.4.n1.','S-methyl-5-thioadenosine + H(2)O = S-methyl-5-thioinosine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.32','8-oxoguanine deaminase','NULL','Zinc.','8-Oxoguanine is formed via the oxidation of guanine within DNA byreactive oxygen species.If uncorrected, this modification leads to the incorporation of8-oxoG:A mismatches and eventually to G:C to T:A transversions.','8-oxoguanine + H(2)O = urate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.33','tRNA(adenine(34)) deaminase','NULL','Zinc.','The enzyme is involved in editing of tRNA.','Adenine(34) in tRNA + H(2)O = hypoxanthine(34) in tRNA + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.34','tRNA(Ala)(adenine(37)) deaminase','NULL','Magnesium.','The enzyme deaminates adenosine(37) to inosine in eukaryotictRNA(Ala).','Adenine(37) in tRNA(Ala) + H(2)O = hypoxanthine(37) in tRNA(Ala) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.35','tRNA(cytosine(8)) deaminase','NULL','NULL','The enzyme from Methanopyrus kandleri specifically catalyzes thedeamimation of cytosine at poition 8 of tRNA in 30 different tRNAs.This cytosine-to-uracil editing guarantees the proper folding andfunctionality of the tRNAs.','Cytosine(8) in tRNA + H(2)O = uracil(8) in tRNA + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.36','mRNA(cytosine(6666)) deaminase','Apolipoprotein B mRNA editing enzyme','NULL','The apolipoprotein B mRNA editing enzyme complex catalyzes theediting of apolipoprotein B mRNA at cytidine(6666) to uridine,thereby transforming the codon for glutamine-2153 to a terminationcodon.Editing results in translation of a truncated apolipoprotein Bisoform (apoB-48) with distinct functions in lipid transport.The catalytic component (APOBEC-1) contains zinc at the active site.','Cytosine(6666) in apolipoprotein B mRNA + H(2)O = uracil(6666) inapolipoprotein B mRNA + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.37','Double-stranded RNA adenine deaminase','Double-stranded RNA adenosine deaminase','NULL','This eukaryotic enzyme is involved in RNA editing.It destabilizes double-stranded RNA through conversion of adenosineto inosine.Inositol hexakisphosphate is required for activity.','Adenine in double-stranded RNA + H(2)O = hypoxanthine in double-strandedRNA + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.38','Single-stranded DNA cytosine deaminase','Activation-induced cytidine deaminase','NULL','The enzyme exclusively catalyzes deamination of cytosine in single-stranded DNA.It preferentially deaminates five-nucleotide bubbles.The optimal target consists of a single-stranded NWRCN motif (W =A or T, R = A or G).The enzyme initiates antibody diversification processes bydeaminating immunoglobulin sequences.','Cytosine in single-stranded DNA + H(2)O = uracil in single-stranded DNA +NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.39','GTP cyclohydrolase IV','NULL','Fe(2+).','The enzyme is involved in methanopterin biosynthesis in methanogenicarchaea.Cf. EC 3.5.4.16, EC 3.5.4.25 and EC 3.5.4.29.Formerly EC 3.5.4.n2.','GTP + H(2)O = formate + 7,8-dihydro-D-neopterin 2,3-cyclic phosphate +diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.40','Aminodeoxyfutalosine deaminase','AFL deaminase','NULL','The enzyme, found in several bacterial species, is part of thefutalosine pathway for menaquinone biosynthesis.','6-amino-6-deoxyfutalosine + H(2)O = futalosine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.n1','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.4.n2','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.5.1','Nitrilase','NULL','NULL','Acts on a wide range of aromatic nitriles including (indol-3-yl)acetonitrile, and also on some aliphatic nitriles, and on thecorresponding acid amides (cf. EC 4.2.1.84).','A nitrile + 2 H(2)O = a carboxylate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.5.2','Ricinine nitrilase','NULL','NULL','NULL','Ricinine + 2 H(2)O = 3-carboxy-4-methoxy-N-methyl-2-pyridone + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.5.3','Transferred entry: 4','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.5.4','Cyanoalanine nitrilase','NULL','NULL','L-asparagine is formed as an intermediate.Cf. EC 4.2.1.65 and EC 3.5.1.1.','3-cyano-L-alanine + 2 H(2)O = L-aspartate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.5.5','Arylacetonitrilase','NULL','NULL','Requires thiol compounds.Also hydrolyzes other 4-substituted phenylacetonitriles, thien-2-ylacetonitrile, tolylacetonitriles, and, more slowly, benzyl cyanide.','4-chlorophenylacetonitrile + 2 H(2)O = 4-chlorophenylacetate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.5.6','Bromoxynil nitrilase','NULL','NULL','Involved in the bacterial degradation of the herbicide bromoxynil.Highly specific.','3,5-dibromo-4-hydroxybenzonitrile + 2 H(2)O = 3,5-dibromo-4-hydroxy-benzoate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.5.7','Aliphatic nitrilase','NULL','NULL','Preferentially hydrolyzes aliphatic nitriles, some of which areapparently not substrates for other known EC 3.5.5.1.Substrates include crotononitrile, acrylonitrile and glutaronitrile.','R-CN + 2 H(2)O = R-COOH + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.5.8','Thiocyanate hydrolase','NULL','NULL','The enzyme from Thiobacillus thioparus catalyzes the first step inthe degradation of thiocyanate.','Thiocyanate + 2 H(2)O = carbonyl sulfide + NH(3) + HO(-).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.99.1','Riboflavinase','NULL','NULL','NULL','Riboflavin + H(2)O = ribitol + lumichrome.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.99.2','Aminopyrimidine aminohydrolase','Thiaminase','NULL','Previously known as thiaminase II, this enzyme is involved in theregeneration of the thiamine pyrimidine from degraded products,rather than in thiamine degradation, and participates in thiaminesalvage pathways.','(1) 4-amino-5-aminomethyl-2-methylpyrimidine + H(2)O = 4-amino-5-hydroxymethyl-2-methylpyrimidine + ammonia.(2) Thiamine + H(2)O = 4-amino-5-hydroxymethyl-2-methylpyrimidine +5-(2-hydroxyethyl)-4-methylthiazole.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.99.3','Hydroxydechloroatrazine ethylaminohydrolase','Hydroxyatrazine ethylaminohydrolase','NULL','Involved in a pathway by which the herbicide atrazine, 2-chloro-4-(ethylamino)-6-(isopropylamino)-1,3,5-triazine, is degraded inbacteria via N-isopropylammelide, 2,4-dihydroxy-6-(isopropylamino)-1,3,5-triazine.','4-(ethylamino)-2-hydroxy-6-(isopropylamino)-1,3,5-triazine + H(2)O =N-isopropylammelide + ethylamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.99.4','N-isopropylammelide isopropylaminohydrolase','NULL','NULL','Involved in a pathway by which the herbicide atrazine, 2-chloro-4-(ethylamino)-6-(isopopylamino)-1,3,5-triazine, is degraded inbacteria via N-isopropylammelide, 2,4-dihydroxy-6-(isopropylamino)-1,3,5-triazine.','N-isopropylammelide + H(2)O = cyanuric acid + isopropylamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.99.5','2-aminomuconate deaminase','NULL','NULL','2-aminomuconate is an intermediate in the bacterial biodegradation ofnitrobenzene.The enzyme has been isolated from several species, includingPseudomonas pseudocaligenes JS45, Pseudomonas fluorescens KU-7,Pseudomonas sp. AP3 and Burkholderia cenocepacia J2315.The reaction is spontaneous in acid conditions.','2-aminomuconate + H(2)O = (3E)-2-oxohex-3-enedioate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.99.6','Glucosamine-6-phosphate deaminase','GlcN6P deaminase','NULL','Isomerization of the aldose-ketose type converts the -CH(-NH(2))-CH=Ogroup of glucosamine 6-phosphate into -C(=NH)-CH(2)-OH, forming2-deoxy-2-imino-D-arabino-hexitol which then hydrolyzes to yieldfructose 6-phosphate and ammonia.N-acetyl-D-glucosamine 6-phosphate, which is not broken down,activates the enzyme.Formerly EC 5.3.1.10.','Alpha-D-glucosamine 6-phosphate + H(2)O = D-fructose 6-phosphate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.99.7','1-aminocyclopropane-1-carboxylate deaminase','1-aminocyclopropane carboxylic acid deaminase','Pyridoxal 5-phosphate.','Its introduction has been used to make fruit ripening dependent onexternally added ethylene, as it removes the substrate for endogenousethylene formation.Formerly EC 4.1.99.4.','1-aminocyclopropane-1-carboxylate + H(2)O = 2-oxobutanoate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.99.8','5-nitroanthranilic acid aminohydrolase','5NAA deaminase','NULL','The enzyme catalyzes the initial step in biodegradation of5-nitroanthranilic acid by Bradyrhizobium sp. strain JS329.','5-nitroanthranilate + H(2)O = 5-nitrosalicylate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.99.9','2-nitroimidazole nitrohydrolase','2NI denitrase','NULL','The enzyme catalyzes the initial step in the biodegradation of2-nitroimidazole by the soil bacterium Mycobacterium sp. JS330.','2-nitroimidazole + H(2)O = imidazol-2-one + nitrite.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.5.99.10','2-iminobutanoate/2-iminopropanoate deaminase','Enamine/imine deaminase','NULL','This enzyme, which has been found in all species and tissuesexamined, catalyzes the hydrolytic deamination of imine intermediatesformed by several types of pyridoxal-5-phosphate-dependentdehydratases, such as EC 4.3.1.19 and EC 4.3.1.17.The reactions, which can occur spontaneously, are accelerated tominimize the cellular damage that could be caused by these reactiveintermediates.','(1) 2-iminobutanoate + H(2)O = 2-oxobutanoate + NH(3).(2) 2-iminopropanoate + H(2)O = pyruvate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.1','Inorganic diphosphatase','Diphosphate phosphohydrolase','NULL','Specificity varies with the source and with the activating metal ion.The enzyme from some sources may be identical with EC 3.1.3.1 orEC 3.1.3.9.','Diphosphate + H(2)O = 2 phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.2','Trimetaphosphatase','NULL','NULL','NULL','Trimetaphosphate + H(2)O = triphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.3','Adenosinetriphosphatase','Adenylpyrophosphatase','NULL','Many enzymes previously listed under this number are now listedseparately as EC 3.6.1.32 to EC 3.6.1.39.The remaining enzymes, not separately listed on the basis of somefunction coupled with hydrolyzes of ATP, include enzymes dependent onCa(2+), Mg(2+), anions, H(+) or DNA.Formerly EC 3.6.1.4.','ATP + H(2)O = ADP + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.4','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.5','Apyrase','Adenosine diphosphatase','Calcium.','Apyrases are active against both di- and triphosphate nucleotides(NDPs and NTPs) and hydrolyze NTPs to nucleotide monophosphates(NMPs) in two distinct successive phosphate-releasing steps, withNDPs as intermediates.They differ from ATPases, which specifically hydrolyze ATP,by hydrolyzing both ATP and ADP.Most of the ecto-ATPases that occur on the cell surface and hydrolyzeextracellular nucleotides belong to this enzyme family.','A nucleoside 5-triphosphate + 2 H(2)O = a nucleoside 5-phosphate +2 phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.6','Nucleoside diphosphate phosphatase','Nucleoside-diphosphatase','NULL','The enzyme, which appears to be limited to metazoa, acts on multiplenucleoside diphosphates as well as on D-ribose 5-diphosphate.Specificity depends on species and isoform.','A nucleoside diphosphate + H(2)O = a nucleoside phosphate + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.7','Acylphosphatase','Acylphosphate phosphohydrolase','NULL','NULL','An acylphosphate + H(2)O = a carboxylate + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.8','ATP diphosphatase','ATP pyrophosphatase','NULL','Also acts on ITP, GTP, CTP and UTP.','ATP + H(2)O = AMP + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.9','Nucleotide diphosphatase','Nucleotide pyrophosphatase','NULL','Substrates include NAD(+), NADP(+), FAD, CoA and also ATP and ADP.','A dinucleotide + H(2)O = 2 mononucleotides.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.10','Endopolyphosphatase','Metaphosphatase','NULL','The product contains 4 or 5 phosphate residues.','Polyphosphate + n H(2)O = (n+1) oligophosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.11','Exopolyphosphatase','Exopolypase','NULL','NULL','(Polyphosphate)(n) + H(2)O = (polyphosphate)(n-1) + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.12','dCTP diphosphatase','dCTP pyrophosphatase','NULL','Also hydrolyzes dCDP to dCMP and phosphate.','dCTP + H(2)O = dCMP + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.13','ADP-ribose diphosphatase','Adenosine diphosphoribose pyrophosphatase','NULL','NULL','ADP-D-ribose + H(2)O = AMP + D-ribose 5-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.14','Adenosine-tetraphosphatase','NULL','NULL','Also acts on inosine tetraphosphate and tripolyphosphate but showslittle or no activity with other nucleotides or polyphosphates.','Adenosine 5-tetraphosphate + H(2)O = ATP + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.15','Nucleoside-triphosphate phosphatase','NTPase','NULL','The enzyme is found in eukaryotes and thermophilic bacteria,but appears to be absent from mesophilic bacteria.Also hydrolyzes nucleoside diphosphates, thiamine diphosphate andFAD.The enzyme from the plant Pisum sativum (garden pea) is regulated bycalmodulin.','NTP + H(2)O = NDP + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.16','CDP-glycerol diphosphatase','CDP-glycerol pyrophosphatase','NULL','NULL','CDP-glycerol + H(2)O = CMP + sn-glycerol 3-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.17','Bis(5-nucleosyl)-tetraphosphatase (asymmetrical)','Ap(4)A hydrolase','NULL','Also acts on bis(5-xanthosyl)-tetraphosphate and, more slowly,on bis(5-adenosyl)-tetraphosphate and bis(5-uridyl)-tetraphosphate(cf. EC 3.6.1.41).','P(1),P(4)-bis(5-guanosyl) tetraphosphate + H(2)O = GTP + GMP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.18','FAD diphosphatase','FAD pyrophosphatase','NULL','The plant enzyme also hydrolyzes NAD(+) and NADH; the animal enzymehydrolyzes NAD(+) and CoA at about half of the rate of hydrolysis ofFAD.May be identical with EC 3.6.1.9.','FAD + H(2)O = AMP + FMN.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.19','Nucleoside-triphosphate diphosphatase','Nucleoside-triphosphate pyrophosphatase','NULL','May be identical with EC 3.6.1.9.','A nucleoside triphosphate + H(2)O = a nucleotide + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.20','5-acylphosphoadenosine hydrolase','NULL','NULL','Also acts on inosine and uridine compounds.','5-acylphosphoadenosine + H(2)O = AMP + a carboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.21','ADP-sugar diphosphatase','ADP-sugar pyrophosphatase','NULL','Has a distinct specificity from EC 3.6.1.45.','ADP-sugar + H(2)O = AMP + alpha-D-aldose 1-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.22','NAD(+) diphosphatase','NAD(+) pyrophosphatase','NULL','Also acts on NADP(+), 3-acetylpyridine and the thionicotinamideanalogs of NAD(+) and NADP(+).','NAD(+) + H(2)O = AMP + NMN.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.23','dUTP diphosphatase','Deoxyuridine-triphosphatase','NULL','NULL','dUTP + H(2)O = dUMP + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.24','Nucleoside phosphoacylhydrolase','NULL','NULL','Attacks ribonucleoside 5-nitrophenylphosphates, but is inactiveagainst phosphodiesters.','Hydrolyzes mixed phospho-anhydride bonds.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.25','Triphosphatase','NULL','NULL','NULL','Triphosphate + H(2)O = diphosphate + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.26','CDP-diacylglycerol diphosphatase','CDP-diacylglycerol phosphatidylhydrolase','NULL','NULL','CDP-diacylglycerol + H(2)O = CMP + phosphatidate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.27','Undecaprenyl-diphosphate phosphatase','C(55)-isoprenyl diphosphatase','NULL','Isolated from the bacteria Micrococcus lysodeikticus, Escherichiacoli and Bacillus subtilis.The product of the reaction, ditrans,octacis-undecaprenyl phosphate,is essential for cell wall polysaccharide biosynthesis in thesestrains.','Ditrans,octacis-undecaprenyl diphosphate + H(2)O = ditrans,octacis-undecaprenyl phosphate + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.28','Thiamine-triphosphatase','ThTPase','NULL','NULL','Thiamine triphosphate + H(2)O = thiamine diphosphate + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.29','Bis(5-adenosyl)-triphosphatase','AP(3)A hydrolase','NULL','NULL','P(1)-P(3)-bis(5-adenosyl) triphosphate + H(2)O = ADP + AMP.', 'NULL', 'NULL');
