INSERT INTO enzyme VALUES ( 'EC 3.6.1.31','Phosphoribosyl-ATP diphosphatase','Phosphoribosyl-ATP pyrophosphatase','NULL','The Neurospora crassa enzyme also catalyzes the reactions ofEC 1.1.1.23 and EC 3.5.4.19.','1-(5-phospho-beta-D-ribosyl)-ATP + H(2)O = 1-(5-phospho-beta-D-ribosyl)-AMP + diphosphate.', 'NULL', 'NULL');
