INSERT INTO enzyme VALUES ( 'EC 3.6.1.39','Thymidine-triphosphatase','NULL','NULL','Also acts, more slowly, on dUTP and UTP.','dTTP + H(2)O = dTDP + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.40','Guanosine-5-triphosphate,3-diphosphate diphosphatase','Guanosine pentaphosphate phosphohydrolase','NULL','Also hydrolyzes other guanosine 5-triphosphate derivatives with atleast one unsubstituted phosphate group on the 3-position, but notGTP, ATP or adenosine 5-triphosphate,3-diphosphate.','Guanosine 5-triphosphate,3-diphosphate + H(2)O = guanosine3,5-bis(diphosphate) + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.41','Bis(5-nucleosyl)-tetraphosphatase (symmetrical)','Ap(4)A hydrolase','NULL','Also acts on bis(5-guanosyl) tetraphosphate and bis(5-adenosyl)pentaphosphate, and, more slowly, on some other polyphosphates,forming a nucleoside bisphosphate as one product in all cases (cf.EC 3.6.1.17).','P(1),P(4)-bis(5-adenosyl) tetraphosphate + H(2)O = 2 ADP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.42','Guanosine-diphosphatase','GDPase','NULL','Also acts on UDP but not on other nucleoside diphosphates andtriphosphates.','GDP + H(2)O = GMP + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.43','Dolichyldiphosphatase','NULL','NULL','NULL','Dolichyl diphosphate + H(2)O = dolichyl phosphate + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.44','Oligosaccharide-diphosphodolichol diphosphatase','Oligosaccharide-diphosphodolichol pyrophosphatase','NULL','NULL','Oligosaccharide-diphosphodolichol + H(2)O = oligosaccharide phosphate +dolichyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.6.1.45','UDP-sugar diphosphatase','Nucleosidediphosphate-sugar diphosphatase','Divalent cation.','UDP-sugar is the best substrate, although other nucleoside-sugardiphosphates are used as substrates with similar Km values but muchlower maximum velocities.Thus, this enzyme has a specificity distinct from that ofEC 3.6.1.21.Some but not all enzymes of this class also appear to have EC 3.1.3.5activity.','UDP-sugar + H(2)O = UMP + alpha-D-aldose 1-phosphate.', 'NULL', 'NULL');
