INSERT INTO enzyme VALUES ( 'EC 4.1.2.3','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.4','Deoxyribose-phosphate aldolase','2-deoxy-D-ribose-5-phosphate acetaldehyde-lyase','NULL','NULL','2-deoxy-D-ribose 5-phosphate = D-glyceraldehyde 3-phosphate +acetaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.5','L-threonine aldolase','L-threonine acetaldehyde-lyase','Pyridoxal 5-phosphate.','This enzyme is specific for L-threonine and can not utilize L-allo-threonine.Different from EC 4.1.2.49 and EC 4.1.2.48.','L-threonine = glycine + acetaldehyde.', 'NULL', 'NULL');
