INSERT INTO enzyme VALUES ( 'EC 4.1.2.8','Indole-3-glycerol-phosphate lyase','(1S,2R)-1-C-(indol-3-yl)glycerol 3-phosphate D-glyceraldehyde-3-phosphate-lyase','NULL','Forms part of the defense mechanism against insects and microbialpathogens in the grass family, Gramineae, where it catalyzes one ofthe steps in the formation of the cyclic hydroxamic acids 2,4-dihydroxy-2H-1,4-benzoxazin-3(4H)-one (DIBOA) and 2,4-dihydroxy-7-methoxy-2H-1,4-benzoxazin-3(4H)-one (DIMBOA).Resembles the alpha-subunit of EC 4.2.1.20 but, unlike tryptophansynthase, its activity is independent of the beta-subunit and freeindole is released.','(1S,2R)-1-C-(indol-3-yl)glycerol 3-phosphate = indole + D-glyceraldehyde3-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.9','Phosphoketolase','D-xylulose-5-phosphate D-glyceraldehyde-3-phosphate-lyase (phosphate-acetylating)','Thiamine diphosphate.','NULL','D-xylulose 5-phosphate + phosphate = acetyl phosphate + D-glyceraldehyde3-phosphate + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.10','(R)-mandelonitrile lyase','(R)-oxynitrilase','Flavoprotein.','A variety of enzymes from different sources and with differentproperties.Some are flavoproteins, others are not.Active toward a number of aromatic and aliphatic hydroxynitriles(cyanohydrins).','(R)-mandelonitrile = cyanide + benzaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.11','Hydroxymandelonitrile lyase','(S)-4-hydroxymandelonitrile hydroxybenzaldehyde-lyase','NULL','Does not accept aliphatic hydroxynitriles, unlike EC 4.1.2.10,EC 4.1.2.46 and EC 4.1.2.47.','(S)-4-hydroxymandelonitrile = cyanide + 4-hydroxybenzaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.12','2-dehydropantoate aldolase','2-dehydropantoate formaldehyde-lyase','NULL','NULL','2-dehydropantoate = 3-methyl-2-oxobutanoate + formaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.13','Fructose-bisphosphate aldolase','Aldolase','Zinc.','Also acts on (3S,4R)-ketose 1-phosphates.The enzymes increase electron-attraction by the carbonyl group, some(Class I) forming a protonated imine with it, others (Class II),mainly of microbial origin, polarizing it with a metal ion, e.g.zinc.Formerly EC 4.1.2.7.','D-fructose 1,6-bisphosphate = glycerone phosphate + D-glyceraldehyde3-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.14','2-dehydro-3-deoxy-phosphogluconate aldolase','2-dehydro-3-deoxy-D-gluconate-6-phosphate D-glyceraldehyde-3-phosphate-lyase','NULL','The enzyme shows no activity with 2-dehydro-3-deoxy-6-phosphate-D-galactonate (cf. EC 4.1.2.55).Also acts on 2-oxobutanoate.','2-dehydro-3-deoxy-6-phosphate-D-gluconate = pyruvate + D-glyceraldehyde3-phosphate.', 'NULL', 'NULL');
