INSERT INTO enzyme VALUES ( 'EC 4.1.2.17','L-fuculose-phosphate aldolase','Fuculose aldolase','NULL','NULL','L-fuculose 1-phosphate = glycerone phosphate + (S)-lactaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.18','2-dehydro-3-deoxy-L-pentonate aldolase','2-dehydro-3-deoxy-L-pentonate glycolaldehyde-lyase','NULL','NULL','2-dehydro-3-deoxy-L-pentonate = pyruvate + glycolaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.19','Rhamnulose-1-phosphate aldolase','L-rhamnulose 1-phosphate aldolase','NULL','NULL','L-rhamnulose 1-phosphate = glycerone phosphate + (S)-lactaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.20','2-dehydro-3-deoxyglucarate aldolase','2-dehydro-3-deoxy-D-glucarate tartronate-semialdehyde-lyase','NULL','NULL','2-dehydro-3-deoxy-D-glucarate = pyruvate + tartronate semialdehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.21','2-dehydro-3-deoxy-6-phosphogalactonate aldolase','2-dehydro-3-deoxy-D-galactonate-6-phosphate D-glyceraldehyde-3-phosphate-lyase','NULL','The enzyme catalyzes the last reaction in a D-galactose degradationpathway.Cf. EC 4.1.2.55.','2-dehydro-3-deoxy-6-phospho-D-galactonate = pyruvate + D-glyceraldehyde3-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.22','Fructose-6-phosphate phosphoketolase','D-fructose-6-phosphate D-erythrose-4-phosphate-lyase (phosphate-acetylating)','Thiamine diphosphate.','Also acts on D-xylulose 5-phosphate.','D-fructose 6-phosphate + phosphate = acetyl phosphate + D-erythrose4-phosphate + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.23','3-deoxy-D-manno-octulosonate aldolase','3-deoxy-D-manno-octulosonate D-arabinose-lyase','NULL','NULL','3-deoxy-D-manno-octulosonate = pyruvate + D-arabinose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.24','Dimethylaniline-N-oxide aldolase','N,N-dimethylaniline-N-oxide formaldehyde-lyase','NULL','Acts on various N,N-dialkylarylamides.','N,N-dimethylaniline N-oxide = N-methylaniline + formaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.25','Dihydroneopterin aldolase','2-amino-4-hydroxy-6-(D-erythro-1,2,3-trihydroxypropyl)-7,8-dihydropteridine glycolaldehyde-lyase','NULL','NULL','2-amino-4-hydroxy-6-(D-erythro-1,2,3-trihydroxypropyl)-7,8-dihydropteridine = 2-amino-4-hydroxy-6-hydroxymethyl-7,8-dihydropteridine+ glycolaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.26','Phenylserine aldolase','L-threo-3-phenylserine benzaldehyde-lyase','Pyridoxal 5-phosphate.','NULL','L-threo-3-phenylserine = glycine + benzaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.27','Sphinganine-1-phosphate aldolase','Sphinganine-1-phosphate palmitaldehyde-lyase','Pyridoxal 5-phosphate.','NULL','Sphinganine 1-phosphate = phosphoethanolamine + palmitaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.28','2-dehydro-3-deoxy-D-pentonate aldolase','2-keto-3-deoxy-D-pentonate aldolase','NULL','NULL','2-dehydro-3-deoxy-D-pentonate = pyruvate + glycolaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.29','5-dehydro-2-deoxyphosphogluconate aldolase','5-dehydro-2-deoxy-D-gluconate-6-phosphate malonate-semialdehyde-lyase','NULL','NULL','5-dehydro-2-deoxy-D-gluconate 6-phosphate = glycerone phosphate +malonate semialdehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.30','17-alpha-hydroxyprogesterone aldolase','17-alpha-hydroxyprogesterone acetaldehyde-lyase','NULL','A microsomal hemeprotein that catalyzes two independent reactions atthe same active site - the 17-hydroxylation of pregnenolone andprogesterone, which is part of glucocorticoid hormones biosynthesis(EC 1.14.99.9), and the conversion of the 17-hydroxylated productsvia a 17,20-lyase reaction to form androstenedione anddehydroepiandrosterone, leading to sex hormone biosynthesis.The enzymes from different organisms differ in their substratespecificity.While the enzymes from pig, hamster, and rat accept both 17-alpha-hydroxyprogesterone and 17-alpha-hydroxypregnenolone, the enzymesfrom human, bovine, sheep, goat, and bison do not accept the former,and the enzyme from guinea pig does not accept the latter.','17-alpha-hydroxyprogesterone = androst-4-ene-3,17-dione + acetaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.31','Transferred entry: 4','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.32','Trimethylamine-oxide aldolase','Trimethylamine-N-oxide formaldehyde-lyase','NULL','NULL','Trimethylamine N-oxide = dimethylamine + formaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.33','Fucosterol-epoxide lyase','(24R,24R)-fucosterol-epoxide acetaldehyde-lyase','NULL','The insect enzyme is involved in the conversion of sitosterol intocholesterol.','(24R,24(1)R)-fucosterol epoxide = desmosterol + acetaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.34','4-(2-carboxyphenyl)-2-oxobut-3-enoate aldolase','(3E)-4-(2-carboxyphenyl)-2-oxobut-3-enoate 2-carboxybenzaldehyde-lyase','NULL','Involved, with EC 1.13.11.38, in the metabolism of phenanthrene inbacteria.','(3Z)-4-(2-carboxyphenyl)-2-oxobut-3-enoate + H(2)O = 2-formylbenzoate +pyruvate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.35','Propioin synthase','4-hydroxy-3-hexanone aldolase','NULL','NULL','4-hydroxy-3-hexanone = 2 propanal.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.2.36','Lactate aldolase','(S)-lactate acetaldehyde-lyase','NULL','NULL','(S)-lactate = formate + acetaldehyde.', 'NULL', 'NULL');
