INSERT INTO enzyme VALUES ( 'EC 4.1.3.16','4-hydroxy-2-oxoglutarate aldolase','2-keto-4-hydroxyglutarate aldolase','NULL','The enzymes from rat liver and bovine liver act on both enantiomersof 4-hydroxy-2-oxoglutarate.Formerly EC 4.1.2.1 and EC 4.1.2.31.','4-hydroxy-2-oxoglutarate = pyruvate + glyoxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.1.3.17','4-hydroxy-4-methyl-2-oxoglutarate aldolase','4-carboxy-4-hydroxy-2-oxoadipate aldolase','Divalent cation.','This enzyme participates in the degradation of protocatechuate (viathe meta-cleavage pathway), phthalate, syringate and gallate.The enzyme from Pseudomonas ochraceae can also cleave 4-hydroxy-2-oxoglutarate to glyoxylate and pyruvate, and also catalyzes thereaction of EC 4.1.1.3.','(1) 4-hydroxy-4-methyl-2-oxoglutarate = 2 pyruvate.(2) 2-hydroxy-4-oxobutane-1,2,4-tricarboxylate = oxaloacetate + pyruvate.', 'NULL', 'NULL');
