INSERT INTO enzyme VALUES ( 'EC 4.2.99.20','2-succinyl-6-hydroxy-2,4-cyclohexadiene-1-carboxylate synthase','2-succinyl-6-hydroxy-2,4-cyclohexadiene-1-carboxylic acid synthase','NULL','Involved in the biosynthesis of vitamin K2 (menaquinone).In most anaerobes and all Gram-positive aerobes, menaquinone is thesole electron transporter in the respiratory chain and is essentialfor their survival.It had previously been thought that the reactions carried out by thisenzyme and EC 2.2.1.9 were carried out by a single enzyme but thishas since been disproved.Formerly EC 2.5.1.64 and EC 4.2.99.n1.','5-enolpyruvoyl-6-hydroxy-2-succinylcyclohex-3-ene-1-carboxylate =(1R,6R)-6-hydroxy-2-succinylcyclohexa-2,4-diene-1-carboxylate + pyruvate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.2.99.21','Isochorismate lyase','Isochorismate pyruvate lyase','NULL','This enzyme is part of the pathway of salicylate formation fromchorismate, and forms an integral part of pathways that producesalicylate-derived siderophores, such as pyochelin andyersiniabactin.','Isochorismate = salicylate + pyruvate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.2.99.22','Tuliposide A-converting enzyme','6-O-(4 -hydroxy-2 -methylenebutyryl)-D-glucose acyltransferase (lactone-forming)','NULL','Isolated from the plant Tulipa gesneriana (tulip).The reaction is an intramolecular transesterification producing thelactone.The enzyme also has a weak activity with 6-tuliposide B and 6-O-benzoyl-D-glucose.','6-tuliposide A = tulipalin A + D-glucose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.2.99.n1','Transferred entry: 4','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.1','Aspartate ammonia-lyase','Aspartase','NULL','NULL','L-aspartate = fumarate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.2','Methylaspartate ammonia-lyase','Beta-methylaspartase','Cobalamin.','NULL','L-threo-3-methylaspartate = mesaconate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.3','Histidine ammonia-lyase','Histidase','NULL','Member of the aromatic amino acid lyase family, other members ofwhich are EC 4.3.1.23, EC 4.3.1.24 and EC 4.3.1.25.Catalyzes the first step in the degradation of histidine and theproduct, urocanic acid, is further metabolized to glutamate.','L-histidine = urocanate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.4','Formimidoyltetrahydrofolate cyclodeaminase','Formiminotetrahydrofolate cyclodeaminase','NULL','In eukaroytes, occurs as a bifunctional enzyme that also hasEC 2.1.2.5 activity.','5-formimidoyltetrahydrofolate = 5,10-methenyltetrahydrofolate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.5','Transferred entry: 4','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.6','Beta-alanyl-CoA ammonia-lyase','NULL','NULL','NULL','Beta-alanyl-CoA = acryloyl-CoA + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.7','Ethanolamine ammonia-lyase','NULL','Cobalamin.','NULL','Ethanolamine = acetaldehyde + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.8','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.9','Glucosaminate ammonia-lyase','2-amino-2-deoxy-D-gluconate ammonia-lyase','Pyridoxal 5-phosphate.','Formerly EC 4.2.1.26 and EC 4.3.1.21.','D-glucosaminate = 2-dehydro-3-deoxy-D-gluconate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.10','Serine-sulfate ammonia-lyase','NULL','NULL','NULL','L-serine O-sulfate + H(2)O = pyruvate + NH(3) + sulfate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.11','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.12','Ornithine cyclodeaminase','OCD','NAD(+).','The enzyme is a member of the mu-crystallin protein family.The reaction is stimulated by the presence of ADP or ATP and isinhibited by O(2).','L-ornithine = L-proline + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.13','Carbamoyl-serine ammonia-lyase','O-carbamoyl-L-serine deaminase','Pyridoxal 5-phosphate.','NULL','O-carbamoyl-L-serine + H(2)O = pyruvate + 2 NH(3) + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.14','3-aminobutyryl-CoA ammonia-lyase','L-3-aminobutyryl-CoA deaminase','NULL','Hydroxylamine can replace ammonia as a substrate.Crotonoyl-pantetheine can replace crotonoyl-CoA but it is a poorersubstrate.','L-3-aminobutyryl-CoA = crotonoyl-CoA + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.15','Diaminopropionate ammonia-lyase','2,3-diaminopropanoate ammonia-lyase','Pyridoxal 5-phosphate.','Active toward both D- and L-diaminopropionate.D- and L-serine are poor substrates.','2,3-diaminopropionate + H(2)O = pyruvate + 2 NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.16','Threo-3-hydroxy-L-aspartate ammonia-lyase','L-threo-3-hydroxyaspartate dehydratase','Pyridoxal 5-phosphate.','The enzyme, purified from the bacterium Pseudomonas sp. T62,is highly specific, and does not accept any other stereoisomer of3-hydroxyaspartate.Different from EC 4.3.1.20 and EC 4.3.1.27.Requires a divalent cation such as Mn(2+), Mg(2+), or Ca(2+).','Threo-3-hydroxy-L-aspartate = oxaloacetate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.17','L-serine ammonia-lyase','L-hydroxyaminoacid dehydratase','Pyridoxal 5-phosphate or iron-sulfur.','The reaction catalyzed by both types of enzymes involves the initialelimination of water to form an enamine intermediate (hence theenzymes original classification as EC 4.2.1.13) followed bytautomerization to an imine form and hydrolysis of the C-N bond.The latter reaction, which can occur spontaneously, is also becatalyzed by EC 3.5.99.10.This reaction is also carried out by EC 4.3.1.19 from a number ofsources.','L-serine = pyruvate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.18','D-serine ammonia-lyase','D-hydroxy amino acid dehydratase','Pyridoxal 5-phosphate.','Also acts, slowly, on D-threonine.The reaction catalyzed probably involves initial elimination of water(hence the enzymes original classification as EC 4.2.1.14), followedby isomerization and hydrolysis of the product with C-N bondbreakage.Formerly EC 4.2.1.14.','D-serine = pyruvate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.19','Threonine ammonia-lyase','L-serine dehydratase','Pyridoxal 5-phosphate or iron-sulfur.','The reaction catalyzed by both types of enzymes involves the initialelimination of water to form an enamine intermediate (hence theenzymes original classification as EC 4.2.1.16), followed bytautomerization to an imine form and hydrolysis of the C-N bond.The latter reaction, which can occur spontaneously, is also becatalyzed by EC 3.5.99.10.The enzymes from a number of sources also act on L-serine,Cf. EC 4.3.1.17.','L-threonine = 2-oxobutanoate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.20','Erythro-3-hydroxy-L-aspartate ammonia-lyase','3-hydroxyaspartate dehydratase','Pyridoxal 5-phosphate.','The enzyme, which was characterized from the bacterium Paracoccusdenitrificans NCIMB 8944, is highly specific for the L-isomer oferythro-3-hydroxyaspartate.Different from EC 4.3.1.16 and EC 4.3.1.27.Requires a divalent cation such as Mn(2+), Mg(2+), or Ca(2+).Formerly EC 4.2.1.38.','Erythro-3-hydroxy-L-aspartate = oxaloacetate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.21','Transferred entry: 4','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.22','3,4-dihydroxyphenylalanine reductive deaminase','DOPA-reductive deaminase','NULL','Forms part of the L-phenylalanine-catabolism pathway in theanoxygenic phototrophic bacterium Rhodobacter sphaeroides OU5.NADPH is oxidized more slowly than NADH.','L-dopa + NADH = 3,4-dihydroxyphenylpropanoate + NAD(+) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.23','Tyrosine ammonia-lyase','L-tyrosine ammonia-lyase','MIO.','Member of the aromatic amino acid lyase family, other members ofwhich are EC 4.3.1.3, EC 4.3.1.24 and EC 4.3.1.25.Far more active with tyrosine than with phenylalanine as substrate,but the substrate specificity can be switched by mutation of a singleamino acid (H89F) in the enzyme from the bacterium Rhodobactersphaeroides.Formerly EC 4.3.1.5.','L-tyrosine = trans-p-hydroxycinnamate + ammonia.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.24','Phenylalanine ammonia-lyase','NULL','MIO.','Member of the aromatic amino acid lyase family, other members ofwhich are EC 4.3.1.3 and EC 4.3.1.23 and EC 4.3.1.25.The enzyme from some species is highly specific for phenylalanine.Formerly EC 4.3.1.5.','L-phenylalanine = trans-cinnamate + ammonia.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.25','Phenylalanine/tyrosine ammonia-lyase','Bifunctional PAL','MIO.','Member of the aromatic amino acid lyase family, other members ofwhich are EC 4.3.1.3, EC 4.3.1.23 and EC 4.3.1.24.The enzyme from some monocots, including maize, and from the yeastRhodosporidium toruloides, deaminate L-phenylalanine and L-tyrosinewith similar catalytic efficiency.Formerly EC 4.3.1.5.','(1) L-phenylalanine = trans-cinnamate + ammonia.(2) L-tyrosine = trans-p-hydroxycinnamate + ammonia.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.26','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.27','Threo-3-hydroxy-D-aspartate ammonia-lyase','D-threo-3-hydroxyaspartate dehydratase','Pyridoxal 5-phosphate.','The enzyme, purified from the bacterium Delftia sp. HT23, also hasactivity against L-threo-3-hydroxyaspartate, L-erythro-3-hydroxyaspartate, and D-serine.Different from EC 4.3.1.20 and EC 4.3.1.16.Requires a divalent cation such as Mn(2+), Co(2+) or Ni(2+).','Threo-3-hydroxy-D-aspartate = oxaloacetate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.28','L-lysine cyclodeaminase','NULL','NAD(+).','The enzyme produces the non-proteinogenic amino acid L-pipecolate,which is incorporated into multiple secondary metabolite products,including rapamycin, tobulysin, virginiamycin and pristinamycin.','L-lysine = L-pipecolate + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.29','D-glucosaminate-6-phosphate ammonia lyase','NULL','NULL','The enzyme, from the bacterium Salmonella typhimurium, is involved inthe degradation pathway of D-glucosaminate.','6-phospho-D-glucosaminate = 2-dehydro-3-deoxy-6-phospho-D-gluconate +NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.1.30','dTDP-4-amino-4,6-dideoxy-D-glucose ammonia-lyase','NULL','Iron-sulfur.','The enzyme, which is a member of the AdoMet radical (radical SAM)family, is involved in biosynthesis of TDP-alpha-D-desosamine.The reaction starts by the transfer of an electron from the reducedform of the enzymes [4Fe-4S] cluster to S-adenosyl-L-methionine,spliting it into methionine and the radical 5-deoxyadenosin-5-yl,which attacks the sugar substrate.','dTDP-4-amino-4,6-dideoxy-alpha-D-glucopyranose + S-adenosyl-L-methionine+ reduced acceptor = dTDP-3-dehydro-4,6-dideoxy-alpha-D-glucopyranose +NH(3) + L-methionine + 5-deoxyadenosine + acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.2.1','Argininosuccinate lyase','Arginosuccinase','NULL','NULL','2-(N(omega)-L-arginino)succinate = fumarate + L-arginine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.2.2','Adenylosuccinate lyase','Adenylosuccinase','NULL','Also acts on 1-(5-phosphoribosyl)-4-(N-succinocarboxamide)-5-aminoimidazole.','(1) N(6)-(1,2-dicarboxyethyl)AMP = fumarate + AMP.(2) (S)-2-(5-amino-1-(5-phospho-D-ribosyl)imidazole-4-carboxamido)succinate = fumarate + 5-amino-1-(5-phospho-D-ribosyl)imidazole-4-carboxamide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.2.3','Ureidoglycolate lyase','Ureidoglycolase','NULL','This microbial enzyme is involved in the degradation ofureidoglycolate, an intermediate of purine degradation.Not to be confused with EC 3.5.1.116 which releases ammonia ratherthan urea.','(S)-ureidoglycolate = glyoxylate + urea.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.2.4','Purine imidazole-ring cyclase','NULL','NULL','Also acts on 2,6-diamino-5-formamido-3,4-dihydro-4-oxopyrimidineresidues.Brings about the reclosure of the imidazole rings of purine residuesdamaged by gamma-rays.','DNA 4,6-diamino-5-formamidopyrimidine = DNA adenine + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.2.5','Peptidylamidoglycolate lyase','Alpha-hydroxyglycine amidating dealkylase','NULL','Acts on the product of the reaction catalyzed by EC 1.14.17.3, thusremoving a terminal glycine residue and leaving a des-glycine peptideamide.','Peptidylamidoglycolate = peptidyl amide + glyoxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.2.6','Gamma-L-glutamyl-butirosin B gamma-glutamyl cyclotransferase','NULL','NULL','The enzyme catalyzes the last step in the biosynthesis of theaminoglycoside antibiotic butirosin B.The enzyme acts as a cyclotransferase, cleaving the amide bond viatransamidation using the alpha-amine of the terminal gamma-L-glutamate of the side chain, releasing it as the cyclic 5-oxoproline.','Gamma-L-glutamyl-butirosin B = butirosin B + 5-oxoproline.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.3.1','3-ketovalidoxylamine C-N-lyase','NULL','Calcium.','Eliminates 4-nitroaniline from 4-nitrophenyl-3-ketovalidamine or4-nitrophenol from 4-nitrophenyl-alpha-D-3-dehydroglucoside.Involved in the degradation of the fungicide validamycin A byFlavobacterium saccharophilum.','4-nitrophenyl-3-ketovalidamine = 4-nitroaniline + 5-D-(5/6)-5-C-(hydroxymethyl)-2,6-dihydroxycyclohex-2-en-1-one.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.3.2','Strictosidine synthase','STR','NULL','Catalyzes a a Pictet-Spengler reaction between the aldehyde group ofsecologanin and the amino group of tryptamine.Involved in the biosynthesis of the monoterpenoid indole alkaloids.','3-alpha-(S)-strictosidine + H(2)O = tryptamine + secologanin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.3.3','Deacetylisoipecoside synthase','NULL','NULL','The enzyme from the leaves of Alangium lamarckii differs inenantiomeric specificity from EC 4.3.3.4.The product is rapidly converted to demethylisoalangiside.','Deacetylisoipecoside + H(2)O = dopamine + secologanin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.3.4','Deacetylipecoside synthase','NULL','NULL','The enzyme from the leaves of Alangium lamarckii differs inenantiomeric specificity from EC 4.3.3.3.The product is rapidly converted to demethylalangiside.','Deacetylipecoside + H(2)O = dopamine + secologanin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.3.5','4-demethylrebeccamycin synthase','Arcyriaflavin A N-glycosyltransferase','NULL','This enzyme catalyzes a step in the biosynthesis of rebeccamycin,an indolocarbazole alkaloid produced by the actinobacteriumLechevalieria aerocolonigenes.The enzyme is a glycosylase, and acts in the reverse direction tothat shown.It has a wide substrate range, and was shown to glycosylate severalsubstrates, including the staurosporine aglycone, EJG-III-108A,J-104303, 6-N-methyl-arcyriaflavin C and indolo-[2,3-a]-carbazole.','4-O-demethylrebeccamycin + H(2)O = dichloro-arcyriaflavin A + beta-D-glucose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.3.6','Pyridoxal 5-phosphate synthase (glutamine hydrolyzing)','NULL','NULL','The ammonia is provided by the glutaminase subunit and channeled tothe active site of the lyase subunit by a 100 A tunnel.The enzyme can also use ribulose 5-phosphate and dihydroxyacetonephosphate.The enzyme complex is found in aerobic bacteria, archeae, fungi andplants.','D-ribose 5-phosphate + D-glyceraldehyde 3-phosphate + L-glutamine =pyridoxal 5-phosphate + L-glutamate + 3 H(2)O + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.3.7','4-hydroxy-tetrahydrodipicolinate synthase','DHDPS','NULL','Formerly EC 4.2.1.52.','Pyruvate + L-aspartate-4-semialdehyde = (4S)-4-hydroxy-2,3,4,5-tetrahydro-(2S)-dipicolinate + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.3.n1','Transferred entry: 4','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.99.1','Transferred entry: 4','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.99.2','Carboxybiotin decarboxylase','Carboxybiotin protein decarboxylase','NULL','The integral membrane protein MadB from the anaerobic bacteriumMalonomonas rubra is a component of the multienzyme complexEC 4.1.1.89.The free energy of the decarboxylation reaction is used to pump Na(+)out of the cell.','A carboxybiotinyl-[protein] + 1-2 Na(+)(In) + H(+)(Out) = CO(2) +a biotinyl-[protein] + 1-2 Na(+)(Out).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.99.3','7-carboxy-7-deazaguanine synthase','7-carboxy-7-carbaguanine synthase','Iron-sulfur; Magnesium; S-adenosyl-L-methionine.','The enzyme is a member of the superfamily of S-adenosyl-L-methionine-dependent radical (radical AdoMet) enzymes.Binds a [4Fe-4S] cluster that is coordinated by 3 cysteines and anexchangeable S-adenosyl-L-methionine molecule.The S-adenosyl-L-methionine is catalytic as it is regenerated at theend of the reaction.The reaction is part of the biosynthesis pathway of queuosine.','6-carboxy-5,6,7,8-tetrahydropterin = 7-carboxy-7-carbaguanine + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.3.99.4','Choline trimethylamine-lyase','NULL','NULL','The enzyme utilizes a glycine radical to break the C-N bond incholine.Found in choline-degrading anaerobic bacteria.','Choline = trimethylamine + acetaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.1','Cystathionine gamma-lyase','Cystathionase','Pyridoxal 5-phosphate.','Also catalyzes elimination reactions of L-homoserine to form H(2)O,NH(3) and 2-oxobutanoate, of L-cystine, producing thiocysteine,pyruvate and NH(3), and of L-cysteine producing pyruvate, NH(3) andH(2)S.Formerly EC 4.2.1.15.','L-cystathionine + H(2)O = L-cysteine + NH(3) + 2-oxobutanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.2','Homocysteine desulfhydrase','Homocysteine desulfurase','Pyridoxal 5-phosphate.','Formerly EC 1.2.1.1.','L-homocysteine + H(2)O = H(2)S + NH(3) + 2-oxobutanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.3','Dimethylpropiothetin dethiomethylase','NULL','NULL','NULL','S,S-dimethyl-beta-propiothetin = dimethyl sulfide + acrylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.4','Alliin lyase','Alliinase','Pyridoxal 5-phosphate.','NULL','An S-alkyl-L-cysteine S-oxide = an alkyl sulfenate + 2-aminoacrylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.5','Lactoylglutathione lyase','(R)-S-lactoylglutathione methylglyoxal-lyase (isomerizing)','NULL','Also acts on 3-phosphoglycerol-glutathione.','(R)-S-lactoylglutathione = glutathione + methylglyoxal.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.6','S-alkylcysteine lyase','S-alkyl-L-cysteinase','Pyridoxal 5-phosphate.','Decomposes S-alkyl-L-cysteines by alpha,beta-elimination.Possibly identical, in Saccharomyces cerevisiae, with EC 4.4.1.8.','An S-alkyl-L-cysteine + H(2)O = an alkyl thiol + NH(3) + pyruvate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.7','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.8','Cystathionine beta-lyase','Beta C-S lyase','Pyridoxal 5-phosphate.','The enzyme from some sources also acts on L-cystine, formingpyruvate, ammonia and cysteine persulfide, and a number of relatedcompounds.Possibly identical, in Saccharomyces cerevisiae, with EC 4.4.1.6.','L-cystathionine + H(2)O = L-homocysteine + NH(3) + pyruvate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.9','L-3-cyanoalanine synthase','Beta-cyano-L-alanine synthase','Pyridoxal 5-phosphate.','NULL','L-cysteine + hydrogen cyanide = L-3-cyanoalanine + hydrogen sulfide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.10','Cysteine lyase','NULL','Pyridoxal 5-phosphate.','Can use a second molecule of cysteine (producing lanthionine) orother alkyl thiols as replacing agents.','L-cysteine + sulfite = L-cysteate + H(2)S.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.11','Methionine gamma-lyase','L-methioninase','Pyridoxal 5-phosphate.','NULL','L-methionine + H(2)O = methanethiol + NH(3) + 2-oxobutanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.12','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.13','Cysteine-S-conjugate beta-lyase','L-cysteine-S-conjugate thiol-lyase (deaminating)','Pyridoxal 5-phosphate.','R may represent aromatic compounds such as 4-bromobenzene and 2,4-dinitrobenzene.','RS-CH(2)-CH(NH(3)(+))COO(-) + H(2)O = RSH + NH(3) + pyruvate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.14','1-aminocyclopropane-1-carboxylate synthase','1-aminocyclopropane-1-carboxylic acid synthase','Pyridoxal 5-phosphate.','Catalyzes an alpha,gamma-elimination.','S-adenosyl-L-methionine = 1-aminocyclopropane-1-carboxylate +methylthioadenosine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.15','D-cysteine desulfhydrase','NULL','NULL','NULL','D-cysteine + H(2)O = H(2)S + NH(3) + pyruvate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.16','Selenocysteine lyase','Selenocysteine beta-lyase','Pyridoxal 5-phosphate.','Dithiothreitol or 2-mercaptoethanol can act as the reducing agent inthe reaction.The enzyme from animals does not act on cysteine, serine orchloroalanine, while the enzyme from bacteria shows activity withcysteine (cf. EC 2.8.1.7).','L-selenocysteine + reduced acceptor = selenide + L-alanine + acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.17','Holocytochrome-c synthase','Cytochrome c heme-lyase','NULL','In the reverse direction, the enzyme catalyzes the attachment of hemeto two cysteine residues in the protein, forming thioether links.','Holocytochrome c = apocytochrome c + heme.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.18','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.19','Phosphosulfolactate synthase','(2R)-phospho-3-sulfolactate synthase','Magnesium.','The enzyme from Methanococcus jannaschii catalyzes the Michaeladdition of sulfite to phosphoenolpyruvate.It specifically requires phosphoenolpyruvate and its broad alkalinepH optimum suggests that it uses sulfite rather than bisulfite.','(2R)-2-O-phospho-3-sulfolactate = phosphoenolpyruvate + hydrogen sulfite.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.20','Leukotriene-C(4) synthase','Leukotriene A(4):glutathione S-leukotrienyltransferase','NULL','The reaction proceeds in the direction of addition.Not identical with EC 2.5.1.18.Formerly EC 2.5.1.37.','Leukotriene C(4) = leukotriene A(4) + glutathione.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.21','S-ribosylhomocysteine lyase','S-ribosylhomocysteinase','Fe(2+).','The 4,5-dihydroxypentan-2,3-dione formed spontaneously cyclizes andcombines with borate to form an autoinducer (AI-2) in the bacterialquorum-sensing mechanism, which is used by many bacteria to controlgene expression in response to cell density.Formerly EC 3.2.1.148 and EC 3.3.1.3.','S-(5-deoxy-D-ribos-5-yl)-L-homocysteine = L-homocysteine + (4S)-4,5-dihydroxypentan-2,3-dione.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.22','S-(hydroxymethyl)glutathione synthase','Glutathione-dependent formaldehyde-activating enzyme','NULL','The enzyme from Paracoccus denitrificans accelerates the spontaneousreaction in which the adduct of formaldehyde and glutathione isformed, i.e. the substrate for EC 1.1.1.284, in the formaldehyde-detoxification pathway.Formerly EC 1.2.1.1.','S-(hydroxymethyl)glutathione = glutathione + formaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.23','2-hydroxypropyl-CoM lyase','Coenzyme M-epoxyalkane ligase','Zinc.','Acts on both enantiomers of chiral epoxyalkanes to form thecorresponding (R)- and (S)-2-hydroxyalkyl-CoM adducts.The enzyme will function with some other thiols (e.g.,2-sulfanylethanol) as the nucleophile.Uses short-chain epoxyalkanes from C(2) (epoxyethane) to C(6) (1,2-epoxyhexane).This enzyme forms component I of a four-component enzyme system(comprising EC 4.4.1.23 (component I), EC 1.8.1.5 (component II),EC 1.1.1.268 (component III) and EC 1.1.1.269 (component IV)) that isinvolved in epoxyalkane carboxylation in Xanthobacter sp. strain Py2.Formerly EC 4.2.99.19.','(1) (R)-2-hydroxypropyl-CoM = (R)-1,2-epoxypropane + HS-CoM.(2) (S)-2-hydroxypropyl-CoM = (S)-1,2-epoxypropane + HS-CoM.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.24','(2R)-sulfolactate sulfo-lyase','Sulfolactate sulfo-lyase','Fe(2+).','This inducible enzyme participates in cysteate degradation inParacoccus pantotrophus NKNCYSA and in 3-sulfolactate degradation inChromohalobacter salexigens.The enzyme is specific for the (R) isomer.','(2R)-3-sulfolactate = pyruvate + bisulfite.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.25','L-cysteate sulfo-lyase','NULL','Pyridoxal 5-phosphate.','D-cysteine can also act as a substrate, but more slowly.It is converted into pyruvate, sulfide and NH(3).The inducible enzyme from Silicibacter pomeroyi DSS-3 forms part ofthe cysteate-degradation pathway.','L-cysteate + H(2)O = pyruvate + bisulfite + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.26','Olivetolic acid cyclase','NULL','NULL','Part of the cannabinoids biosynthetic pathway in the plant Cannabissativa.','3,5,7-trioxododecanoyl-CoA = CoA + 2,4-dihydroxy-6-pentylbenzoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.4.1.27','Carbon disulfide lyase','Carbon disulfide hydrolase','Zn(2+).','The hyperthermophilic archaeon Acidianus sp. A1-3 obtains energy bythe conversion of carbon disulfide to hydrogen sulfide, with carbonylsulfide as an intermediate.','Carbon disulfide + 2 H(2)O = CO(2) + 2 hydrogen sulfide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.5.1.1','DDT-dehydrochlorinase','NULL','NULL','NULL','1,1,1-trichloro-2,2-bis(4-chlorophenyl)ethane = 1,1-dichloro-2,2-bis(4-chlorophenyl)ethylene + chloride.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.5.1.2','3-chloro-D-alanine dehydrochlorinase','NULL','Pyridoxal 5-phosphate.','Also catalyzes beta-replacement reactions, e.g. converts 3-chloro-D-alanine and H(2)S into D-cysteine and HCl.','3-chloro-D-alanine + H(2)O = pyruvate + HCl + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.5.1.3','Dichloromethane dehalogenase','NULL','Glutathione.','NULL','Dichloromethane + H(2)O = formaldehyde + 2 HCl.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.5.1.4','L-2-amino-4-chloropent-4-enoate dehydrochlorinase','NULL','NULL','NULL','L-2-amino-4-chloropent-4-enoate + H(2)O = 2-oxopent-4-enoate + HCl +NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.5.1.5','S-carboxymethylcysteine synthase','NULL','Pyridoxal 5-phosphate.','NULL','3-chloro-L-alanine + thioglycolate = S-carboxymethyl-L-cysteine +chloride.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 4.6.1.1','Adenylate cyclase','3,5-cyclic AMP synthetase','NULL','Also acts on dATP to form 3,5-cyclic dAMP.Requires pyruvate.Activated by NAD(+) in presence of EC 2.4.2.31.','ATP = 3,5-cyclic AMP + diphosphate.', 'NULL', 'NULL');
