INSERT INTO enzyme VALUES ( 'EC 5.1.1.2','Methionine racemase','NULL','Pyridoxal 5-phosphate.','NULL','L-methionine = D-methionine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.1.3','Glutamate racemase','NULL','Pyridoxal 5-phosphate.','NULL','L-glutamate = D-glutamate.', 'NULL', 'NULL');
