INSERT INTO enzyme VALUES ( 'EC 5.1.1.11','Phenylalanine racemase (ATP-hydrolyzing)','NULL','NULL','NULL','ATP + L-phenylalanine + H(2)O = AMP + diphosphate + D-phenylalanine.', 'NULL', 'NULL');
