INSERT INTO enzyme VALUES ( 'EC 5.1.2.3','3-hydroxybutyryl-CoA epimerase','NULL','NULL','NULL','(S)-3-hydroxybutanoyl-CoA = (R)-3-hydroxybutanoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.2.4','Acetoin racemase','Acetylmethylcarbinol racemase','NULL','NULL','(S)-acetoin = (R)-acetoin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.2.5','Tartrate epimerase','NULL','NULL','NULL','(R,R)-tartrate = meso-tartrate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.2.6','Isocitrate epimerase','NULL','NULL','(1R,2S)-1-hydroxypropane-1,2,3-tricarboxylate is the commonlyoccurring isomer of isocitrate.','(1R,2S)-1-hydroxypropane-1,2,3-tricarboxylate = (1S,2S)-1-hydroxypropane-1,2,3-tricarboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.1','Ribulose-phosphate 3-epimerase','D-ribulose phosphate-3-epimerase','NULL','Also converts D-erythrose 4-phosphate into D-erythrulose 4-phosphateand D-threose 4-phosphate.','D-ribulose 5-phosphate = D-xylulose 5-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.2','UDP-glucose 4-epimerase','Galactowaldenase','NAD(+).','Also acts on UDP-2-deoxyglucose.','UDP-alpha-D-glucose = UDP-alpha-D-galactose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.3','Aldose 1-epimerase','Aldose mutarotase','NULL','Also acts on L-arabinose, D-xylose, D-galactose, maltose and lactose.This enzyme catalyzes the first step in galactose metabolism byconverting beta-D-galactose into alpha-D-galactose, which is thesubstrate for EC 2.7.1.6.','Alpha-D-glucose = beta-D-glucose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.4','L-ribulose-5-phosphate 4-epimerase','L-ribulose 5-phosphate 4-epimerase','Divalent cation.','NULL','L-ribulose 5-phosphate = D-xylulose 5-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.5','UDP-arabinose 4-epimerase','NULL','NULL','NULL','UDP-L-arabinose = UDP-D-xylose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.6','UDP-glucuronate 4-epimerase','UDP glucuronic epimerase','NULL','NULL','UDP-glucuronate = UDP-D-galacturonate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.7','UDP-N-acetylglucosamine 4-epimerase','UDP-GlcNAc 4-epimerase','NULL','NULL','UDP-N-acetyl-alpha-D-glucosamine = UDP-N-acetyl-alpha-D-galactosamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.8','N-acylglucosamine 2-epimerase','GlcNAc 2-epimerase','NULL','Requires catalytic amounts of ATP.','N-acyl-D-glucosamine = N-acyl-D-mannosamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.9','N-acylglucosamine-6-phosphate 2-epimerase','Acylglucosamine phosphate 2-epimerase','NULL','NULL','N-acyl-D-glucosamine 6-phosphate = N-acyl-D-mannosamine 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.10','CDP-paratose 2-epimerase','CDP-abequose epimerase','NAD(+).','CDP-paratose (CDP-3,6-dideoxy-D-glucose), is more systematicallycalled CDP-alpha-3,6-dideoxy-D-ribo-hexose.CDP-tyvelose (CDP-3,6-dideoxy-D-mannose) is systematically calledCDP-3,6-dideoxy-D-arabino-hexose.','CDP-3,6-dideoxy-D-glucose = CDP-3,6-dideoxy-D-mannose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.11','Cellobiose epimerase','NULL','NULL','NULL','Cellobiose = D-glucosyl-D-mannose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.12','UDP-glucuronate 5-epimerase','UDP-glucuronic acid 5-epimerase','NAD(+).','NULL','UDP-glucuronate = UDP-L-iduronate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.13','dTDP-4-dehydrorhamnose 3,5-epimerase','dTDP-4-keto-6-deoxyglucose 3,5-epimerase','NULL','Occurs in a complex with EC 1.1.1.133.','dTDP-4-dehydro-6-deoxy-alpha-D-glucose = dTDP-4-dehydro-beta-L-rhamnose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.14','UDP-N-acetylglucosamine 2-epimerase (non-hydrolyzing)','UDP-GlcNAc-2-epimerase','NULL','This bacterial enzyme catalyzes the reversible interconversion ofUDP-GlcNAc and UDP-ManNAc.The latter is used in a variety of bacterial polysaccharidebiosyntheses.Cf. EC 3.2.1.183.','UDP-N-acetyl-alpha-D-glucosamine = UDP-N-acetyl-alpha-D-mannosamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.15','Glucose-6-phosphate 1-epimerase','NULL','NULL','NULL','Alpha-D-glucose 6-phosphate = beta-D-glucose 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.16','UDP-glucosamine 4-epimerase','NULL','NULL','NULL','UDP-glucosamine = UDP-galactosamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.17','Heparosan-N-sulfate-glucuronate 5-epimerase','NULL','NULL','Acts on D-glucuronosyl residues adjacent to sulfated D-glucosamineunits in the heparin precursor.Not identical with EC 5.1.3.19.','Heparosan-N-sulfate D-glucuronate = heparosan-N-sulfate L-iduronate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.18','GDP-mannose 3,5-epimerase','NULL','NULL','NULL','GDP-alpha-D-mannose = GDP-beta-L-galactose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.19','Chondroitin-glucuronate 5-epimerase','Dermatan-sulfate 5-epimerase','NULL','Not identical with EC 5.1.3.17.','Chondroitin D-glucuronate = dermatan L-iduronate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.20','ADP-glyceromanno-heptose 6-epimerase','ADP-L-glycero-D-manno-heptose 6-epimerase','NAD(+).','NULL','ADP-D-glycero-D-manno-heptose = ADP-L-glycero-D-manno-heptose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.21','Maltose epimerase','NULL','NULL','The enzyme catalyzes the interconversion of alpha and beta anomers ofmaltose more effectively than those of disaccharides such as lactoseand cellobiose.','Alpha-maltose = beta-maltose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.22','L-ribulose-5-phosphate 3-epimerase','L-xylulose 5-phosphate 3-epimerase','NULL','Along with EC 4.1.1.85, this enzyme is involved in a pathway for theutilization of L-ascorbate by Escherichia coli.','L-ribulose 5-phosphate = L-xylulose 5-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.23','UDP-2,3-diacetamido-2,3-dideoxyglucuronic acid 2-epimerase','2,3-diacetamido-2,3-dideoxy-alpha-D-glucuronic acid 2-epimerase','NULL','Participates in the biosynthetic pathway for UDP-alpha-D-ManNAc3NAcA(UDP-2,3-diacetamido-2,3-dideoxy-alpha-D-mannuronic acid),an important precursor of the B-band lipopolysaccharide ofPseudomonas aeroginosa serotype O5 and of the band-A trisaccharide ofBordetella pertussis, both important respiratory pathogens.Highly specific as UDP-alpha-D-GlcNAc, UDP-alpha-D-GlcNAcA (UDP-2-acetamido-2-deoxy-alpha-D-glucuronic acid) and UDP-alpha-D-GlcNAc3NAc(UDP-2,3-diacetamido-2,3-dideoxy-alpha-D-glucose) cannot act assubstrates.','UDP-2,3-diacetamido-2,3-dideoxy-alpha-D-glucuronate = UDP-2,3-diacetamido-2,3-dideoxy-alpha-D-mannuronate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.24','N-acetylneuraminate epimerase','Alpha-N-acetylneuraminic acid epimerase','NULL','Sialoglycoconjugates present in vertebrates are linked exclusively byalpha-linkages and are released in alpha form during degradation.This enzyme accelerates maturotation to the beta form (which alsooccurs as a slow spontaneous reaction), which is necessary forfurther metabolism by the bacteria.Formerly EC 5.1.3.n1.','N-acetyl-alpha-neuraminate = N-acetyl-beta-neuraminate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.25','dTDP-L-rhamnose 4-epimerase','dTDP-4-L-rhamnose 4-epimerase','NULL','The equilibrium is strongly toward dTDP-beta-L-rhamnose.','dTDP-6-deoxy-beta-L-talose = dTDP-beta-L-rhamnose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.26','N-acetyl-alpha-D-glucosaminyl-diphospho-ditrans,octacis-undecaprenol4-epimerase','GlcNAc-P-P-Und 4-epimerase','NULL','The enzyme is involved in biosynthesis of the repeatingtetrasaccharide unit of the O-antigen produced by some Gram-negativebacteria.','N-acetyl-alpha-D-glucosaminyl-diphospho-ditrans,octacis-undecaprenol =N-acetyl-alpha-D-galactosaminyl-diphospho-ditrans,octacis-undecaprenol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.27','dTDP-4-dehydro-6-deoxy-D-glucose 3-epimerase','dTDP-4-keto-6-deoxy-D-glucose 3-epimerase','NULL','The enzyme is involved in the biosynthetic pathway of dTDP-6-deoxy-alpha-D-allose, which is converted to mycinose after attachment ogthe aglycones of several macrolide antibiotics, including tylosin,chalcomycin, dihydrochalcomycin, and mycinamicin II.','dTDP-4-dehydro-6-deoxy-alpha-D-glucose = dTDP-4-dehydro-6-deoxy-alpha-D-allose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.28','UDP-N-acetyl-L-fucosamine synthase','NULL','NULL','Isolated from the bacteria Pseudomonas aeruginosa and Staphylococcusaureus.Involved in bacterial polysaccharide biosynthesis.','UDP-2-acetamido-2,6-dideoxy-beta-L-talose = UDP-N-acetyl-beta-L-fucosamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.n1','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.n2','L-fucose mutarotase','Fucose 1-epimerase','NULL','NULL','Alpha-L-fucose = beta-L-fucose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.3.n3','L-rhamnose mutarotase','Rhamnose 1-epimerase','NULL','NULL','Alpha-L-rhamnose = beta-L-rhamnose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.99.1','Methylmalonyl-CoA epimerase','2-methyl-3-oxopropanoyl-CoA 2-epimerase','NULL','NULL','(R)-methylmalonyl-CoA = (S)-methylmalonyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.99.2','16-hydroxysteroid epimerase','NULL','NULL','NULL','16-alpha-hydroxysteroid = 16-beta-hydroxysteroid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.99.3','Allantoin racemase','NULL','NULL','NULL','(S)(+)-allantoin = (R)(-)-allantoin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.99.4','Alpha-methylacyl-CoA racemase','NULL','NULL','Alpha-methyl-branched acyl-CoA derivatives with chain lengths of morethan C(10) are substrates.Also active toward some aromatic compounds (e.g. ibuprofen) and bileacid intermediates, such as trihydroxycoprostanoyl-CoA.Not active toward free acids.','(2S)-2-methylacyl-CoA = (2R)-2-methylacyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.99.5','Hydantoin racemase','5-monosubstituted-hydantoin racemase','NULL','This enzyme, along with EC 3.5.1.77, EC 3.5.1.87 and EC 3.5.2.2,forms part of the reaction cascade known as the hydantoinaseprocess, which allows the total conversion of D,L-5-monosubstitutedhydantoins into optically pure D- or L-amino acids.The enzyme from Pseudomonas sp. (HyuE) has a preference forhydantoins with aliphatic substituents, e.g. D- and L-5-(2-methylthioethyl)hydantoin, whereas that from Arthrobacteraurescens shows highest activity with arylalkyl substituents,especially 5-benzylhydantoin, at the 5-position.In the enzyme from Sinorhizobium meliloti, Cys-76 is responsible forrecognition and proton retrieval of D-isomers, while Cys-181 isresponsible for L-isomer recognition and racemization.','D-5-monosubstituted hydantoin = L-5-monosubstituted hydantoin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.1.99.6','NAD(P)H-hydrate epimerase','NAD(P)HX epimerase','NULL','The enzyme can use either (R)-NADH-hydrate or (R)-NADPH-hydrate as asubstrate.Its physiological role is to convert the (R) forms to the (S) forms,which could then be restored to active dinucleotides by EC 4.2.1.93.','(1) (6R)-6-beta-hydroxy-1,4,5,6-tetrahydronicotinamide-adeninedinucleotide = (6S)-6-beta-hydroxy-1,4,5,6-tetrahydronicotinamide-adeninedinucleotide.(2) (6R)-6-beta-hydroxy-1,4,5,6-tetrahydronicotinamide-adeninedinucleotide phosphate = (6S)-6-beta-hydroxy-1,4,5,6-tetrahydronicotinamide-adenine dinucleotide phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.1','Maleate isomerase','NULL','NULL','NULL','Maleate = fumarate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.2','Maleylacetoacetate isomerase','Maleylacetone isomerase','NULL','Also acts on maleylpyruvate.','4-maleylacetoacetate = 4-fumarylacetoacetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.3','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.4','Maleylpyruvate isomerase','NULL','NULL','NULL','3-maleylpyruvate = 3-fumarylpyruvate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.5','Linoleate isomerase','NULL','NULL','NULL','9-cis,12-cis-octadecadienoate = 9-cis,11-trans-octadecadienoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.6','Furylfuramide isomerase','NULL','NAD(+).','NULL','(E)-2-(2-furyl)-3-(5-nitro-2-furyl)acrylamide = (Z)-2-(2-furyl)-3-(5-nitro-2-furyl)acrylamide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.7','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.8','Peptidylprolyl isomerase','Cyclophilin','NULL','The first type of this enzyme found proved to be the proteincyclophilin, which binds the immunosuppressant cyclosporin A.Other distinct families of the enzyme exist, one being FK-506 bindingproteins (FKBP) and another that includes parvulin from Escherichiacoli.The three families are structurally unrelated and can bedistinguished by being inhibited by cyclosporin A, FK-506 and5-hydroxy-1,4-naphthoquinone, respectively.','Peptidylproline (omega=180) = peptidylproline (omega=0).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.9','Farnesol 2-isomerase','NULL','NULL','NULL','(2E,6E)-farnesol = (2Z,6E)-farnesol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.10','2-chloro-4-carboxymethylenebut-2-en-1,4-olide isomerase','2-chlorocarboxymethylenebutenolide isomerase','NULL','NULL','Cis-2-chloro-4-carboxymethylenebut-2-en-1,4-olide = trans-2-chloro-4-carboxymethylenebut-2-en-1,4-olide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.11','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.12','Zeta-carotene isomerase','15-cis-zeta-carotene isomerase','NULL','Involved in carotenoid biosynthesis.','9,15,9-tri-cis-zeta-carotene = 9,9-di-cis-zeta-carotene.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.13','Prolycopene isomerase','Carotene cis-trans isomerase','FADH(2).','The enzyme is involved in carotenoid biosynthesis.','7,9,7,9-tetra-cis-lycopene = all-trans-lycopene.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.2.1.14','Beta-carotene isomerase','NULL','NULL','The enzyme participates in a pathway leading to biosynthesis ofstrigolactones, plant hormones involved in promotion of symbioticassociations known as arbuscular mycorrhiza.','All-trans-beta-carotene = 9-cis-beta-carotene.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.1','Triose-phosphate isomerase','Phosphotriose isomerase','NULL','NULL','D-glyceraldehyde 3-phosphate = glycerone phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.2','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.3','D-arabinose isomerase','Arabinose isomerase','Manganese.','The enzyme binds the closed form of the sugar and catalyzes ringopening to generate a form of open-chain conformation thatfacilitates the isomerization reaction, which proceeds via an ene-diol mechanism.The enzyme catalyzes the aldose-ketose isomerization of severalsugars.Most enzymes also catalyze the reaction of EC 5.3.1.25.The enzyme from the bacterium Falsibacillus pallidus also convertsD-altrose to D-psicose.Cf. EC 5.3.1.4.','D-arabinose = D-ribulose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.4','L-arabinose isomerase','NULL','Divalent cation.','The enzyme binds the closed form of the sugar and catalyzes ringopening to generate a form of open-chain conformation thatfacilitates the isomerization reaction, which proceeds via an ene-diol mechanism.The enzyme can also convert D-galactose to D-tagatose with lowerefficiency.','L-arabinose = L-ribulose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.5','Xylose isomerase','D-xylose ketoisomerase','Magnesium.','The enzyme catalyzes the interconversion of aldose and ketose sugarswith broad substrate specificity.The enzyme binds the closed form of its sugar substrate (in the caseof glucose, only the alpha anomer) and catalyzes ring opening togenerate a form of open-chain conformation that is coordinated to oneof the metal sites.Isomerization proceeds via a hydride-shift mechanism.','D-xylopyranose = D-xylulose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.6','Ribose-5-phosphate isomerase','5-phosphoribose isomerase','NULL','Also acts on D-ribose 5-diphosphate and D-ribose 5-triphosphate.','D-ribose 5-phosphate = D-ribulose 5-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.7','Mannose isomerase','D-mannose isomerase','NULL','Also acts on D-lyxose and D-rhamnose.','D-mannose = D-fructose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.8','Mannose-6-phosphate isomerase','Phosphohexoisomerase','Zinc.','NULL','D-mannose 6-phosphate = D-fructose 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.9','Glucose-6-phosphate isomerase','Hexose monophosphate isomerase','NULL','Also catalyzes the anomerization of D-glucose 6-phosphate.','D-glucose 6-phosphate = D-fructose 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.10','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.11','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.12','Glucuronate isomerase','D-glucuronate isomerase','NULL','Also converts D-galacturonate to D-tagaturonate.','D-glucuronate = D-fructuronate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.13','Arabinose-5-phosphate isomerase','Arabinose phosphate isomerase','NULL','NULL','D-arabinose 5-phosphate = D-ribulose 5-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.14','L-rhamnose isomerase','Rhamnose isomerase','Divalent cation.','The enzyme binds the closed ring form of the substrate and catalyzesring opening to generate a form of open-chain conformation that iscoordinated to one of the metal sites.Isomerization proceeds via a hydride-shift mechanism.While the enzyme from the bacterium Escherichia coli is specific forL-rhamnose, the enzyme from the bacterium Pseudomonas stutzeri hasbroad substrate specificity and catalyzes the interconversion ofL-mannose and L-fructose, L-lyxose and L-xylulose, D-ribose andD-ribulose, and D-allose and D-psicose.','L-rhamnopyranose = L-rhamnulose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.15','D-lyxose ketol-isomerase','D-lyxose isomerase','NULL','NULL','D-lyxose = D-xylulose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.16','1-(5-phosphoribosyl)-5-((5-phosphoribosylamino)methylideneamino)imidazole-4-carboxamideisomerase','N-(5-phospho-D-ribosylformimino)-5-amino-1-(5-phosphoribosyl)-4-imidazolecarboxamide isomerase','NULL','Involved in histidine biosynthesis.','1-(5-phospho-beta-D-ribosyl)-5-((5-phospho-beta-D-ribosylamino)methylideneamino)imidazole-4-carboxamide = 5-((5-phospho-1-deoxy-D-ribulos-1-ylamino)methylideneamino)-1-(5-phospho-beta-D-ribosyl)imidazole-4-carboxamide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.17','5-dehydro-4-deoxy-D-glucuronate isomerase','4-deoxy-L-threo-5-hexosulose-uronate ketol-isomerase','NULL','The enzyme is involved in the degradation of polygalacturonate,a later stage in the degradation of pectin by many microorganisms.','5-dehydro-4-deoxy-D-glucuronate = 3-deoxy-D-glycero-2,5-hexodiulosonate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.18','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.19','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.20','Ribose isomerase','D-ribose isomerase','NULL','Also acts on L-lyxose and L-rhamnose.','D-ribose = D-ribulose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.21','Corticosteroid side-chain-isomerase','NULL','NULL','An epimerization at C-20 and C-21 is probably catalyzed by the sameenzyme.','11-deoxycorticosterone = 20-hydroxy-3-oxopregn-4-en-21-al.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.22','Hydroxypyruvate isomerase','NULL','NULL','NULL','Hydroxypyruvate = 2-hydroxy-3-oxopropanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.23','S-methyl-5-thioribose-1-phosphate isomerase','1-phospho-5-S-methylthioribose isomerase','NULL','NULL','S-methyl-5-thio-alpha-D-ribose 1-phosphate = S-methyl-5-thio-D-ribulose1-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.24','Phosphoribosylanthranilate isomerase','N-(5-phosphoribosyl)anthranilate isomerase','NULL','In some organisms, this enzyme is part of a multifunctional proteintogether with one or more components of the system for biosynthesisof tryptophan (EC 2.4.2.18, EC 4.1.1.48, EC 4.1.3.27,and EC 4.2.1.20).','N-(5-phospho-beta-D-ribosyl)anthranilate = 1-(2-carboxyphenylamino)-1-deoxy-D-ribulose 5-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.25','L-fucose isomerase','NULL','Manganese.','The enzyme binds the closed form of the sugar and catalyzes ringopening to generate a form of open-chain conformation thatfacilitates the isomerization reaction, which proceeds via an ene-diol mechanism.The enzyme from Escherichia coli can also convert D-arabinose toD-ribulose.The enzyme from the thermophilic bacterium Caldicellulosiruptorsaccharolyticus also converts D-altrose to D-psicose and L-galactoseto L-tagatose.','L-fucopyranose = L-fuculose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.26','Galactose-6-phosphate isomerase','NULL','NULL','Involved in the tagatose 6-phosphate pathway of lactose catabolism inbacteria.','D-galactose 6-phosphate = D-tagatose 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.27','6-phospho-3-hexuloisomerase','3-hexulose-6-phosphate isomerase','NULL','This enzyme, along with EC 4.1.2.43, plays a key role in theribulose-monophosphate cycle of formaldehyde fixation, which ispresent in many microorganisms that are capable of utilizingC1-compounds.The hyperthermophilic and anaerobic archaeon Pyrococcus horikoshiiOT3 constitutively produces a bifunctional enzyme that sequentiallycatalyzes the reactions of EC 4.1.2.43 and this enzyme.','D-arabino-hex-3-ulose 6-phosphate = D-fructose 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.28','D-sedoheptulose 7-phosphate isomerase','Phosphoheptose isomerase','NULL','In Gram-negative bacteria the enzyme is involved in biosynthesis ofADP-L-glycero-beta-D-manno-heptose, which is utilized for assembly ofthe lipopolysaccharide inner core.In Gram-positive bacteria the enzyme is involved in biosynthesis ofGDP-D-glycero-alpha-D-manno-heptose, which is required for assemblyof S-layer glycoprotein.','D-sedoheptulose 7-phosphate = D-glycero-D-manno-heptose 7-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.29','Ribose 1,5-bisphosphate isomerase','Ribulose 1,5-bisphosphate synthase','NULL','This archaeal enzyme is involved in AMP metabolism and CO(2) fixationthrough type III RubisCO enzymes.The enzyme is activated by cAMP.Formerly EC 5.3.1.n2.','Alpha-D-ribose 1,5-bisphosphate = D-ribulose 1,5-bisphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.30','5-deoxy-glucuronate isomerase','5DG isomerase','NULL','The enzyme, found in the bacterium Bacillus subtilis, is part of amyo-inositol degradation pathway leading to acetyl-CoA.Formerly EC 5.3.1.n1.','5-deoxy-D-glucuronate = 5-dehydro-2-deoxy-D-gluconate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.n1','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.1.n2','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.2.1','Phenylpyruvate tautomerase','Phenylpyruvate keto--enol tautomerase','NULL','Also acts on other arylpyruvates.','Keto-phenylpyruvate = enol-phenylpyruvate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.2.2','Oxaloacetate tautomerase','Oxalacetic keto--enol isomerase','NULL','NULL','Keto-oxaloacetate = enol-oxaloacetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.2.3','TDP-4-oxo-6-deoxy-alpha-D-glucose-3,4-oxoisomerase (dTDP-3-dehydro-6-deoxy-alpha-D-galactopyranose-forming)','dTDP-6-deoxy-hex-4-ulose isomerase','NULL','The enzyme is involved in the biosynthesis of dTDP-3-acetamido-3,6-dideoxy-alpha-D-galactose.Four moieties of alpha-D-rhamnose and two moities of 3-acetamido-3,6-dideoxy-alpha-D-galactose form the repeating unit of the glycan chainin the S-layer of the bacterium Aneurinibacillus thermoaerophilus.','dTDP-4-dehydro-6-deoxy-alpha-D-glucopyranose = dTDP-3-dehydro-6-deoxy-alpha-D-galactopyranose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.2.4','TDP-4-oxo-6-deoxy-alpha-D-glucose-3,4-oxoisomerase (dTDP-3-dehydro-6-deoxy-alpha-D-glucopyranose-forming)','dTDP-4-keto-6-deoxy-D-glucose-3,4-ketoisomerase','NULL','The enzyme is involved in biosynthesis of D-mycaminose.','dTDP-4-dehydro-6-deoxy-alpha-D-glucopyranose = dTDP-3-dehydro-6-deoxy-alpha-D-glucopyranose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.2.5','2,3-diketo-5-methylthiopentyl-1-phosphate enolase','DK-MTP-1-P enolase','NULL','The enzyme participates in the methionine salvage pathway in Bacillussubtilis.In some species a single bifunctional enzyme, EC 3.1.3.77, catalyzesboth this reaction and that of EC 3.1.3.87.Formerly EC 5.3.2.n1.','5-(methylthio)-2,3-dioxopentyl phosphate = 2-hydroxy-5-(methylthio)-3-oxopent-1-enyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.2.6','2-hydroxymuconate tautomerase','4-oxalocrotonate isomerase','NULL','Involved in the meta-cleavage pathway for the degradation of phenols,modified phenols and catechols.The enol form (2Z,4E)-2-hydroxyhexa-2,4-dienedioate is produced aspart of this pathway and is converted to the keto form (3E)-2-oxohex-3-enedioate by the enzyme.Another keto form, (4E)-2-oxohex-4-enedioate (4-oxalocrotonate),was originally thought to be produced by the enzyme but later shownto be produced non-enzymatically.','(2Z,4E)-2-hydroxyhexa-2,4-dienedioate = (3E)-2-oxohex-3-enedioate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.2.7','Ascopyrone tautomerase','1,5-anhydro-D-glycero-hex-3-en-2-ulose tautomerase','NULL','Catalyzes one of the steps in the anhydrofructose pathway, whichleads to the degradation of glycogen and starch via 1,5-anhydro-D-fructose.The other enzymes involved in this pathway are EC 4.2.1.110,EC 4.2.1.111 and EC 4.2.2.13.Ascopyrone P is an anti-oxidant.Formerly EC 5.3.3.15.','1,5-anhydro-4-deoxy-D-glycero-hex-3-en-2-ulose = 1,5-anhydro-4-deoxy-D-glycero-hex-1-en-3-ulose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.2.8','4-oxalomesaconate tautomerase','NULL','NULL','This enzyme has been characterized from the bacterium Pseudomonasputida KT2440 and is involved in the degradation pathway of syringateand gallate.It catalyzes the interconversion of two of the tautomers of4-oxalomesaconate, a reaction that can also occur spontaneously.Formerly EC 5.3.3.16.','(1E)-4-oxobut-1-ene-1,2,4-tricarboxylate = (1E,3E)-4-hydroxybuta-1,3-diene-1,2,4-tricarboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.2.n1','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.1','Steroid Delta-isomerase','3-oxosteroid isomerase','NULL','NULL','A 3-oxo-Delta(5)-steroid = a 3-oxo-Delta(4)-steroid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.2','Isopentenyl-diphosphate Delta-isomerase','IPP isomerase','Calcium or magnesium or manganese; FMN or FAD.','NULL','Isopentenyl diphosphate = dimethylallyl diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.3','Vinylacetyl-CoA Delta-isomerase','Vinylacetyl coenzyme A isomerase','NULL','Also acts on 3-methyl-vinylacetyl-CoA.','Vinylacetyl-CoA = (E)-but-2-enoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.4','Muconolactone Delta-isomerase','Muconolactone isomerase','NULL','Formerly EC 3.1.1.16.','(S)-5-oxo-2,5-dihydrofuran-2-acetate = 5-oxo-4,5-dihydrofuran-2-acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.5','Cholestenol Delta-isomerase','NULL','NULL','NULL','5-alpha-cholest-7-en-3-beta-ol = 5-alpha-cholest-8-en-3-beta-ol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.6','Methylitaconate Delta-isomerase','NULL','NULL','Forms part of the nicotinate-fermentation catabolism pathway inEubacterium barkeri.Other enzymes involved in this pathway are EC 1.17.1.5, EC 1.3.7.1,EC 3.5.2.18, EC 1.1.1.291, EC 5.4.99.4, EC 4.2.1.85 and EC 4.1.3.32.','Methylitaconate = 2,3-dimethylmaleate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.7','Aconitate Delta-isomerase','Aconitate isomerase','NULL','Cis-aconitate is used to designate the isomer (Z)-prop-1-ene-1,2,3-tricarboxylate.This isomerization could take place either in a direct cis-transinterconversion or by an allylic rearrangement; the enzyme has beenshown to catalyze the latter change.','Trans-aconitate = cis-aconitate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.8','Dodecenoyl-CoA isomerase','3,2-trans-enoyl-CoA isomerase','NULL','Also catalyzes the interconversion of 3-acetylenic fatty acylthioesters and (+)-2,3-dienoyl fatty acyl thioesters, with fatty acidchain lengths C(6) to C(12).','(3Z)-dodec-3-enoyl-CoA = (2E)-dodec-2-enoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.9','Prostaglandin-A(1) Delta-isomerase','Prostaglandin A isomerase','NULL','Interconverts prostaglandin A(1) and prostaglandin C(1).','(13E)-(15S)-15-hydroxy-9-oxoprosta-10,13-dienoate = (13E)-(15S)-15-hydroxy-9-oxoprosta-11,13-dienoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.10','5-carboxymethyl-2-hydroxymuconate Delta-isomerase','5-carboxymethyl-2-hydroxymuconic acid isomerase','NULL','NULL','5-carboxymethyl-2-hydroxymuconate = 5-carboxy-2-oxohept-3-enedioate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.11','Isopiperitenone Delta-isomerase','NULL','NULL','Involved in the biosynthesis of menthol and related monoterpenes inpeppermint (Mentha piperita) leaves.','Isopiperitenone = piperitenone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.12','L-dopachrome isomerase','DCF','Zinc.','Stereospecific for L-dopachrome (5,6-dioxo-2,3-5,6-tetrahydroindole-2-carboxylate).Dopachrome methyl ester is a substrate, but dopaminochrome (2,3-dihydroxyindolyl-5,6-quinone) is not (see also EC 4.1.1.84).','L-dopachrome = 5,6-dihydroxyindole-2-carboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.13','Polyenoic fatty acid isomerase','Eicosapentaenoate cis-Delta(5,8,11,14,17)-eicosapentaenoate cis-Delta(5)-trans-Delta(7,9)-cis-Delta(14,17) isomerase','NULL','The enzyme from the red alga Ptilota filicina catalyzes theisomerization of skip dienes (methylene-interrupted double bonds) ina broad range of fatty acids and fatty-acid analogs, such asarachidonate and gamma-linolenate, to yield a conjugated triene.','(5Z,8Z,11Z,14Z,17Z)-icosapentaenoate = (5Z,7E,9E,14Z,17Z)-icosapentaenoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.14','Trans-2-decenoyl-[acyl-carrier-protein] isomerase','Beta-hydroxydecanoyl thioester dehydrase','NULL','While the enzyme from Escherichia coli is highly specific for the10-carbon enoyl-ACP, the enzyme from Streptococcus pneumoniae canalso use the 12-carbon enoyl-ACP as substrate in vitro but not 14- or16-carbon enoyl-ACPs.ACP can be replaced by either CoA or N-acetylcysteamine thioesters.The cis-3-enoyl product is required to form unsaturated fatty acids,such as palmitoleic acid and cis-vaccenic acid, in dissociated (ortype II) fatty-acid biosynthesis.','Trans-dec-2-enoyl-[acyl-carrier-protein] = cis-dec-3-enoyl-[acyl-carrier-protein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.15','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.16','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.17','Trans-2,3-dihydro-3-hydroxyanthranilate isomerase','NULL','NULL','The enzyme is involved in phenazine biosynthesis.','(5S,6S)-6-amino-5-hydroxycyclohexane-1,3-diene-1-carboxyate = (1R,6S)-6-amino-5-oxocyclohex-2-ene-1-carboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.3.18','2-(1,2-epoxy-1,2-dihydrophenyl)acetyl-CoA isomerase','1,2-epoxyphenylacetyl-CoA isomerase','NULL','The enzyme catalyzes the reversible isomerization of 2-(1,2-epoxy-1,2-dihydrophenyl)acetyl-CoA to the unusual unsaturated, oxygen-containing, seven-member heterocyclic enol ether 2-oxepin-2(3H)-ylideneacetyl-CoA, as part of an aerobic phenylacetate degradationpathway.','2-(1,2-epoxy-1,2-dihydrophenyl)acetyl-CoA = 2-oxepin-2(3H)-ylideneacetyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.4.1','Protein disulfide-isomerase','S-S rearrangase','NULL','Needs reducing agents or partly reduced enzyme; the reaction dependson sulfhydryl-disulfide interchange.','Catalyzes the rearrangement of -S-S- bonds in proteins.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.1','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.2','Prostaglandin-D synthase','PGD2 synthase','Glutathione.','Brings about the opening of the epidioxy bridge.','(5Z,13E,15S)-9-alpha,11-alpha-epidioxy-15-hydroxyprosta-5,13-dienoate =(5Z,13E,15S)-9-alpha,15-dihydroxy-11-oxoprosta-5,13-dienoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.3','Prostaglandin-E synthase','Endoperoxide isomerase','Glutathione.','Brings about the opening of the epidioxy bridge.','(5Z,13E)-(15S)-9-alpha,11-alpha-epidioxy-15-hydroxyprosta-5,13-dienoate =(5Z,13E)-(15S)-11-alpha,15-dihydroxy-9-oxoprosta-5,13-dienoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.4','Prostaglandin-I synthase','PGI(2) synthase','Heme-thiolate.','Converts prostaglandin H(2) into prostaglandin I(2) (prostacyclin).','(5Z,13E)-(15S)-9-alpha,11-alpha-epidioxy-15-hydroxyprosta-5,13-dienoate =(5Z,13E)-(15S)-6,9-alpha-epoxy-11-alpha,15-dihydroxyprosta-5,13-dienoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.5','Thromboxane-A synthase','Thromboxane synthase','Heme-thiolate.','Converts prostaglandin H(2) into thromboxane A(2).','(5Z,13E)-(15S)-9-alpha,11-alpha-epidioxy-15-hydroxyprosta-5,13-dienoate =(5Z,13E)-(15S)-9-alpha,11-alpha-epoxy-15-hydroxythromboxa-5,13-dienoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.6','Allene-oxide cyclase','NULL','NULL','Allene oxides formed by the action of EC 4.2.1.92 are converted intocyclopentenone derivatives.','(9Z)-(13S)-12,13-epoxyoctadeca-9,11,15-trienoate = (15Z)-12-oxophyto-10,15-dienoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.7','Styrene-oxide isomerase','SOI','NULL','Highly specific.','Styrene oxide = phenylacetaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.8','Capsanthin/capsorubin synthase','Capsanthin-capsorubin synthase','NULL','Induced during chromoplast differentiation in plants.Isomerization of the epoxide ring of violaxanthin gives thecyclopentyl-ketone of capsorubin or capsanthin.','(1) Violaxanthin = capsorubin.(2) Antheraxanthin = capsanthin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.9','Neoxanthin synthase','NSY','NULL','The opening of the epoxide ring of violaxanthin generates a chiralallene.Neoxanthin is a precursor of the plant hormone abscisic acid and thelast product of carotenoid synthesis in green plants.','Violaxanthin = neoxanthin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.10','Thiazole tautomerase','NULL','NULL','The enzyme catalyzes the irreversible aromatization of the thiazolemoiety of 2-((2R,5Z)-2-carboxy-4-methylthiazol-5(2H)-ylidene)ethylphosphate.','2-((2R,5Z)-2-carboxy-4-methylthiazol-5(2H)-ylidene)ethyl phosphate =2-(2-carboxy-4-methylthiazol-5-yl)ethyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.11','2-keto-myo-inositol isomerase','2KMI isomerase','Divalent cation.','The enzyme, found in the bacterium Bacillus subtilis, is part of themyo-inositol/D-chiro-inositol degradation pathway leading to acetyl-CoA.Formerly EC 5.3.99.n1.','2,4,6/3,5-pentahydroxycyclohexanone = 2D-2,3,5/4,6-pentahydroxycyclohexanone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.3.99.n1','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.1.1','Lysolecithin acylmutase','Lysolecithin migratase','NULL','NULL','2-lysolecithin = 3-lysolecithin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.1.2','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.1.3','2-methylfumaryl-CoA isomerase','Mesaconyl-CoA C(1)-C(4) CoA transferase','NULL','The enzyme, purified from the bacterium Chloroflexus aurantiacus,acts as an intramolecular CoA transferase and does not transfer CoAto free mesaconate.It is part of the 3-hydroxypropanoate cycle for carbon assimilation.','2-methylfumaryl-CoA = 3-methylfumaryl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.1','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.2','Phosphoglucomutase (alpha-D-glucose-1,6-bisphosphate-dependent)','Glucose phosphomutase','NULL','Maximum activity is only obtained in the presence of alpha-D-glucose1,6-bisphosphate.This bisphosphate is an intermediate in the reaction, being formed bytransfer of a phosphate residue from the enzyme to the substrate,but the dissociation of bisphosphate from the enzyme complex is muchslower than the overall isomerization.Also, more slowly, catalyzes the interconversion of 1-phosphate and6-phosphate isomers of many other alpha-D-hexoses, and theinterconversion of alpha-D-ribose 1-phosphate and 5-phosphate.Cf. EC 5.4.2.5.Formerly EC 2.7.5.1.','Alpha-D-glucose 1-phosphate = alpha-D-glucose 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.3','Phosphoacetylglucosamine mutase','Acetylglucosamine phosphomutase','NULL','Activated by N-acetyl-alpha-D-glucosamine 1,6-bisphosphate.Formerly EC 2.7.5.2.','N-acetyl-alpha-D-glucosamine 1-phosphate = N-acetyl-D-glucosamine6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.4','Bisphosphoglycerate mutase','2,3-bisphosphoglycerate mutase','NULL','In the direction shown, the enzyme is phosphorylated by3-phosphoglyceroyl phosphate to give phosphoenzyme and3-phosphoglycerate.The latter is rephosphorylated by the enzyme to yield 2,3-diphosphoglycerate, but this reaction is slowed down by dissociationof 3-phosphoglycerate from the enzyme, which is therefore more activein the presence of added 3-phosphoglycerate.Also catalyzes, slowly, the reactions of EC 3.1.3.13 and EC 5.4.2.12.Formerly EC 2.7.5.4.','3-phospho-D-glyceroyl phosphate = 2,3-bisphospho-D-glycerate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.5','Phosphoglucomutase (glucose-cofactor)','Glucose phosphomutase','NULL','Activated by D-glucose, which probably acts as an acceptor for aphosphate residue from the substrate, thus being itself convertedinto the product.Cf. EC 5.4.2.2.Formerly EC 2.7.5.5.','Alpha-D-glucose 1-phosphate = alpha-D-glucose 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.6','Beta-phosphoglucomutase','NULL','NULL','NULL','Beta-D-glucose 1-phosphate = beta-D-glucose 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.7','Phosphopentomutase','Deoxyribomutase','NULL','Also converts 2-deoxy-alpha-D-ribose 1-phosphate into 2-deoxy-D-ribose 5-phosphate.Alpha-D-ribose 1,5-bisphosphate, 2-deoxy-alpha-D-ribose 1,5-bisphosphate, or alpha-D-glucose 1,6-bisphosphate can act ascofactor.Formerly EC 2.7.5.6.','Alpha-D-ribose 1-phosphate = D-ribose 5-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.8','Phosphomannomutase','Phosphomannose mutase','NULL','Alpha-D-mannose 1,6-bisphosphate or alpha-D-glucose 1,6-bisphosphatecan act as cofactor.Formerly EC 2.7.5.7.','Alpha-D-mannose 1-phosphate = D-mannose 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.9','Phosphoenolpyruvate mutase','PEP mutase','NULL','Involved in the biosynthesis of the C-P bond, although theequilibrium greatly favors phosphoenolpyruvate.','Phosphoenolpyruvate = 3-phosphonopyruvate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.10','Phosphoglucosamine mutase','NULL','NULL','The enzyme is involved in the pathway for bacterial cell-wallpeptidoglycan and lipopolysaccharide biosyntheses, being an essentialstep in the pathway for UDP-N-acetylglucosamine biosynthesis.The enzyme from Escherichia coli is activated by phosphorylation andcan be autophosphorylated in vitro by alpha-D-glucosamine 1,6-bisphosphate, which is an intermediate in the reaction, alpha-D-glucose 1,6-bisphosphate or ATP.It can also catalyze the interconversion of alpha-D-glucose1-phosphate and alpha-D-glucose 6-phosphate, although at a much lowerrate.','Alpha-D-glucosamine 1-phosphate = D-glucosamine 6-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.11','Phosphoglycerate mutase (2,3-diphosphoglycerate-dependent)','2,3-diphosphoglycerate dependent phosphoglycerate mutase','NULL','The enzymes from vertebrates, platyhelminths, mollusks, annelids,crustaceans, insects, algae, fungi, yeast and some bacteria(particularly Gram-negative) require 2,3-bisphospho-D-glycerate as acofactor.The enzyme is activated by 2,3-bisphospho-D-glycerate by transferringa phosphate to histidine (His(10) in man and Escherichia coli, His(8)in Saccharomyces cerevisiae).This phosphate can be transferred to the free OH of phosphoglycerate,followed by transfer of the phosphate already on the phosphoglycerateback to the histidine.Cf. EC 5.4.2.12.The enzyme has no requirement for metal ions.This enzyme also catalyze, slowly, the reactions of EC 5.4.2.4.Formerly EC 2.7.5.3 and EC 5.4.2.1.','2-phospho-D-glycerate = 3-phospho-D-glycerate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.2.12','Phosphoglycerate mutase (2,3-diphosphoglycerate-independent)','2,3-diphosphoglycerate-independent phosphoglycerate mutase','Manganese or cobalt.','The enzymes from higher plants, algae, fungi, nematodes, sponges,coelenterates, myriapods, arachnids, echinoderms, archaea and somebacteria (particularly Gram-positive) have maximum activity in theabsence of 2,3-bisphospho-D-glycerate.Cf. EC 5.4.2.11.The reaction involves a phosphotransferase reaction to serinefollowed by transfer back to the glycerate at the other position.Both metal ions are involved in the reaction.Formerly EC 2.7.5.3 and EC 5.4.2.1.','2-phospho-D-glycerate = 3-phospho-D-glycerate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.1','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.2','Lysine 2,3-aminomutase','NULL','NULL','Activity is stimulated by S-adenosyl-L-methionine and pyridoxalphosphate.','L-lysine = (3S)-3,6-diaminohexanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.3','Beta-lysine 5,6-aminomutase','Beta-lysine mutase','Cobalamin.','NULL','(3S)-3,6-diaminohexanoate = (3S,5S)-3,5-diaminohexanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.4','D-lysine 5,6-aminomutase','Adenosylcobalamin-dependent D-lysine 5,6-aminomutase','Cobalamin.','NULL','D-lysine = 2,5-diaminohexanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.5','D-ornithine 4,5-aminomutase','D-alpha-ornithine 5,4-aminomutase','Cobalamin; Pyridoxal 5-phosphate.','NULL','D-ornithine = (2R,4S)-2,4-diaminopentanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.6','Tyrosine 2,3-aminomutase','Tyrosine alpha,beta-mutase','MIO.','NULL','L-tyrosine = 3-amino-3-(4-hydroxyphenyl)propanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.7','Leucine 2,3-aminomutase','NULL','Cobalamin.','NULL','(2S)-alpha-leucine = (3R)-beta-leucine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.8','Glutamate-1-semialdehyde 2,1-aminomutase','Glutamate-1-semialdehyde aminotransferase','Pyridoxal 5-phosphate.','NULL','(S)-4-amino-5-oxopentanoate = 5-aminolevulinate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.9','Glutamate 2,3-aminomutase','NULL','Iron-sulfur; Pyridoxal 5-phosphate; S-adenosyl-L-methionine.','This enzyme is a member of the AdoMet radical (radical SAM) family.It contains pyridoxal phosphate and a [4Fe-4S] cluster, which iscoordinated by 3 cysteines and binds an exchangeable S-adenosyl-L-methionine molecule.During the reaction cycle, the AdoMet forms a 5-deoxyadenosylradical, which is regenerated at the end of the reaction.','L-glutamate = 3-aminopentanedioate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.10','Phenylalanine aminomutase (L-beta-phenylalanine forming)','NULL','MIO.','Cf. EC 5.4.3.11.','L-phenylalanine = L-beta-phenylalanine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.3.11','Phenylalanine aminomutase (D-beta-phenylalanine forming)','NULL','MIO.','The enzyme from the bacterium Pantoea agglomerans produces D-beta-phenylalanine, an intermediate in the biosynthesis of the polyketidenon-ribosomal antibiotic andrimid.Cf. EC 5.4.3.10.','L-phenylalanine = D-beta-phenylalanine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.4.1','(Hydroxyamino)benzene mutase','HAB mutase','NULL','NULL','(Hydroxyamino)benzene = 2-aminophenol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.4.2','Isochorismate synthase','Isochorismate mutase','Magnesium.','The reaction is reversible.Formerly EC 5.4.99.6.','Chorismate = isochorismate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.4.3','3-(hydroxyamino)phenol mutase','3-hydroxylaminophenol mutase','NULL','NULL','3-hydroxyaminophenol = aminohydroquinone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.4.4','Geraniol isomerase','NULL','NULL','In absence of oxygen the bifunctional linalool dehydratase-isomerasecan catalyze in vitro two reactions, the isomerization of (3S)-linalool to geraniol and the hydration of myrcene to (3S)-linalool,the latter activity being classified as EC 4.2.1.127.','Geraniol = linalool.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.4.5','9,12-octadecadienoate 8-hydroperoxide 8R-isomerase','(8R,9Z,12Z)-8-hydroperoxy-9,12-octadecadienoate mutase ((5S,8R,9Z,12Z)-5,8-dihydroxy-9,12-octadecadienoate-forming)','Heme.','The bifunctional enzyme from Aspergillus nidulans uses different hemedomains to catalyze two separate reactions.Linoleic acid is oxidized within the N-terminal heme peroxidasedomain to (8R,9Z,12Z)-8-hydroperoxyoctadeca-9,12-dienoate (cf.EC 1.13.11.60), which is subsequently isomerized to (5S,8R,9Z,12Z)-5,8-dihydroxyoctadeca-9,12-dienoate within the C-terminal P450 hemethiolate domain.','(8R,9Z,12Z)-8-hydroperoxyoctadeca-9,12-dienoate = (5S,8R,9Z,12Z)-5,8-dihydroxyoctadeca-9,12-dienoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.4.6','9,12-octadecadienoate 8-hydroperoxide 8S-isomerase','(8R,9Z,12Z)-8-hydroperoxy-9,12-octadecadienoate mutase ((7S,8S,9Z,12Z)-5,8-dihydroxy-9,12-octadecadienoate-forming)','Heme.','The bifunctional enzyme from Gaeumannomyces graminis catalyzes theoxidation of linoleic acid to (8R,9Z,12Z)-8-hydroperoxyoctadeca-9,12-dienoate (cf. EC 1.13.11.60), which is then isomerized to(7S,8S,9Z,12Z)-5,8-dihydroxyoctadeca-9,12-dienoate.Formerly EC 1.13.11.44.','(8R,9Z,12Z)-8-hydroperoxyoctadeca-9,12-dienoate = (7S,8S,9Z,12Z)-7,8-dihydroxyoctadeca-9,12-dienoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.1','Methylaspartate mutase','Beta-methylaspartate-glutamate mutase','Cobalamin.','NULL','L-threo-3-methylaspartate = L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.2','Methylmalonyl-CoA mutase','(R)-2-methyl-3-oxopropanoyl-CoA CoA-carbonylmutase','Cobalamin.','NULL','(R)-methylmalonyl-CoA = succinyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.3','2-acetolactate mutase','Acetohydroxy acid isomerase','L-ascorbate.','Also converts 2-aceto-2-hydroxybutanoate to 3-hydroxy-3-methyl-2-oxopentanoate.','2-acetolactate = 3-hydroxy-3-methyl-2-oxobutanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.4','2-methyleneglutarate mutase','Alpha-methyleneglutarate mutase','Cobalamin.','Forms part of the nicotinate-fermentation catabolism pathway inEubacterium barkeri.Other enzymes involved in this pathway are EC 1.17.1.5, EC 1.3.7.1,EC 3.5.2.18, EC 1.1.1.291, EC 5.3.3.6, EC 4.2.1.85 and EC 4.1.3.32.','2-methyleneglutarate = 2-methylene-3-methylsuccinate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.5','Chorismate mutase','Hydroxyphenylpyruvate synthase','NULL','NULL','Chorismate = prephenate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.6','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.7','Lanosterol synthase','2,3-epoxysqualene--lanosterol cyclase','NULL','Formerly EC 1.14.1.3 and EC 1.99.1.13.','(3S)-2,3-epoxy-2,3-dihydrosqualene = lanosterol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.8','Cycloartenol synthase','2,3-epoxysqualene--cycloartenol cyclase','NULL','NULL','(3S)-2,3-epoxy-2,3-dihydrosqualene = cycloartenol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.9','UDP-galactopyranose mutase','NULL','NULL','NULL','UDP-alpha-D-galactopyranose = UDP-alpha-D-galactofuranose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.10','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.11','Isomaltulose synthase','Isomaltulose synthetase','NULL','Simultaneously produces isomaltulose (6-O-alpha-D-glucopyranosyl-D-fructose) and smaller amounts of trehalulose (1-O-alpha-D-glucopyranosyl-beta-D-fructose) from sucrose.Formerly EC 5.4.99.10.','Sucrose = 6-O-alpha-D-glucopyranosyl-D-fructofuranose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.12','tRNA pseudouridine(38-40) synthase','PSUI','NULL','The uridylate residues at positions 38, 39 and 40 of nearly all tRNAsare isomerized to pseudouridine.TruA specifically modifies uridines at positions 38, 39, and/or 40 inthe anticodon stem loop of tRNAs with highly divergent sequences andstructures.','tRNA uridine(38-40) = tRNA pseudouridine(38-40).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.13','Isobutyryl-CoA mutase','NULL','Cobalt.','NULL','2-methylpropanoyl-CoA = butanoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.14','4-carboxymethyl-4-methylbutenolide mutase','4-methyl-2-enelactone methyl-isomerase','NULL','NULL','4-carboxymethyl-4-methylbut-2-en-1,4-olide = 4-carboxymethyl-3-methylbut-2-en-1,4-olide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.15','(1-','(1,4)-alpha-D-glucan 1-alpha-D-glucosylmutase','NULL','Acts on (1-','4-((1-', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.16','Maltose alpha-D-glucosyltransferase','Maltose glucosylmutase','NULL','NULL','Maltose = alpha,alpha-trehalose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.17','Squalene--hopene cyclase','NULL','NULL','The enzyme also produces the cyclization product hopan-22-ol byaddition of water (cf. EC 4.2.1.129).Hopene and hopanol are formed at a constant ratio of 5:1.','Squalene = hop-22(29)-ene.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.18','5-(carboxyamino)imidazole ribonucleotide mutase','N(5)-CAIR mutase','NULL','In eubacteria, fungi and plants, this enzyme, along with EC 6.3.4.18,is required to carry out the single reaction catalyzed by EC 4.1.1.21in vertebrates.In the absence of EC 6.3.2.6, the reaction is reversible.The substrate is readily converted into 5-amino-1-(5-phospho-D-ribosyl)imidazole by non-enzymic decarboxylation.','5-carboxyamino-1-(5-phospho-D-ribosyl)imidazole = 5-amino-1-(5-phospho-D-ribosyl)imidazole-4-carboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.19','16S rRNA pseudouridine(516) synthase','16S RNA pseudouridine 516 synthase','NULL','The enzyme is specific for uridine(516) in 16S rRNA.In vitro, the enzyme does not modify free 16S rRNA.The preferred substrate is a 5-terminal fragment of 16S rRNAcomplexed with 30S ribosomal proteins.','16S rRNA uridine(516) = 16S rRNA pseudouridine(516).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.20','23S rRNA pseudouridine(2457) synthase','NULL','NULL','The enzyme modifies uridine(2457) in a stem of 23S RNA in Escherichiacoli.','23S rRNA uridine(2457) = 23S rRNA pseudouridine(2457).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.21','23S rRNA pseudouridine(2604) synthase','NULL','NULL','The enzyme is not completely specific for uridine(2604) and can, to asmall extent, also react with uridine(2605).','23S rRNA uridine(2604) = 23S rRNA pseudouridine(2604).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.22','23S rRNA pseudouridine(2605) synthase','NULL','NULL','Pseudouridine synthase RluB converts uridine(2605) of 23S rRNA topseudouridine.','23S rRNA uridine(2605) = 23S rRNA pseudouridine(2605).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.23','23S rRNA pseudouridine(1911/1915/1917) synthase','NULL','NULL','Pseudouridine synthase RluD converts uridines at positions 1911,1915, and 1917 of 23S rRNA to pseudouridines.These nucleotides are located in the functionally important helix-loop 69 of 23S rRNA.','23S rRNA uridine(1911)/uridine(1915)/uridine(1917) = 23S rRNApseudouridine(1911)/pseudouridine(1915)/pseudouridine(1917).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.24','23S rRNA pseudouridine(955/2504/2580) synthase','NULL','NULL','The enzyme converts uridines at position 955, 2504 and 2580 of 23SrRNA to pseudouridines.','23S rRNA uridine(955)/uridine(2504)/uridine(2580) = 23S rRNApseudouridine(955)/pseudouridine(2504)/pseudouridine(2580).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.25','tRNA pseudouridine(55) synthase','Psi(55) synthase','NULL','Pseudourine synthase TruB from Escherichia coli specifically modifiesuridine(55) in tRNA molecules.The bifunctional archaeal enzyme also catalyzes the pseudouridylationof uridine(54).It is not known whether the enzyme from Escherichia coli can also acton position 54 in vitro, since this position is occupied in E.colitRNAs by thymine.','tRNA uridine(55) = tRNA pseudouridine(55).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.26','tRNA pseudouridine(65) synthase','NULL','NULL','TruC specifically modifies uridines at positions 65 in tRNA.','tRNA uridine(65) = tRNA pseudouridine(65).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.27','tRNA pseudouridine(13) synthase','tRNA Psi(13) synthase','NULL','Pseudouridine synthase PusS from Escherichia coli specifically actson uridine(13) in tRNA.The Pus7 protein from Saccharomyces cerevisiae is a multisite-multisubstrate pseudouridine synthase that is able to modifyuridine(13) in several yeast tRNAs, uridine(35) in the pre-tRNA(Tyr),uridine(35) in U2 small nuclear RNA, and uridine(50) in 5S rRNA.','tRNA uridine(13) = tRNA pseudouridine(13).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.28','tRNA pseudouridine(32) synthase','Pseudouridine synthase RluA','NULL','The dual-specificity enzyme also catalyzes the formation ofpseudouridine(746) in 23S rRNA.Cf. EC 5.4.99.29.','tRNA uridine(32) = tRNA pseudouridine(32).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.29','23S rRNA pseudouridine(746) synthase','23S RNA Psi(746) synthase','NULL','RluA is the sole protein responsible for the in vivo formation of 23SRNA pseudouridine(746).The dual-specificity enzyme also catalyzes the formation ofuridine(32) in tRNA.Cf. EC 5.4.99.28.','23S rRNA uridine(746) = 23S rRNA pseudouridine(746).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.30','UDP-arabinopyranose mutase','NULL','NULL','The reaction is reversible and at thermodynamic equilibrium thepyranose form is favored over the furanose form (90:10).','UDP-beta-L-arabinofuranose = UDP-beta-L-arabinopyranose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.31','Thalianol synthase','NULL','NULL','NULL','(3S)-2,3-epoxy-2,3-dihydrosqualene = thalianol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.32','Protostadienol synthase','NULL','NULL','(17Z)-protosta-17(20),24-dien-3-beta-ol is a precursor of thesteroidal antibiotic helvolic acid.','(3S)-2,3-epoxy-2,3-dihydrosqualene = (17Z)-protosta-17(20),24-dien-3-beta-ol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.33','Cucurbitadienol synthase','NULL','NULL','NULL','(3S)-2,3-epoxy-2,3-dihydrosqualene = cucurbitadienol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.34','Germanicol synthase','NULL','NULL','The enzyme produces germanicol, beta-amyrin and lupeol in the ratio63:33:4.','(3S)-2,3-epoxy-2,3-dihydrosqualene = germanicol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.35','Taraxerol synthase','NULL','NULL','The enzyme gives taraxerol, beta-amyrin and lupeol in the ratio70:17:13.','(3S)-2,3-epoxy-2,3-dihydrosqualene = taraxerol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.36','Isomultiflorenol synthase','NULL','NULL','NULL','(3S)-2,3-epoxy-2,3-dihydrosqualene = isomultiflorenol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.37','Dammaradiene synthase','NULL','NULL','NULL','Squalene = dammara-20,24-diene.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.38','Camelliol C synthase','NULL','Iron-sulfur; S-adenosyl-L-methionine.','The product is 97% camelliol, 2% achilleol A and 0.2% beta-amyrin.Achilleol is an isomer of camelliol C with a 4-methylenecyclohexanolring system.This enzyme probably evolved from EC 5.4.99.39.','(3S)-2,3-epoxy-2,3-dihydrosqualene = camelliol C.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.39','Beta-amyrin synthase','2,3-oxidosqualene beta-amyrin cyclase','NULL','Some organism possess a monofunctional beta-amyrin synthase, otherhave a multifunctional enzyme that also catalyzes the synthesis ofalpha-amyrin (EC 5.4.99.40) or lupeol (EC 5.4.99.41).Formerly EC 5.4.99.n1.','(3S)-2,3-epoxy-2,3-dihydrosqualene = beta-amyrin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.40','Alpha-amyrin synthase','2,3-oxidosqualene alpha-amyrin cyclase','NULL','A multifunctional enzyme which produces both alpha- and beta-amyrin(see EC 5.4.99.39).','(3S)-2,3-epoxy-2,3-dihydrosqualene = alpha-amyrin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.41','Lupeol synthase','NULL','NULL','Also forms some beta-amyrin.The recombinant enzyme from Arabidopsis thaliana gives a 1:1 mixtureof lupeol and lupan-3-beta,20-diol with small amounts of beta-amyrin,germanicol, taraxasterol and psi-taraxasterol.See EC 4.2.1.128.Formerly EC 5.4.99.n2.','(3S)-2,3-epoxy-2,3-dihydrosqualene = lupeol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.42','tRNA pseudouridine(31) synthase','NULL','NULL','The enzyme specifically acts on uridine(31) in tRNA.','tRNA uridine(31) = tRNA pseudouridine(31).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.43','21S rRNA pseudouridine(2819) synthase','NULL','NULL','The enzyme specifically acts on uridine(2819) in 21S rRNA.','21S rRNA uridine(2819) = 21S rRNA pseudouridine(2819).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.44','Mitochondrial tRNA pseudouridine(27/28) synthase','NULL','NULL','The mitochondrial enzyme Pus2p is specific for position 27 or 28 inmitochondrial tRNA.','Mitochondrial tRNA uridine(27/28) = mitochondrial tRNApseudouridine(27/28).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.45','tRNA pseudouridine(38/39) synthase','Pseudouridine synthase 3','NULL','The enzyme from Saccharomyces cerevisiae is active only towarduridine(38) and uridine(39), and shows no activity with uridine(40)(cf. EC 5.4.99.12).In vitro the enzyme from mouse is active on uridine(39) and veryslightly on uridine(38) (human tRNA(Leu)).','tRNA uridine(38/39) = tRNA pseudouridine(38/39).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.46','Shionone synthase','NULL','NULL','The enzyme gives traces of four other triterpenoids.','(3S)-2,3-epoxy-2,3-dihydrosqualene = shionone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.47','Parkeol synthase','NULL','NULL','The enzyme from rice (Oryza sativa) produces parkeol as a singleproduct.','(3S)-2,3-epoxy-2,3-dihydrosqualene = parkeol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.48','Achilleol B synthase','NULL','NULL','Achilleol B is probably formed by cleavage of the 8-14 and 9-10 bondsof (3S)-2,3-epoxy-2,3-dihydrosqualene as part of the cyclizationreaction, after formation of the oleanane skeleton.','(3S)-2,3-epoxy-2,3-dihydrosqualene = achilleol B.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.49','Glutinol synthase','NULL','NULL','The enzyme from Kalanchoe daigremontiana also gives traces of othertriterpenoids.','(3S)-2,3-epoxy-2,3-dihydrosqualene = glutinol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.50','Friedelin synthase','NULL','NULL','The enzyme from Kalanchoe daigremontiana also gives traces of othertriterpenoids.','(3S)-2,3-epoxy-2,3-dihydrosqualene = friedelin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.51','Baccharis oxide synthase','NULL','NULL','The enzyme from Stevia rebaudiana also gives traces of othertriterpenoids.','(3S)-2,3-epoxy-2,3-dihydrosqualene = baccharis oxide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.52','Alpha-seco-amyrin synthase','NULL','NULL','The enzyme from Arabidopsis thaliana is multifunctional and producesabout equal amounts of alpha- and beta-seco-amyrin.See EC 5.4.99.54.','(3S)-2,3-epoxy-2,3-dihydrosqualene = alpha-seco-amyrin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.53','Marneral synthase','NULL','NULL','Marneral is a triterpenoid formed by Grob fragmentation of the A ringof 2,3-epoxy-2,3-dihydrosqualene during cyclization.','(3S)-2,3-epoxy-2,3-dihydrosqualene = marneral.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.54','Beta-seco-amyrin synthase','NULL','NULL','The enzyme from Arabidopsis thaliana is multifunctional and producesabout equal amounts of alpha- and beta-seco-amyrin.See EC 5.4.99.52.','(3S)-2,3-epoxy-2,3-dihydrosqualene = beta-seco-amyrin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.55','Delta-amyrin synthase','NULL','NULL','The enzyme from tomato (Solanum lycopersicum) gives 48% delta-amyrin,18% alpha-amyrin, 13% beta-amyrin and traces of three or four othertriterpenoid alcohols.See also EC 5.4.99.40 and EC 5.4.99.39.','(3S)-2,3-epoxy-2,3-dihydrosqualene = delta-amyrin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.56','Tirucalladienol synthase','NULL','NULL','The product from Arabidopsis thaliana is 85% tirucalla-7,24-dien-3-beta-ol with trace amounts of other triterpenoids.','(3S)-2,3-epoxy-2,3-dihydrosqualene = tirucalla-7,24-dien-3-beta-ol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.57','Baruol synthase','BARS1','NULL','The enzyme from Arabidopsis thaliana also produces traces of 22 othertriterpenoids.','(3S)-2,3-epoxy-2,3-dihydrosqualene = baruol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.58','Methylornithine synthase','NULL','NULL','The enzyme is a member of the superfamily of S-adenosyl-L-methionine-dependent radical (radical AdoMet) enzymes.Binds a [4Fe-4S] cluster that is coordinated by 3 cysteines and anexchangeable S-adenosyl-L-methionine molecule.The reaction is part of the biosynthesis pathway of pyrrolysine,a naturally occurring amino acid found in some archaealmethyltransferases.','L-lysine = (3R)-3-methyl-D-ornithine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.59','dTDP-fucopyranose mutase','NULL','NULL','The enzyme is involved in the biosynthesis of the Escherichia coliO52 O antigen.','dTDP-alpha-D-fucopyranose = dTDP-alpha-D-fucofuranose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.60','Cobalt-precorrin-8 methylmutase','NULL','NULL','The enzyme catalyzes the the conversion of cobalt-precorrin-8 tocobyrinate by methyl rearrangement, a step in the anaerobic (earlycobalt insertion) pathway of adenosylcobalamin biosynthesis.The equivalent enzyme in the aerobic pathway is EC 5.4.99.61.','Cobalt-precorrin-8 = cobyrinate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.61','Precorrin-8X methylmutase','HBA synthase','NULL','The enzyme catalyzes the the conversion of precorrin-8X tohydrogenobyrinate by methyl rearrangement, a step in the aerobic(late cobalt insertion) pathway of adenosylcobalamin biosynthesis.The equivalent enzyme in the anaerobic pathway is EC 5.4.99.60.Formerly EC 5.4.1.2.','Precorrin-8X = hydrogenobyrinate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.n1','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.4.99.n2','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.1','Muconate cycloisomerase','Cis,cis-muconate lactonizing enzyme I','Manganese.','Also acts, in the reverse reaction, on 3-methyl-cis,cis-hexa-dienedioate and, very slowly, on cis,trans-hexadienedioate.Not identical with EC 5.5.1.7 or EC 5.5.1.11.','2,5-dihydro-5-oxofuran-2-acetate = cis,cis-hexadienedioate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.2','3-carboxy-cis,cis-muconate cycloisomerase','3-carboxymuconate lactonizing enzyme','NULL','NULL','2-carboxy-2,5-dihydro-5-oxofuran-2-acetate = cis,cis-butadiene-1,2,4-tricarboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.3','Tetrahydroxypteridine cycloisomerase','NULL','NULL','NULL','Tetrahydroxypteridine = xanthine-8-carboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.4','Inositol-3-phosphate synthase','1L-myo-inositol-1-phosphate lyase (isomerizing)','NAD(+).','The NAD(+) dehydrogenates the -CHOH- group to -CO- at C-5 of theglucose 6-phosphate, making C-6 into an active methylene, able tocondense with the -CHO at C-1; finally, the enzyme-bound NADHreconverts C-5 into the -CHOH- form.','D-glucose 6-phosphate = 1D-myo-inositol 3-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.5','Carboxy-cis,cis-muconate cyclase','3-carboxy-cis,cis-muconate lactonizing enzyme','NULL','NULL','3-carboxy-2,5-dihydro-5-oxofuran-2-acetate = 3-carboxy-cis,cis-muconate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.6','Chalcone isomerase','Chalcone--flavonone isomerase','NULL','NULL','A chalcone = a flavanone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.7','Chloromuconate cycloisomerase','Muconate cycloisomerase II','Manganese.','Spontaneous elimination of HCl produces cis-4-carboxymethylenebut-2-en-4-olide.Also acts in reverse direction on 2-chloro-cis,cis-muconate.Not identical with EC 5.5.1.1 or EC 5.5.1.11.','2-chloro-2,5-dihydro-5-oxofuran-2-acetate = 3-chloro-cis,cis-muconate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.8','(+)-bornyl diphosphate synthase','Bornyl pyrophosphate synthase','NULL','The enzyme from Salvia officinalis (sage) can also use (3R)-linalyldiphosphate or more slowly neryl diphosphate in vitro.The reaction proceeds via isomeration of geranyl diphosphate to (3R)-linalyl diphosphate.The oxygen and phosphorus originally linked to C-1 of geranyldiphosphate end up linked to C-2 of (+)-bornyl diphosphate.cf. EC 5.5.1.22.','Geranyl diphosphate = (+)-bornyl diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.9','Cycloeucalenol cycloisomerase','Cycloeucalenol--obtusifoliol isomerase','NULL','Opens the cyclopropane ring of a number of related 4-alpha-methyl-9-beta-19-cyclosterols, but not those with a 4-beta-methyl group, withformation of an 8(9) double bond.Involved in the synthesis of plant sterols.','Cycloeucalenol = obtusifoliol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.10','Alpha-pinene-oxide decyclase','Alpha-pinene oxide lyase','NULL','Both rings of pinene are cleaved in the reaction.','Alpha-pinene oxide = (Z)-2-methyl-5-isopropylhexa-2,5-dienal.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.11','Dichloromuconate cycloisomerase','NULL','Manganese.','The product of cyclisomerization of dichloro-cis,cis-muconatespontaneously eliminates chloride to produce cis-4-carboxymethylene-3-chlorobut-2-en-4-olide.Also acts, in the reverse direction, on cis,cis-muconate and itsmonochloro-derivatives, but with lower affinity.Not identical with EC 5.5.1.1 or EC 5.5.1.7.','2,4-dichloro-2,5-dihydro-5-oxofuran-2-acetate = 2,4-dichloro-cis,cis-muconate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.12','Copalyl diphosphate synthase','NULL','NULL','In some plants, such as Salvia miltiorrhiza, this enzyme ismonofunctional.In other plants this activity is often a part of a bifunctionalenzyme.For example, in Selaginella moellendorffii this activity is catalyzedby a bifunctional enzyme that also catalyzes EC 4.2.3.131, while inthe tree Abies grandis (grand fir) it is catalyzed by a bifunctionalenzyme that also catalyzes EC 4.2.3.18.','Geranylgeranyl diphosphate = (+)-copalyl diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.13','Ent-copalyl diphosphate synthase','Ent-CDP synthase','NULL','Part of a bifunctional enzyme involved in the biosynthesis ofkaurene.See also EC 4.2.3.19.','Geranylgeranyl diphosphate = ent-copalyl diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.14','Syn-copalyl-diphosphate synthase','Syn-copalyl diphosphate synthase','Magnesium.','This class II terpene synthase produces syn-copalyl diphosphate,a precursor of several rice phytoalexins, including oryzalexin S andmomilactones A and B.Phytoalexins are diterpenoid secondary metabolites that are involvedin the defense mechanism of the plant, and are produced in responseto pathogen attack through the perception of elicitor signalmolecules such as chitin oligosaccharide, or after exposure to UVirradiation.Constitutively expressed in the roots of plants where one of itsproducts, momilactone B, acts as an allelochemical (a moleculereleased into the environment to suppress the growth of neighboringplants).In other tissues the enzyme is up-regulated by conditions thatstimulate the biosynthesis of phytoalexins.','Geranylgeranyl diphosphate = 9-alpha-copalyl diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.15','Terpentedienyl-diphosphate synthase','Clerodadienyl diphosphate synthase','Magnesium.','Contains a DXDD motif, which is a characteristic of diterpene cylaseswhose reactions are initiated by protonation at the 14,15-double bondof geranylgeranyl diphosphate (GGDP).The triggering proton is lost at the end of the cyclization reaction.The product of the reaction, terpentedienyl diphosphate, is thesubstrate for EC 4.2.3.36 and is a precursor of the diterpenoidantibiotic terpentecin.','Geranylgeranyl diphosphate = terpentedienyl diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.16','Halimadienyl-diphosphate synthase','Tuberculosinol diphosphate synthase','Magnesium.','Found in pathogenic prokaryotes such as Mycobacterium tuberculosisbut not in non-pathogens such as Mycobacterium smegmatis so may playa role in pathogenicity.The product of the reaction is subsequently dephosphorylated yieldingtuberculosinol (halima-5,13-dien-15-ol).','Geranylgeranyl diphosphate = tuberculosinyl diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.17','(S)-beta-macrocarpene synthase','NULL','NULL','NULL','(S)-beta-bisabolene = (S)-beta-macrocarpene.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.18','Lycopene epsilon-cyclase','NULL','NULL','The carotenoid lycopene has the psi-end group at both ends.When acting on one end, this enzyme forms delta-carotene.When acting on both ends, it forms epsilon-carotene.','Carotenoid psi-end group = carotenoid epsilon-end group.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.19','Lycopene beta-cyclase','NULL','FAD.','Lycopene has a psi-end group at both ends.When acting on one end, this enzyme forms gamma-carotene.When acting on both ends it forms beta-carotene.It also acts on neurosporene to give beta-zeacarotene.Formerly EC 5.5.1.n2.','Carotenoid psi-end group = carotenoid beta-end group.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.20','Prosolanapyrone-III cycloisomerase','Prosolanapyrone II oxidase/prosolanapyrone III cyclosiomerase','NULL','The enzyme is involved in the biosynthesis of the phytotoxinsolanapyrone in some fungi.The bifunctional enzyme catalyzes the oxidation of prosolanapyrone IIand the subsequent Diels Alder cycloisomerization of the productprosolanapyrone III to (-)-solanapyrone A (cf. EC 1.1.3.42).','Prosolanapyrone III = (-)-solanapyrone A.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.21','Transferred entry: 4','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.22','(-)-bornyl diphosphate synthase','(-)-bornyl pyrophosphate cyclase','NULL','The enzyme from Tanacetum vulgare (tansey) can also use (3S)-linalyldiphosphate or more slowly neryl diphosphate in vitro.The reaction proceeds via isomeration of geranyl diphosphate to (3S)-linalyl diphosphate.The oxygen and phosphorus originally linked to C-1 of geranyldiphosphate end up linked to C-2 of (-)-bornyl diphosphate.Cf. EC 5.5.1.8.','Geranyl diphosphate = (-)-bornyl diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.23','Aklanonic acid methyl ester cyclase','Methyl aklanonate cyclase','NULL','The enzyme is involved in the biosynthesis of aklaviketone,an intermediate in the biosynthetic pathways leading to formation ofseveral anthracycline antibiotics, including aclacinomycin,daunorubicin and doxorubicin.','Methyl aklanonate = aklaviketone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.24','Tocopherol cyclase','NULL','NULL','The enzyme has been described from plants and cyanobacteria.It has similar activity with all four listed benzoquinol substrates.Involved in the biosynthesis of vitamin E tocopherols andtocotrienols.','(1) Delta-tocopherol = 2-methyl-6-phytylbenzene-1,4-diol.(2) Gamma-tocopherol = 2,3-dimethyl-6-phytylbenzene-1,4-diol.(3) Delta-tocotrienol = 2-methyl-6-geranylgeranylbenzene-1,4-diol.(4) Gamma-tocotrienol = 2,3-dimethyl-6-geranylgeranylbenzene-1,4-diol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.n1','D-ribose pyranase','NULL','NULL','The enzyme also catalyzes the conversion between beta-allofuranoseand beta-allopyranose.','Beta-D-ribopyranose = beta-D-ribofuranose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.5.1.n2','Transferred entry: 5','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.99.1.1','Thiocyanate isomerase','Isothiocyanate isomerase','NULL','NULL','Benzyl isothiocyanate = benzyl thiocyanate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.99.1.2','DNA topoisomerase','DNA topoisomerase I','NULL','Brings about the conversion of one topological isomer of DNA intoanother, e.g. the relaxation of superhelical turns in DNA,the interconversion of simple and knotted rings of single-strandedDNA, and the intertwisting of single-stranded rings of complementarysequences (cf. EC 5.99.1.3).','ATP-independent breakage of single-stranded DNA, followed by passage andrejoining.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.99.1.3','DNA topoisomerase (ATP-hydrolyzing)','DNA gyrase','NULL','Can introduce negative superhelical turns into double-strandedcircular DNA.One unit has nicking-closing activity, and another catalyzes super-twisting and hydrolysis of ATP (cf. EC 5.99.1.2).','ATP-dependent breakage, passage and rejoining of double-stranded DNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 5.99.1.4','2-hydroxychromene-2-carboxylate isomerase','2HC2CA isomerase','NULL','This enzyme is involved in naphthalene degradation.','2-hydroxy-2H-chromene-2-carboxylate = (3E)-4-(2-hydroxyphenyl)-2-oxobut-3-enoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.1','Tyrosine--tRNA ligase','L-tyrosine-tRNA(Tyr) ligase (AMP-forming)','NULL','NULL','ATP + L-tyrosine + tRNA(Tyr) = AMP + diphosphate + L-tyrosyl-tRNA(Tyr).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.2','Tryptophan--tRNA ligase','L-tryptophan-tRNA(Trp) ligase (AMP-forming)','NULL','NULL','ATP + L-tryptophan + tRNA(Trp) = AMP + diphosphate + L-tryptophyl-tRNA(Trp).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.3','Threonine--tRNA ligase','Threonine translase','NULL','NULL','ATP + L-threonine + tRNA(Thr) = AMP + diphosphate + L-threonyl-tRNA(Thr).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.4','Leucine--tRNA ligase','Leucine translase','NULL','NULL','ATP + L-leucine + tRNA(Leu) = AMP + diphosphate + L-leucyl-tRNA(Leu).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.5','Isoleucine--tRNA ligase','Isoleucine translase','NULL','NULL','ATP + L-isoleucine + tRNA(Ile) = AMP + diphosphate + L-isoleucyl-tRNA(Ile).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.6','Lysine--tRNA ligase','Lysine translase','NULL','NULL','ATP + L-lysine + tRNA(Lys) = AMP + diphosphate + L-lysyl-tRNA(Lys).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.7','Alanine--tRNA ligase','Alanine translase','NULL','NULL','ATP + L-alanine + tRNA(Ala) = AMP + diphosphate + L-alanyl-tRNA(Ala).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.8','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.9','Valine--tRNA ligase','Valine translase','NULL','NULL','ATP + L-valine + tRNA(Val) = AMP + diphosphate + L-valyl-tRNA(Val).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.10','Methionine--tRNA ligase','Methionine translase','NULL','In those organisms producing N-formylmethionyl-tRNA(fMet) fortranslation initiation, this enzyme also recognizes the initiatortRNA(fMet) and catalyzes the formation of L-methionyl-tRNA(fMet),the substrate for EC 2.1.2.9.','ATP + L-methionine + tRNA(Met) = AMP + diphosphate + L-methionyl-tRNA(Met).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.11','Serine--tRNA ligase','Serine translase','NULL','This enzyme also recognizes tRNA(Sec), the special tRNA forselenocysteine, and catalyzes the formation of L-seryl-tRNA(Sec),the substrate for EC 2.9.1.1.','ATP + L-serine + tRNA(Ser) = AMP + diphosphate + L-seryl-tRNA(Ser).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.12','Aspartate--tRNA ligase','Aspartic acid translase','NULL','NULL','ATP + L-aspartate + tRNA(Asp) = AMP + diphosphate + L-aspartyl-tRNA(Asp).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.13','D-alanine--poly(phosphoribitol) ligase','D-alanine-activating enzyme','NULL','A thioester bond is formed transiently between D-alanine and thesulfhydryl group of the 4-phosphopantetheine prosthetic group ofD-alanyl carrier protein during the activation of the alanine.Involved in the synthesis of teichoic acids.','ATP + D-alanine + poly(ribitol phosphate) = AMP + diphosphate + O-D-alanyl-poly(ribitol phosphate).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.14','Glycine--tRNA ligase','Glycyl translase','NULL','NULL','ATP + glycine + tRNA(Gly) = AMP + diphosphate + glycyl-tRNA(Gly).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.15','Proline--tRNA ligase','Proline translase','NULL','NULL','ATP + L-proline + tRNA(Pro) = AMP + diphosphate + L-prolyl-tRNA(Pro).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.16','Cysteine--tRNA ligase','Cysteine translase','NULL','NULL','ATP + L-cysteine + tRNA(Cys) = AMP + diphosphate + L-cysteinyl-tRNA(Cys).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.17','Glutamate--tRNA ligase','Glutamic acid translase','NULL','NULL','ATP + L-glutamate + tRNA(Glu) = AMP + diphosphate + L-glutamyl-tRNA(Glu).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.18','Glutamine--tRNA ligase','Glutamine translase','NULL','NULL','ATP + L-glutamine + tRNA(Gln) = AMP + diphosphate + L-glutaminyl-tRNA(Gln).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.19','Arginine--tRNA ligase','Arginine translase','NULL','NULL','ATP + L-arginine + tRNA(Arg) = AMP + diphosphate + L-arginyl-tRNA(Arg).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.20','Phenylalanine--tRNA ligase','Phenylalanine translase','NULL','NULL','ATP + L-phenylalanine + tRNA(Phe) = AMP + diphosphate + L-phenylalanyl-tRNA(Phe).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.21','Histidine--tRNA ligase','Histidine translase','NULL','NULL','ATP + L-histidine + tRNA(His) = AMP + diphosphate + L-histidyl-tRNA(His).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.22','Asparagine--tRNA ligase','Asparagine translase','NULL','NULL','ATP + L-asparagine + tRNA(Asn) = AMP + diphosphate + L-asparaginyl-tRNA(Asn).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.23','Aspartate--tRNA(Asn) ligase','Nondiscriminating aspartyl-tRNA synthetase','NULL','When this enzyme acts on tRNA(Asp), it catalyzes the same reaction asEC 6.1.1.12.It has, however, diminished discrimination, so that it can also formaspartyl-tRNA(Asn).This relaxation of specificity has been found to result from theabsence of a loop in the tRNA that specifically recognizes the thirdposition of the anticodon.This accounts for the ability of this enzyme in, for example, Thermusthermophilus, to recognize both tRNA(Asp) (GUC anticodon) andtRNA(Asn) (GUU anticodon).The aspartate-tRNA(Asn) is not used in protein synthesis until it isconverted by EC 6.3.5.6 into asparaginyl-tRNA(Asn).','ATP + L-aspartate + tRNA(Asx) = AMP + diphosphate + L-aspartyl-tRNA(Asx).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.24','Glutamate--tRNA(Gln) ligase','Nondiscriminating glutamyl-tRNA synthetase','NULL','When this enzyme acts on tRNA(Glu), it catalyzes the same reaction asEC 6.1.1.17.It has, however, diminished discrimination, so that it can also formglutamate-tRNA(Gln).This relaxation of specificity has been found to result from theabsence of a loop in the tRNA that specifically recognizes the thirdposition of the anticodon.This accounts for the ability of this enzyme in, for example,Bacillus subtilis, to recognize both tRNA(1)(Gln) (UUG anticodon) andtRNA(Glu) (UUC anticodon) but not tRNA(2)(Gln) (CUG anticodon).The ability of this enzyme to recognize both tRNA(Glu) and one of thetRNA(Gln) isoacceptors derives from their sharing a major identityelement, a hypermodified derivative of U34 (5-methylaminomethyl-2-thiouridine).The glutamyl-tRNA(Gln) is not used in protein synthesis until it isconverted by EC 6.3.5.7 into glutaminyl-tRNA(Gln).','ATP + L-glutamate + tRNA(Glx) = AMP + diphosphate + L-glutamyl-tRNA(Glx).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.25','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.26','Pyrrolysine--tRNA(Pyl) ligase','PylRS','NULL','In organisms such as Methanosarcina barkeri that incorporate themodified amino acid pyrrolysine (Pyl) into certain methylaminemethyltransferases, an unusual tRNA(Pyl), with a CUA anticodon,can be charged directly with pyrrolysine by this class II aminoacyl--tRNA ligase.Specific for pyrrolysine as substrate as it cannot be replaced bylysine or any of the other natural amino acids.','ATP + L-pyrrolysine + tRNA(Pyl) = AMP + diphosphate + L-pyrrolysyl-tRNA(Pyl).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.27','O-phosphoserine--tRNA ligase','O-phosphoseryl-tRNA ligase','NULL','In organisms like Archaeoglobus fulgidus lacking EC 6.1.1.16 for thedirect Cys-tRNA(Cys) formation, Cys-tRNA(Cys) is produced by anindirect pathway, in which EC 6.1.1.27 ligates O-phosphoserine totRNA(Cys), and EC 2.5.1.73 converts the produced O-phospho-L-seryl-tRNA(Cys) to Cys-tRNA(Cys).The SepRS/SepCysS pathway is the sole route for cysteine biosynthesisin the organism.Methanosarcina mazei can use both pathways, the direct route usingEC 6.1.1.16 and the indirect pathway with EC 6.1.1.27 andEC 2.5.1.73.Formerly EC 6.1.1.n2.','ATP + O-phospho-L-serine + tRNA(Cys) = AMP + diphosphate + O-phospho-L-seryl-tRNA(Cys).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.n1','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.1.n2','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.1.2.1','D-alanine--(R)-lactate ligase','NULL','NULL','The product of this enzyme, the depsipeptide D-alanyl-(R)-lactate,can be incorporated into the peptidoglycan pentapeptide instead ofthe usual D-alanyl-D-alanine dipeptide, which is formed byEC 6.3.2.4.The resulting peptidoglycan does not bind the glycopeptideantibiotics vancomycin and teicoplanin, conferring resistance on thebacteria.','D-alanine + (R)-lactate + ATP = D-alanyl-(R)-lactate + ADP + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.1','Acetate--CoA ligase','Acetate thiokinase','NULL','Also acts on propanoate and propenoate.','ATP + acetate + CoA = AMP + diphosphate + acetyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.2','Butyrate--CoA ligase','Acyl-activating enzyme','NULL','Acts on acids from C(4) to C(11) and on the corresponding 3-hydroxy-and 2,3- or 3,4-unsaturated acids.','ATP + a carboxylate + CoA = AMP + diphosphate + an acyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.3','Long-chain-fatty-acid--CoA ligase','Acyl-activating enzyme','NULL','Acts on a wide range of long-chain saturated and unsaturated fattyacids, but the enzymes from different tissues show some variation inspecificity.The liver enzyme acts on acids from C(6) to C(20); that from brainshows high activity up to C(24).','ATP + a long-chain fatty acid + CoA = AMP + diphosphate + an acyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.4','Succinate--CoA ligase (GDP-forming)','Succinyl-CoA synthetase (GDP-forming)','NULL','Itaconate can act instead of succinate and ITP instead of GTP.','GTP + succinate + CoA = GDP + phosphate + succinyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.5','Succinate--CoA ligase (ADP-forming)','Succinate thiokinase','NULL','NULL','ATP + succinate + CoA = ADP + phosphate + succinyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.6','Glutarate--CoA ligase','Glutaryl-CoA synthetase','NULL','GTP or ITP can act instead of ATP.','ATP + glutarate + CoA = ADP + phosphate + glutaryl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.7','Cholate--CoA ligase','3-alpha,7-alpha,12-alpha-trihydroxy-5-beta-cholestanate--CoA ligase','Magnesium.','This membrane-bound enzyme catalyzes the first step in theconjugation of bile acids with amino acids, converting bile acidsinto their acyl-CoA thioesters.The second step involves EC 2.3.1.65 and converts the acyl-CoAthioester into the corresponding N-acyl amidate by conjugation withglycine or taurine.Chenodeoxycholate, deoxycholate, lithocholate andtrihydroxycoprostanoate can also act as substrates.Formerly EC 6.2.1.29.','(1) ATP + cholate + CoA = AMP + diphosphate + choloyl-CoA.(2) ATP + (25R)-3-alpha,7-alpha,12-alpha-trihydroxy-5-beta-cholestan-26-oate + CoA = AMP + diphosphate + (25R)-3-alpha,7-alpha,12-alpha-trihydroxy-5-beta-cholestanoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.8','Oxalate--CoA ligase','Oxalyl-CoA synthetase','NULL','NULL','ATP + oxalate + CoA = AMP + diphosphate + oxalyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.9','Malate--CoA ligase','Malate thiokinase','NULL','NULL','ATP + malate + CoA = ADP + phosphate + malyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.10','Acid--CoA ligase (GDP-forming)','Acyl-CoA synthetase (GDP-forming)','NULL','NULL','GTP + a carboxylate + CoA = GDP + phosphate + acyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.11','Biotin--CoA ligase','Biotinyl-CoA synthetase','NULL','NULL','ATP + biotin + CoA = AMP + diphosphate + biotinyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.12','4-coumarate--CoA ligase','4-coumaroyl-CoA synthase','NULL','NULL','ATP + 4-coumarate + CoA = AMP + diphosphate + 4-coumaroyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.13','Acetate--CoA ligase (ADP-forming)','Acetate thiokinase','NULL','Also acts on propanoate and, very slowly, on butanoate.','ATP + acetate + CoA = ADP + phosphate + acetyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.14','6-carboxyhexanoate--CoA ligase','6-carboxyhexanoyl-CoA synthetase','NULL','NULL','ATP + 6-carboxyhexanoate + CoA = AMP + diphosphate + 6-carboxyhexanoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.15','Arachidonate--CoA ligase','Arachidonoyl-CoA synthetase','NULL','8,11,14-icosatrienoate, but not the other long-chain fatty acids,can act in place of arachidonate.Not identical with EC 6.2.1.3.','ATP + arachidonate + CoA = AMP + diphosphate + arachidonoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.16','Acetoacetate--CoA ligase','Acetoacetyl-CoA synthetase','NULL','Also acts, more slowly, on L-3-hydroxybutanoate.','ATP + acetoacetate + CoA = AMP + diphosphate + acetoacetyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.17','Propionate--CoA ligase','Propionyl-CoA synthetase','NULL','Propenoate can act instead of propanoate.Not identical with EC 6.2.1.1 or EC 6.2.1.2.','ATP + propanoate + CoA = AMP + diphosphate + propanoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.18','Citrate--CoA ligase','NULL','NULL','Component of EC 4.1.3.8.','ATP + citrate + CoA = ADP + phosphate + (3S)-citryl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.19','Long-chain-fatty-acid--luciferin-component ligase','Acyl-protein synthetase','NULL','Together with EC 1.2.1.50 forms a fatty acid reductase system whichproduces the substrate of EC 1.14.14.3, thus being a component of thebacterial luciferase system.','ATP + a long-chain fatty acid + protein = AMP + diphosphate + an acyl-protein thioester.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.20','Long-chain-fatty-acid--[acyl-carrier-protein] ligase','Acyl-[acyl-carrier-protein] synthetase','NULL','Not identical with EC 6.2.1.3.','ATP + an acid + [acyl-carrier-protein] = AMP + diphosphate + acyl-[acyl-carrier-protein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.21','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.22','[Citrate (pro-3S)-lyase] ligase','Citrate lyase ligase','NULL','Converts the inactive thiol form of EC 4.1.3.6 into the active form.','ATP + acetate + [citrate (pro-3S)-lyase](thiol form) = AMP + diphosphate+ [citrate (pro-3S)-lyase](acetyl form).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.23','Dicarboxylate--CoA ligase','NULL','NULL','Acts on dicarboxylic acids of chain length C(5) to C(16); the bestsubstrate is dodecanedioic acid.','ATP + an alpha-omega-dicarboxylate + CoA = AMP + diphosphate + an omega-carboxyacyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.24','Phytanate--CoA ligase','NULL','NULL','Not identical with EC 6.2.1.20.','ATP + phytanate + CoA = AMP + diphosphate + phytanoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.25','Benzoate--CoA ligase','NULL','NULL','Also acts on 2-, 3- and 4-fluorobenzoate, but only very slowly on thecorresponding chlorobenzoates.','ATP + benzoate + CoA = AMP + diphosphate + benzoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.26','o-succinylbenzoate--CoA ligase','o-succinylbenzoyl-CoA synthetase','NULL','NULL','ATP + 2-succinylbenzoate + CoA = AMP + diphosphate + 2-succinylbenzoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.27','4-hydroxybenzoate--CoA ligase','4-hydroxybenzoate-CoA synthetase','NULL','NULL','ATP + 4-hydroxybenzoate + CoA = AMP + diphosphate + 4-hydroxybenzoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.28','3-alpha,7-alpha-dihydroxy-5-beta-cholestanate--CoA ligase','DHCA-CoA ligase','NULL','NULL','ATP + 3-alpha,7-alpha-dihydroxy-5-beta-cholestanate + CoA = AMP +diphosphate + 3-alpha,7-alpha-dihydroxy-5-beta-cholestanoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.29','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.30','Phenylacetate--CoA ligase','NULL','NULL','Also acts, more slowly, on acetate, propanoate and butanoate, but noton hydroxy derivatives of phenylacetate and related compounds.Formerly EC 6.2.1.21.','ATP + phenylacetate + CoA = AMP + diphosphate + phenylacetyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.31','2-furoate--CoA ligase','NULL','NULL','NULL','ATP + 2-furoate + CoA = AMP + diphosphate + 2-furoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.32','Anthranilate--CoA ligase','NULL','NULL','NULL','ATP + anthranilate + CoA = AMP + diphosphate + anthraniloyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.33','4-chlorobenzoate--CoA ligase','NULL','Magnesium.','Part of the bacterial 2,4-dichlorobenzoate degradation pathway.','4-chlorobenzoate + CoA + ATP = 4-chlorobenzoyl-CoA + AMP + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.34','Trans-feruloyl-CoA synthase','Trans-feruloyl-CoA synthetase','Magnesium.','It has not yet been established whether AMP + diphosphate or ADP +phosphate are formed in this reaction.','Ferulic acid + CoA + ATP = feruloyl-CoA + products of ATP breakdown.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.35','ACP-SH:acetate ligase','[Acyl-carrier protein]:acetate ligase','NULL','This enzyme, from the anaerobic bacterium Malonomonas rubra, is acomponent of the multienzyme complex EC 4.1.1.89.The enzyme uses the energy from hydrolysis of ATP to convert thethiol group of the acyl-carrier-protein-bound 2-(5-phosphoribosyl)-3-dephospho-CoA prosthetic group into its acetyl thioester.','ATP + acetate + an [acyl-carrier protein] = AMP + diphosphate +an acetyl-[acyl-carrier protein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.36','3-hydroxypropionyl-CoA synthase','3-hydroxypropionate--CoA ligase','NULL','Catalyzes a step in the 3-hydroxypropanoate/4-hydroxybutyrate cycle,an autotrophic CO(2) fixation pathway found in some thermoacidophilicarchaea.The enzymes from Metallosphaera sedula and Sulfolobus tokodaii canalso use propanoate, acrylate, acetate, and butanoate as substrates,and are thus different from EC 6.2.1.17, which does not acceptacetate or butanoate.','3-hydroxypropanoate + ATP + CoA = 3-hydroxypropionyl-CoA + AMP +diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.37','3-hydroxybenzoate--CoA ligase','3-hydroxybenzoate--coenzyme A ligase (AMP-forming)','NULL','The enzyme works equally well with 4-hydroxybenzoate but shows lowactivity toward benzoate, 4-aminobenzoate, 3-aminobenzoate,3-fluorobenzoate, 4-fluorobenzoate, 3-chlorobenzoate,and 4-chlorobenzoate.There is no activity with 3,4-dihydroxybenzoate, 2,3-dihydroxybenzoate, and 2-hydroxybenzoate as substrates.Formerly EC 6.2.1.n1.','ATP + 3-hydroxybenzoate + CoA = AMP + diphosphate + 3-hydroxybenzoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.38','(2,2,3-trimethyl-5-oxocyclopent-3-enyl)acetyl-CoA synthase','2-oxo-Delta(3)-4,5,5-trimethylcyclopentenylacetyl-CoA synthetase','NULL','Isolated from Pseudomonas putida.Forms part of the pathway of camphor catabolism.','((1R)-2,2,3-trimethyl-5-oxocyclopent-3-enyl)acetate + ATP + CoA = AMP +diphosphate + ((1R)-2,2,3-trimethyl-5-oxocyclopent-3-enyl)acetyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.39','[Butirosin acyl-carrier protein]--L-glutamate ligase','[BtrI acyl-carrier protein]--L-glutamate ligase','Manganese or magnesium.','Catalyzes two steps in the biosynthesis of the side chain of theaminoglycoside antibiotics of the butirosin family.The enzyme adds one molecule of L-glutamate to a dedicated acyl-carrier protein, and following decarboxylation of the product byEC 4.1.1.95, adds a second L-glutamate molecule.','(1) ATP + L-glutamate + [BtrI acyl-carrier protein] = ADP + phosphate +L-glutamyl-[BtrI acyl-carrier protein].(2) ATP + L-glutamate + 4-amino butanoyl-[BtrI acyl-carrier protein] =ADP + phosphate + 4-(L-gamma-glutamylamino)butanoyl-[BtrI acyl-carrierprotein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.40','4-hydroxybutyrate--CoA ligase','4-hydroxybutyrate-CoA synthetase','NULL','Isolated from the archaeon Metallosphaera sedula.Involved in the 3-hydroxypropionate/4-hydroxybutyrate cycle.','ATP + 4-hydroxybutanoate + CoA = AMP + diphosphate + 4-hydroxybutanoyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.n1','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.n2','Amino acid--[acyl-carrier-protein] ligase','NULL','NULL','Catalyzes the ATP-dependent activation of L-alanine, and to a lesserextent of L-glycine and L-serine, and their transfer to thephosphopantetheine prosthetic group covalently attached to a vicinalcarrier protein.May participate in nonribosomal peptide synthesis or relatedprocesses.L-proline and L-glutamate are very poor substrates whereas D-aminoacids are not substrates for ATP-dependent activation.Does not display tRNA aminoacylation activity.','ATP + an amino acid + [acyl-carrier-protein] = AMP + diphosphate +an aminoacyl-[acyl-carrier-protein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.2.1.n3','Malonate--CoA ligase','NULL','NULL','NULL','ATP + malonate + CoA = AMP + diphosphate + malonyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.1','Aspartate--ammonia ligase','Asparagine synthetase','NULL','NULL','ATP + L-aspartate + NH(3) = AMP + diphosphate + L-asparagine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.2','Glutamate--ammonia ligase','Glutamine synthetase','NULL','Also acts, more slowly, on 4-methylene-L-glutamate (cf. EC 6.3.1.7).','ATP + L-glutamate + NH(3) = ADP + phosphate + L-glutamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.3','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.4','Aspartate--ammonia ligase (ADP-forming)','Asparagine synthetase (ADP-forming)','NULL','NULL','ATP + L-aspartate + NH(3) = ADP + phosphate + L-asparagine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.5','NAD(+) synthase','NAD(+) synthetase','NULL','L-glutamine also acts, more slowly, as amido-donor (cf. EC 6.3.5.1).','ATP + deamido-NAD(+) + NH(3) = AMP + diphosphate + NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.6','Glutamate--ethylamine ligase','N(5)-ethyl-L-glutamine synthetase','NULL','NULL','ATP + L-glutamate + ethylamine = ADP + phosphate + N(5)-ethyl-L-glutamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.7','4-methyleneglutamate--ammonia ligase','4-methyleneglutamine synthetase','NULL','Glutamine can act instead of NH(3), more slowly.','ATP + 4-methylene-L-glutamate + NH(3) = AMP + diphosphate + 4-methylene-L-glutamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.8','Glutathionylspermidine synthase','Gamma-L-glutamyl-L-cysteinyl-glycine:spermidine ligase (ADP-forming)','Magnesium.','Involved in the synthesis of trypanothione in trypanosomatids.The enzyme from Escherichia coli is bifunctional and also catalyzesthe EC 3.5.1.78 reaction, resulting in a net hydrolysis of ATP.','Glutathione + spermidine + ATP = glutathionylspermidine + ADP +phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.9','Trypanothione synthase','TSR synthetase','Magnesium.','Involved in the synthesis of trypanothione in trypanosomatids.','Glutathione + glutathionylspermidine + ATP = N(1),N(8)-bis-(glutathionyl)spermidine + ADP + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.10','Adenosylcobinamide-phosphate synthase','NULL','NULL','This enzyme forms part of the anaerobic cobalamin biosynthesispathway.One of the substrates for this reaction, (R)-1-aminopropan-2-ylphosphate, is produced by CobD (EC 4.1.1.81).','(1) ATP + adenosylcobyric acid + (R)-1-aminopropan-2-yl phosphate = ADP +phosphate + adenosylcobinamide phosphate.(2) ATP + adenosylcobyric acid + (R)-1-aminopropan-2-ol = ADP + phosphate+ adenosylcobinamide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.11','Glutamate--putrescine ligase','Gamma-glutamylputrescine synthetase','NULL','Forms part of a bacterial putrescine utilization pathway.','ATP + L-glutamate + putrescine = ADP + phosphate + gamma-L-glutamylputrescine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.12','D-aspartate ligase','Asl(fm)','NULL','Forms part of the peptidoglycan assembly pathway of Gram-positivebacteria grown in medium containing D-Asp.Normally, the side chains the acylate the 6-amino group of theL-lysine residue contain L-Ala-L-Ala but these amino acids arereplaced by D-Asp when D-Asp is included in the medium.Hybrid chains containing L-Ala-D-Asp, L-Ala-L-Ala-D-Asp or D-Asp-L-Ala are not formed.Highly specific for D-aspartate, as L-aspartate, D-glutamate,D-alanine, D-iso-asparagine and D-malic acid are not substrates.In Enterococcus faecium, the substrate D-aspartate is produced byEC 5.1.1.13.','ATP + D-aspartate + (beta-GlcNAc-(1-', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.13','L-cysteine:1D-myo-inositol 2-amino-2-deoxy-alpha-D-glucopyranosideligase','MshC ligase','NULL','This enzyme is a key enzyme in the biosynthesis of mycothiol, a smallmolecular weight thiol found in Mycobacteria spp. and otheractinomycetes.Mycothiol plays a fundamental role in these organisms by helping toprovide protection from the effects of reactive oxygen species andelectrophiles, including many antibiotics.The enzyme may represent a novel target for new classes ofantituberculars.Formerly EC 6.1.1.n1.','1-O-(2-amino-2-deoxy-alpha-D-glucopyranosyl)-1D-myo-inositol + L-cysteine+ ATP = 1-O-(2-(L-cysteinamido)-2-deoxy-alpha-D-glucopyranosyl)-1D-myo-inositol + AMP + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.14','Diphthine--ammonia ligase','Diphthamide synthase','NULL','This amidase catalyzes the last step in the conversion of anL-histidine residue in the translation elongation factor translationelongation factor 2 to diphthamide.This factor is found in all archaebacteria and eukaryotes, but not ineubacteria, and is the target of bacterial toxins such as thediphtheria toxin and the Pseudomonas exotoxin A (see EC 2.4.2.36).The substrate of the enzyme, diphthine, is produced by EC 2.1.1.98.Formerly EC 6.3.2.22.','ATP + diphthine-[translation elongation factor 2] + NH(3) = AMP +diphosphate + diphthamide-[translation elongation factor 2].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.15','8-demethylnovobiocic acid synthase','8-demethylnovobiocic acid synthetase','NULL','The enzyme is involved in the biosynthesis of the aminocoumarinantibiotic novobiocin.','ATP + 3-dimethylallyl-4-hydroxybenzoate + 3-amino-4,7-dihydroxycoumarin =AMP + diphosphate + 8-demethylnovobiocic acid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.16','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.1.17','Beta-citrylglutamate synthase','NAAG synthetase I','NULL','The enzyme, found in animals, also has the activity of EC 6.3.2.41.','ATP + citrate + L-glutamate = ADP + phosphate + beta-citryl-L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.1','Pantoate--beta-alanine ligase','Pantoate-activating enzyme','NULL','NULL','ATP + (R)-pantoate + beta-alanine = AMP + diphosphate + (R)-pantothenate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.2','Glutamate--cysteine ligase','Gamma-glutamyl-L-cysteine synthetase','NULL','Can use L-aminohexanoate in place of glutamate.','ATP + L-glutamate + L-cysteine = ADP + phosphate + gamma-L-glutamyl-L-cysteine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.3','Glutathione synthase','Glutathione synthetase','NULL','NULL','ATP + gamma-L-glutamyl-L-cysteine + glycine = ADP + phosphate +glutathione.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.4','D-alanine--D-alanine ligase','Alanine:alanine ligase (ADP-forming)','NULL','Involved with EC 6.3.2.7 or EC 6.3.2.13, EC 6.3.2.8, EC 6.3.2.9 andEC 6.3.2.10 in the synthesis of a cell-wall peptide.','ATP + 2 D-alanine = ADP + phosphate + D-alanyl-D-alanine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.5','Phosphopantothenate--cysteine ligase','Phosphopantothenoylcysteine synthetase','NULL','Cysteine can be replaced by some of its derivatives.','CTP + (R)-4-phosphopantothenate + L-cysteine = CMP + diphosphate +N-((R)-4-phosphopantothenoyl)-L-cysteine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.6','Phosphoribosylaminoimidazolesuccinocarboxamide synthase','4-((N-succinylamino)carbonyl)-5-aminoimidazole ribonucleotide synthetase','NULL','Forms part of the purine biosynthesis pathway.','ATP + 5-amino-1-(5-phospho-D-ribosyl)imidazole-4-carboxylate +L-aspartate = ADP + phosphate + (S)-2-(5-amino-1-(5-phospho-D-ribosyl)imidazole-4-carboxamido)succinate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.7','UDP-N-acetylmuramoyl-L-alanyl-D-glutamate--L-lysine ligase','L-lysine-adding enzyme','NULL','Involved with EC 6.3.2.4, EC 6.3.2.8, EC 6.3.2.9 and EC 6.3.2.10 inthe synthesis of a cell-wall peptide.This enzyme adds lysine in some Gram-positive organisms; in othersand in Gram-negative organisms EC 6.3.2.13 adds 2,6-diaminopimelateinstead.','ATP + UDP-N-acetylmuramoyl-L-alanyl-D-glutamate + L-lysine = ADP +phosphate + UDP-N-acetylmuramoyl-L-alanyl-D-glutamyl-L-lysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.8','UDP-N-acetylmuramate--L-alanine ligase','Alanine-adding enzyme','NULL','Involved with EC 6.3.2.4, EC 6.3.2.7 or EC 6.3.2.13, EC 6.3.2.9 andEC 6.3.2.10 in the synthesis of a cell-wall peptide.','ATP + UDP-N-acetylmuramate + L-alanine = ADP + phosphate + UDP-N-acetylmuramoyl-L-alanine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.9','UDP-N-acetylmuramoyl-L-alanine--D-glutamate ligase','D-glutamate ligase','NULL','Involved with EC 6.3.2.4, EC 6.3.2.7 or EC 6.3.2.13, EC 6.3.2.8 andEC 6.3.2.10 in the synthesis of a cell-wall peptide.','ATP + UDP-N-acetylmuramoyl-L-alanine + glutamate = ADP + phosphate + UDP-N-acetylmuramoyl-L-alanyl-D-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.10','UDP-N-acetylmuramoyl-tripeptide--D-alanyl-D-alanine ligase','MurF synthetase','NULL','Involved with EC 6.3.2.4, EC 6.3.2.7 or EC 6.3.2.13, EC 6.3.2.8 andEC 6.3.2.9 in the synthesis of a cell-wall peptide.Also catalyzes the reaction when the C-terminal residue of thetripeptide is meso-2,4-diaminoheptanedioate (acylated at itsL-center), linking the D-Ala--D-Ala to the carboxy group of theL-center.Formerly EC 6.3.2.15.','ATP + UDP-N-acetylmuramoyl-L-alanyl-gamma-D-glutamyl-L-lysine + D-alanyl-D-alanine = ADP + phosphate + UDP-N-acetylmuramoyl-L-alanyl-gamma-D-glutamyl-L-lysyl-D-alanyl-D-alanine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.11','Carnosine synthase','Carnosine synthetase','NULL','This enzyme was thought to form AMP, but studies with highly purifiedenzyme proved that it forms ADP.Carnosine is a dipeptide that is present at high concentrations inskeletal muscle and the olfactory bulb of vertebrates.It is also found in the skeletal muscle of some invertebrates.The enzyme can also catalyze the formation of homocarnosine from4-aminobutyrate and L-histidine, with much lower activity.','ATP + L-histidine + beta-alanine = ADP + phosphate + carnosine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.12','Dihydrofolate synthase','7,8-dihydrofolate synthetase','NULL','In some bacteria, a single protein catalyzes both this activity andthat of EC 6.3.2.17, the combined activity of which leads to theformation of the coenzyme polyglutamated tetrahydropteroate(H(4)PteGlu(n)), i.e. various tetrahydrofolates.In contrast, the activities are located on separate proteins in mosteukaryotes studied to date.This enzyme is reponsible for attaching the first glutamate residueto dihydropteroate to form dihydrofolate and is present only in thoseorganisms that have the ability to synthesize tetrahydrofolate denovo, e.g. plants, most bacteria, fungi and protozoa.','ATP + 7,8-dihydropteroate + L-glutamate = ADP + phosphate + 7,8-dihydropteroylglutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.13','UDP-N-acetylmuramoyl-L-alanyl-D-glutamate--2,6-diaminopimelate ligase','MurE synthetase','NULL','Involved with EC 6.3.2.4, EC 6.3.2.8, EC 6.3.2.9 and EC 6.3.2.10 inthe synthesis of a cell-wall peptide.This enzyme adds diaminopimelate in Gram-negative organisms and insome Gram-positive organisms; in others EC 6.3.2.7 adds lysineinstead.It is the amino group of the L-center of the diaminopimelate that isacylated.','ATP + UDP-N-acetylmuramoyl-L-alanyl-D-glutamate + meso-2,6-diaminoheptanedioate = ADP + phosphate + UDP-N-acetylmuramoyl-L-alanyl-D-gamma-glutamyl-meso-2,6-diamino-heptanedioate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.14','Enterobactin synthase','2,3-dihydroxybenzoate--serine ligase','NULL','This enzyme complex catalyzes the conversion of three molecules eachof 2,3-dihydroxybenzoate and L-serine to form the siderophoreenterobactin.In Escherichia coli the complex is formed by EntB (an aryl carrierprotein that has to be activated by 4-phosphopantetheine), EntD (aphosphopantetheinyl transferase that activates EntB), EntE (catalyzesthe ATP-dependent condensation of 2,3-dihydroxybenzoate and holo-EntBto form the covalently arylated form of EntB), and EntF (a fourdomain protein that catalyzes the activation of L-serine by ATP,the condensation of the activated L-serine with the activated 2,3-dihydroxybenzoate, and the trimerization of three such moieties to asingle enterobactin molecule).','6 ATP + 3 2,3-dihydroxybenzoate + 3 L-serine = enterobactin + 6 AMP +6 diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.15','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.16','D-alanine--alanyl-poly(glycerolphosphate) ligase','D-alanine:membrane-acceptor ligase','NULL','Involved in the synthesis of teichoic acids.','ATP + D-alanine + alanyl-poly(glycerolphosphate) = ADP + phosphate +D-alanyl-alanyl-poly(glycerolphosphate).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.17','Tetrahydrofolate synthase','Folate polyglutamate synthetase','NULL','In some bacteria, a single protein catalyzes both this activity andthat of EC 6.3.2.12, the combined activity of which leads to theformation of the coenzyme polyglutamated tetrahydropteroate(H(4)PteGlu(n)), i.e. various tetrahydrofolates (H(4)folate).In contrast, the activities are located on separate proteins in mosteukaryotes studied to date.In Arabidopsis thaliana, this enzyme is present as distinct isoformsin the mitochondria, the cytosol and the chloroplast.Each isoform is encoded by a separate gene, a situation that isunique among eukaryotes.As the affinity of folate-dependent enzymes increases markedly withthe number of glutamic residues, the tetrahydropteroyl polyglutamatesare the preferred coenzymes of C(1) metabolism.The enzymes from different sources (particularly eukaryotes versusprokaryotes) have different substrate specificities with regard toone-carbon substituents and the number of glutamate residues presenton the tetrahydrofolates.','ATP + tetrahydropteroyl-(gamma-Glu)(n) + L-glutamate = ADP + phosphate +tetrahydropteroyl-(gamma-Glu)(n+1).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.18','Gamma-glutamylhistamine synthase','Gamma-GHA synthetase','NULL','NULL','ATP + L-glutamate + histamine = products of ATP breakdown + N(alpha)-gamma-L-glutamylhistamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.19','Ubiquitin--protein ligase','Ubiquitin-activating enzyme','NULL','Ubiquitin is coupled to protein by a peptide bond between theC-terminal glycine of ubiquitin and epsilon-amino groups of lysineresidues in the protein.An intermediate in the reaction contains one ubiquitin residue boundas a thioester to the enzyme, and a residue of ubiquitin adenylatenon-covalently bound to the enzyme.','ATP + ubiquitin + protein lysine = AMP + diphosphate + proteinN-ubiquityllysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.20','Indoleacetate--lysine synthetase','IAA-lysine synthetase','NULL','NULL','ATP + (indol-3-yl)acetate + L-lysine = ADP + phosphate + N(6)-((indol-3-yl)acetyl)-L-lysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.21','Ubiquitin--calmodulin ligase','Ubiquityl-calmodulin synthase','NULL','Specific for Ca(2+)-calmodulin from vertebrates.At least 3 ubiquitin molecules can be coupled to lysine residues incalmodulin.','n ATP + calmodulin + n ubiquitin = n AMP + n diphosphate +(ubiquitin)(n)-calmodulin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.22','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.23','Homoglutathione synthase','Beta-alanine specific hGSH synthetase','NULL','Not identical with EC 6.3.2.3.','ATP + gamma-L-glutamyl-L-cysteine + beta-alanine = ADP + phosphate +gamma-L-glutamyl-L-cysteinyl-beta-alanine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.24','Tyrosine--arginine ligase','Kyotorphin synthase','NULL','NULL','ATP + L-tyrosine + L-arginine = AMP + diphosphate + L-tyrosyl-L-arginine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.25','Tubulin--tyrosine ligase','TTL','NULL','L-tyrosine is linked via a peptide bond to the C-terminus ofde-tyrosinated alpha-tubulin (des-Tyr(omega)-alpha-tubulin).The enzyme is highly specific for alpha-tubulin and moderatelyspecific for ATP and L-tyrosine.L-phenylalanine and 3,4-dihydroxy-L-phenylalanine are transferred butwith higher Km values.','ATP + detyrosinated alpha-tubulin + L-tyrosine = alpha-tubulin + ADP +phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.26','N-(5-amino-5-carboxypentanoyl)-L-cysteinyl-D-valine synthase','ACV synthetase','Magnesium.','The enzyme contains 4-phosphopantetheine, which may be involved inthe mechanism of the reaction.Forms part of the penicillin biosynthesis pathway.','L-2-aminohexanedioate + L-cysteine + L-valine + 3 ATP + H(2)O = N-(L-5-amino-5-carboxypentanoyl)-L-cysteinyl-D-valine + 3 AMP + 3 diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.27','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.28','L-amino-acid alpha-ligase','Bacilysin synthetase','Manganese or magnesium.','While the enzyme has extremely broad substrate specificity, it doesnot accept highly charged amino acids, such as Lys, Arg, Glu and Asp,nor does it react with secondary amines such as Pro.The N-terminal residue of the alpha-dipeptide formed seems to belimited to Ala, Gly, Ser, Thr and Met (with Ala and Ser being themost preferred), whereas the C-terminal residue seems to allow for awider variety of amino acids (but with a preference for Met and Phe).However, not all combinations or dipeptides are formed; for example,while Ser is acceptable for the N-terminus and Thr for theC-terminus, a Ser-Thr dipeptide is not formed.D-Ala, D-Ser and D-Phe are not substrates.','ATP + an L-amino acid + an L-amino acid = ADP + phosphate + L-aminoacyl-L-amino acid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.29','Cyanophycin synthase (L-aspartate-adding)','Cyanophycin synthetase','Magnesium.','Both this enzyme and EC 6.3.2.30 are required for the elongation ofcyanophycin, which is a protein-like cell inclusion that is unique tocyanobacteria and acts as a temporary nitrogen store.Both enzymes are found in the same protein but have different activesites.Both L-Asp and L-Arg must be present before either enzyme willdisplay significant activity.','ATP + (L-Asp(4-L-Arg))(n) + L-Asp = ADP + phosphate + (L-Asp(4-L-Arg))(n)-L-Asp.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.30','Cyanophycin synthase (L-arginine-adding)','Cyanophycin synthetase','Magnesium.','Both this enzyme and EC 6.3.2.29 are required for the elongation ofcyanophycin, which is a protein-like cell inclusion that is unique tocyanobacteria and acts as a temporary nitrogen store.Both enzymes are found in the same protein but have different activesites.Both L-Asp and L-Arg must be present before either enzyme willdisplay significant activity.Canavanine and lysine can be incoporated into the polymer instead ofarginine.','ATP + (L-Asp(4-L-Arg))(n)-L-Asp + L-Arg = ADP + phosphate + (L-Asp(4-L-Arg))(n+1).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.31','Coenzyme F420-0:L-glutamate ligase','NULL','NULL','This protein catalyzes the successive addition of two glutamateresidues to cofactor F420 by two distinct and independent reactions.In the reaction described here the enzyme attaches a glutamate viaits alpha-amine group to F420-0.In the second reaction (EC 6.3.2.34) it catalyzes the addition of asecond L-glutamate residue to the gamma-carboxyl of the firstglutamate.','GTP + coenzyme F420-0 + L-glutamate = GDP + phosphate + coenzyme F420-1.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.32','Coenzyme gamma-F420-2:alpha-L-glutamate ligase','NULL','NULL','The enzyme caps the gamma-glutamyl tail of the hydride carriercoenzyme F420.','ATP + coenzyme gamma-F420-2 + L-glutamate = ADP + phosphate + coenzymealpha-F420-3.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.33','Tetrahydrosarcinapterin synthase','NULL','NULL','This enzyme catalyzes the biosynthesis of 5,6,7,8-tetrahydrosarcinapterin, a modified form of tetrahydromethanopterinfound in the Methanosarcinales.It does not require K(+), and does not discriminate between ATP andGTP.','ATP + tetrahydromethanopterin + L-glutamate = ADP + phosphate + 5,6,7,8-tetrahydrosarcinapterin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.34','Coenzyme F420-1:gamma-L-glutamate ligase','NULL','NULL','This protein catalyzes the successive addition of two glutamateresidues to cofactor F420 by two distinct and independent reactions.In the first reaction (EC 6.3.2.31) the enzyme attaches a glutamatevia its alpha-amine group to F420-0.In the second reaction, which is described here, the enzyme catalyzesthe addition of a second L-glutamate residue to the gamma-carboxyl ofthe first glutamate.','GTP + coenzyme F420-1 + L-glutamate = GDP + phosphate + coenzyme gamma-F420-2.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.35','D-alanine--D-serine ligase','NULL','NULL','The product of this enzyme, D-alanyl-D-serine, can be incorporatedinto the peptidoglycan pentapeptide instead of the usual D-alanyl-D-alanine dipeptide, which is formed by EC 6.3.2.4, D-alanine--D-alanine ligase.The resulting peptidoglycan does not bind the glycopeptideantibiotics vancomycin and teicoplanin, conferring resistance on thebacteria.','D-alanine + D-serine + ATP = D-alanyl-D-serine + ADP + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.36','4-phosphopantoate--beta-alanine ligase','Phosphopantothenate synthetase','NULL','The conversion of (R)-pantoate to (R)-4-phosphopantothenate is partof the pathway leading to biosynthesis of 4-phosphopantetheine,an essential cofactor of coenzyme A and acyl-carrier protein.In bacteria and eukaryotes this conversion is performed bycondensation with beta-alanine, followed by phosphorylation(EC 6.3.2.1 and EC 2.7.1.33, respectively).In archaea the order of these two steps is reversed,and phosphorylation precedes condensation with beta-alanine.The two archaeal enzymes that catalyze this conversion areEC 2.7.1.169 and this enzyme.','ATP + (R)-4-phosphopantoate + beta-alanine = AMP + diphosphate + (R)-4-phosphopantothenate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.37','UDP-N-acetylmuramoyl-L-alanyl-D-glutamate--D-lysine ligase','D-lysine-adding enzyme','NULL','The enzyme from Thermotoga maritima also performs the reaction ofEC 6.3.2.7.Involved in the synthesis of cell-wall peptidoglycan.Formerly EC 6.3.2.n1.','ATP + UDP-N-acetylmuramoyl-L-alanyl-D-glutamate + D-lysine = ADP +phosphate + UDP-N-acetylmuramoyl-L-alanyl-D-glutamyl-D-lysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.38','N(2)-citryl-N(6)-acetyl-N(6)-hydroxylysine synthase','N(alpha)-citryl-N(epsilon)-acetyl-N(epsilon)-hydroxylysine synthase','Magnesium.','Aerobactin is one of a group of high-affinity iron chelators known assiderophores and is produced under conditions of iron deprivation.It is a dihydroxamate comprising two molecules of N(6)-acetyl-N(6)-hydroxy-L-lysine and one molecule of citrate.This enzyme catalyzes the first of two synthase reactions to linkN(6)-acetyl-N(6)-hydroxy-L-lysine and citrate.Formerly EC 6.3.2.27.','2 ATP + citrate + N(6)-acetyl-N(6)-hydroxy-L-lysine + H(2)O = 2 ADP +2 phosphate + N(2)-citryl-N(6)-acetyl-N(6)-hydroxy-L-lysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.39','Aerobactin synthase','NULL','Magnesium.','Aerobactin is one of a group of high-affinity iron chelators known assiderophores and is produced under conditions of iron deprivation.It is a dihydroxamate comprising two molecules of N(6)-acetyl-N(6)-hydroxy-L-lysine and one molecule of citric acid.This enzyme catalyzes the second of two synthase reactions to linkN(6)-acetyl-N(6)-hydroxy-L-lysine and citrate.Formerly EC 6.3.2.27.','2 ATP + N(2)-citryl-N(6)-acetyl-N(6)-hydroxy-L-lysine + N(6)-acetyl-N(6)-hydroxy-L-lysine + H(2)O = 2 ADP + 2 phosphate + aerobactin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.40','Cyclopeptine synthase','NULL','NULL','Cyclopeptine synthase is the key enzyme of benzodiazepine alkaloidbiosynthesis in the fungus Penicillium cyclopium.The enzyme is a non-ribosomal peptide synthase.Formerly EC 2.7.7.54 and EC 2.7.7.55.','2 ATP + S-adenosyl-L-methionine + anthranilate + L-phenylalanine =cyclopeptine + 2 AMP + 2 diphosphate + S-adenosyl-L-homocysteine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.41','N-acetylaspartylglutamate synthase','N-acetylaspartylglutamate synthetase','NULL','The enzyme, found in animals, produces the neurotransmitter N-acetyl-L-aspartyl-L-glutamate.One isoform also has the activity of EC 6.3.1.17, while anotherisoform has the activity of EC 6.3.2.42.Formerly EC 6.3.2.n6.','ATP + N-acetyl-L-aspartate + L-glutamate = ADP + phosphate + N-acetyl-L-aspartyl-L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.42','N-acetylaspartylglutamylglutamate synthase','N-acetylaspartylglutamylglutamate synthetase','NULL','The enzyme, found in mammals, also has the activity of EC 6.3.2.41.','2 ATP + N-acetyl-L-aspartate + 2 L-glutamate = 2 ADP + 2 phosphate +N-acetyl-L-aspartyl-L-glutamyl-L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.n1','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.n2','Pup--protein ligase','Pup-conjugating enzyme','NULL','The enzyme catalyzes the covalent attachment of the prokaryoticubiquitin-like protein modifier Pup to the proteasomal substrateproteins, thereby targeting them for proteasomal degradation.This tagging system is termed pupylation.The ligation reaction involves the side-chain carboxylate of theC-terminal glutamate of Pup and the side-chain amino group of asubstrate lysine.PafA is required to confer resistance against the lethal effects ofreactive nitrogen intermediates (RNI), antimicrobial moleculesproduced by activated macrophages and other cell types.','ATP + [Pup] + [protein]-lysine = ADP + phosphate + [protein]-N-pupyllysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.n3','ISG15--protein ligase','ISG15-conjugating enzyme','NULL','The enzyme from human also functions as ubiquitin--protein ligase(EC 6.3.2.19).','ATP + [ISG15] + [protein]-lysine = AMP + diphosphate + [protein]-N-ISGyllysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.n4','Alpha-aminoadipate--LysW ligase','AAA--LysW ligase','NULL','The enzyme ligates the L-2-aminoadipate to the C-terminal glutamateresidue of LysW.This is the first step in the AAA pathway of L-lysine biosynthesis.','ATP + [LysW] + L-2-aminoadipate = ADP + phosphate + [LysW]-L-2-aminoadipate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.n5','Pantoate--beta-alanine ligase (ADP-forming)','Pantothenate synthetase','NULL','The enzyme is found in Archaea.','ATP + (R)-pantoate + beta-alanine = ADP + phosphate + (R)-pantothenate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.2.n6','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.3.1','Phosphoribosylformylglycinamidine cyclo-ligase','AIR synthase','NULL','NULL','ATP + 2-(formamido)-N(1)-(5-phospho-D-ribosyl)acetamidine = ADP +phosphate + 5-amino-1-(5-phospho-D-ribosyl)imidazole.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.3.2','5-formyltetrahydrofolate cyclo-ligase','5,10-methenyltetrahydrofolate synthetase','NULL','NULL','ATP + 5-formyltetrahydrofolate = ADP + phosphate + 5,10-methenyltetrahydrofolate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.3.3','Dethiobiotin synthase','DTB synthetase','NULL','CTP has half the activity of ATP.','ATP + 7,8-diaminononanoate + CO(2) = ADP + phosphate + dethiobiotin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.3.4','(Carboxyethyl)arginine beta-lactam-synthase','Beta-lactam synthetase','NULL','Forms part of the pathway for the biosythesis of the beta-lactamaseinhibitor clavulanate in Streptomyces clavuligerus.It has been proposed that L-N(2)-(2-carboxyethyl)arginine is firstconverted into an acyl-AMP by reaction with ATP and loss ofdiphosphate, and that the beta-lactam ring is then formed by theintramolecular attack of the beta-nitrogen on the activated carboxygroup.','ATP + L-N(2)-(2-carboxyethyl)arginine = AMP + diphosphate +deoxyamidinoproclavaminate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.3.5','O-ureido-D-serine cyclo-ligase','NULL','NULL','The enzyme participates in the biosynthetic pathway of D-cycloserine,an antibiotic substance produced by several Streptomyces species.','O-ureido-D-serine + ATP + H(2)O = D-cycloserine + CO(2) + ammonia + ADP +phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.3.6','Carbapenam-3-carboxylate synthase','Carbapenam-3-carboxylate ligase','NULL','The enzyme is involved in the biosynthesis of the carbapenem beta-lactam antibiotic (5R)-carbapen-2-em-3-carboxylate in the bacteriumPectobacterium carotovorum.Formerly EC 6.3.1.16.','ATP + (2S,5S)-5-carboxymethylproline = AMP + diphosphate + (3S,5S)-carbapenam 3-carboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.1','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.2','CTP synthase (glutamine hydrolyzing)','CTP synthetase','NULL','The enzyme contains three functionally distinct sites: an allostericGTP-binding site, a glutaminase site where glutamine hydrolysisoccurs (cf. EC 3.5.1.2), and the active site where CTP synthesistakes place.The reaction proceeds via phosphorylation of UTP by ATP to give anactivated intermediate 4-phosphoryl UTP and ADP.Ammonia then reacts with this intermediate generating CTP and aphosphate.The enzyme can also use ammonia from the surrounding solution.','ATP + UTP + L-glutamine = ADP + phosphate + CTP + L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.3','Formate--tetrahydrofolate ligase','Formyltetrahydrofolate synthetase','NULL','In eukaryotes occurs as a trifunctional enzyme also having EC 1.5.1.5and EC 3.5.4.9 activity.','ATP + formate + tetrahydrofolate = ADP + phosphate +10-formyltetrahydrofolate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.4','Adenylosuccinate synthase','Adenylosuccinate synthetase','NULL','NULL','GTP + IMP + L-aspartate = GDP + phosphate + N(6)-(1,2-dicarboxyethyl)-AMP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.5','Argininosuccinate synthase','Arginine succinate synthetase','NULL','NULL','ATP + L-citrulline + L-aspartate = AMP + diphosphate + N(omega)-(L-arginino)succinate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.6','Urea carboxylase','ATP--urea amidolyase','Biotin.','The Saccharomyces cerevisiae enzyme (but not that from green algae)also catalyzes the reaction of EC 3.5.1.54, thus bringing about thehydrolysis of urea to CO(2) and NH(3).The enzyme from the prokaryotic bacterium Oleomonas sagaranensis canalso use acetamide and formamide as substrates.Formerly EC 3.5.1.45.','ATP + urea + HCO(3)(-) = ADP + phosphate + urea-1-carboxylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.7','Ribose-5-phosphate--ammonia ligase','5-phosphoribosylamine synthetase','NULL','NULL','ATP + ribose 5-phosphate + NH(3) = ADP + phosphate +5-phosphoribosylamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.8','Imidazoleacetate--phosphoribosyldiphosphate ligase','5-phosphoribosylimidazoleacetate synthetase','NULL','NULL','ATP + imidazole-4-acetate + 5-phosphoribosyl diphosphate = ADP +phosphate + 1-(5-phosphoribosyl)imidazole-4-acetate + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.9','Biotin--[methylmalonyl-CoA-carboxytransferase] ligase','Biotin--[methylmalonyl-CoA-carboxyltransferase] ligase','NULL','NULL','ATP + biotin + apo-[methylmalonyl-CoA:pyruvate carboxytransferase] =AMP + diphosphate + [methylmalonyl-CoA:pyruvate carboxytransferase].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.10','Biotin--[propionyl-CoA-carboxylase (ATP-hydrolyzing)] ligase','Biotin-[propionyl-CoA-carboxylase (ATP-hydrolyzing)] synthetase','NULL','NULL','ATP + biotin + apo-[propionyl-CoA:carbon-dioxide ligase (ADP-forming)] =AMP + diphosphate + [propionyl-CoA:carbon-dioxide ligase (ADP-forming)].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.11','Biotin--[methylcrotonoyl-CoA-carboxylase] ligase','Biotin--[methylcrotonoyl-CoA-carboxylase] synthetase','NULL','NULL','ATP + biotin + apo-[3-methylcrotonoyl-CoA:carbon-dioxide ligase (ADP-forming)] = AMP + diphosphate + [3-methylcrotonoyl-CoA:carbon-dioxideligase (ADP-forming)].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.12','Glutamate--methylamine ligase','Gamma-glutamylmethylamide synthetase','NULL','NULL','ATP + L-glutamate + methylamine = ADP + phosphate + N(5)-methyl-L-glutamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.13','Phosphoribosylamine--glycine ligase','GAR synthetase','NULL','Formerly EC 6.3.1.3.','ATP + 5-phospho-D-ribosylamine + glycine = ADP + phosphate + N(1)-(5-phospho-D-ribosyl)glycinamide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.14','Biotin carboxylase','NULL','NULL','NULL','ATP + biotin-[carboxyl-carrier-protein] + CO(2) = ADP + phosphate +carboxy-biotin-[carboxyl-carrier-protein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.15','Biotin--[acetyl-CoA-carboxylase] ligase','Acetyl-CoA carboxylase biotin holoenzyme synthetase','NULL','NULL','ATP + biotin + apo-[acetyl-CoA:carbon-dioxide ligase (ADP-forming)] =AMP + diphosphate + [acetyl-CoA:carbon-dioxide ligase (ADP-forming)].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.16','Carbamoyl-phosphate synthase (ammonia)','Carbamoyl-phosphate synthetase (ammonia)','NULL','Formerly EC 2.7.2.5.','2 ATP + NH(3) + CO(2) + H(2)O = 2 ADP + phosphate + carbamoyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.17','Formate--dihydrofolate ligase','Dihydrofolate formyltransferase','NULL','NULL','ATP + formate + dihydrofolate = ADP + phosphate + 10-formyldihydrofolate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.18','5-(carboxyamino)imidazole ribonucleotide synthase','N(5)-CAIR synthetase','NULL','In Escherichia coli, this enzyme, along with EC 5.4.99.18,is required to carry out the single reaction catalyzed by EC 4.1.1.21in vertebrates.Carboxyphosphate is the putative acyl phosphate intermediate.Involved in the late stages of purine biosynthesis.','ATP + 5-amino-1-(5-phospho-D-ribosyl)imidazole + HCO(3)(-) = ADP +phosphate + 5-carboxyamino-1-(5-phospho-D-ribosyl)imidazole.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.19','tRNA(Ile)-lysidine synthetase','Isoleucine-specific transfer ribonucleate lysidine synthetase','NULL','The bacterial enzyme modifies the wobble base of the CAU anticodon oftRNA(Ile) at the oxo group in position 2 of cytidine(34).This modification determines both codon and amino acid specificitiesof tRNA(Ile).','(tRNA(Ile2))-cytidine(34) + L-lysine + ATP = (tRNA(Ile2))-lysidine(34) +AMP + diphosphate + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.20','7-cyano-7-deazaguanine synthase','7-cyano-7-carbaguanine synthase','NULL','The reaction is part of the biosynthesis pathway of queuosine.','7-carboxy-7-carbaguanine + NH(3) + ATP = 7-cyano-7-carbaguanine + ADP +phosphate + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.21','Nicotinate phosphoribosyltransferase','Niacin ribonucleotidase','NULL','The enzyme, which is involved in pyridine nucleotide recycling,can form beta-nicotinate D-ribonucleotide and diphosphate fromnicotinate and 5-phospho-alpha-D-ribose 1-diphosphate (PRPP) in theabsence of ATP.However, when ATP is available the enzyme is phosphorylated resultingin a much lower K(m) for nicotinate.The phospho-enzyme is hydrolyzed during the transferase reaction,regenerating the low affinity form.The presence of ATP shifts the products/substrates equilibrium from0.67 to 1100.Formerly EC 2.4.2.11.','Nicotinate + 5-phospho-alpha-D-ribose 1-diphosphate + ATP + H(2)O = beta-nicotinate D-ribonucleotide + diphosphate + ADP + phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.22','tRNA(Ile)(2)-agmatinylcytidine synthase','tRNA(Ile)-2-agmatinylcytidine synthetase','NULL','The enzyme from the archaeon Archaeoglobus fulgidus modifies thewobble base of the CAU anticodon of the archaeal tRNA(Ile)(2) at theoxo group in position 2 of cytidine(34).This modification is crucial for accurate decoding of the geneticcode.In bacteria EC 6.3.4.19, catalyzes the modification of(tRNA(Ile)(2))-cytidine(34) to (tRNA(Ile)(2))-lysidine(34).','ATP + agmatine + (tRNA(Ile)(2))-cytidine(34) + H(2)O = (tRNA(Ile)(2))-2-agmatinylcytidine(34) + AMP + 2 phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.4.23','Formate--phosphoribosylaminoimidazolecarboxamide ligase','5-formaminoimidazole-4-carboxamide ribonucleotide synthetase','NULL','This archaeal enzyme, characterized from the methanogenMethanocaldococcus jannaschii, catalyzes a step in the synthesis ofpurine nucleotides.It differs from the orthologous bacterial/eukaryotic enzymes, whichutilize 10-formyltetrahydrofolate rather than formate and ATP.Cf. EC 2.1.2.3.','ATP + formate + 5-amino-1-(5-phospho-D-ribosyl)imidazole-4-carboxamide =ADP + phosphate + 5-formamido-1-(5-phospho-D-ribosyl)imidazole-4-carboxamide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.1','NAD(+) synthase (glutamine-hydrolyzing)','NAD(+) synthetase (glutamine-hydrolyzing)','NULL','NH(3) can act instead of glutamine (cf. EC 6.3.1.5).','ATP + deamido-NAD(+) + L-glutamine + H(2)O = AMP + diphosphate + NAD(+) +L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.2','GMP synthase (glutamine-hydrolyzing)','GMP synthetase (glutamine-hydrolyzing)','NULL','Involved in the de novo biosynthesis of guanosine nucleotides.An N-terminal glutaminase domain binds L-glutamine and generatesammonia, which is transferred by a substrate-protective tunnel to theATP-pyrophosphatase domain.The enzyme can catalyze the second reaction alone in the presence ofammonia.Formerly EC 6.3.4.1.','ATP + XMP + L-glutamine + H(2)O = AMP + diphosphate + GMP + L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.3','Phosphoribosylformylglycinamidine synthase','FGAM synthase','NULL','NULL','ATP + N(2)-formyl-N(1)-(5-phospho-D-ribosyl)glycinamide + L-glutamine +H(2)O = ADP + phosphate + 2-(formamido)-N(1)-(5-phospho-D-ribosyl)acetamidine + L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.4','Asparagine synthase (glutamine-hydrolyzing)','AS-B','NULL','The enzyme from Escherichia coli has two active sites that areconnected by an intramolecular ammonia tunnel.The enzyme catalyzes three distinct chemical reactions: glutaminehydrolysis to yield ammonia takes place in the N-terminal domain.The C-terminal active site mediates both the synthesis of a beta-aspartyl-AMP intermediate and its subsequent reaction with ammonia.The ammonia released is channeled to the other active site to yieldasparagine.','ATP + L-aspartate + L-glutamine + H(2)O = AMP + diphosphate +L-asparagine + L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.5','Carbamoyl-phosphate synthase (glutamine-hydrolyzing)','Carbamoyl phosphate synthetase','NULL','The product carbamoyl phosphate is an intermediate in thebiosynthesis of arginine and the pyrimidine nucleotides.The enzyme from Escherichia coli has three separate active sites,which are connected by a molecular tunnel that is almost 100 A inlength.The amidotransferase domain within the small subunit of the enzymehydrolyzes glutamine to ammonia via a thioester intermediate.The ammonia migrates through the interior of the protein, where itreacts with carboxy phosphate to produce the carbamate intermediate.The carboxy-phosphate intermediate is formed by the phosphorylationof bicarbonate by ATP at a site contained within the N-terminal halfof the large subunit.The carbamate intermediate is transported through the interior of theprotein to a second site within the C-terminal half of the largesubunit, where it is phosphorylated by another ATP to yield the finalproduct, carbamoyl phosphate.Formerly EC 2.7.2.9.','2 ATP + L-glutamine + HCO(3)(-) + H(2)O = 2 ADP + phosphate + L-glutamate+ carbamoyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.6','Asparaginyl-tRNA synthase (glutamine-hydrolyzing)','Asp-AdT','NULL','This reaction forms part of a two-reaction system for producingasparaginyl-tRNA in Deinococcus radiodurans and other organismslacking a specific enzyme for asparagine synthesis.In the first step, a non-discriminating ligase (EC 6.1.1.23)mischarges tRNA(Asn) with aspartate, leading to the formation ofaspartyl-tRNA(Asn).The aspartyl-tRNA(Asn) is not used in protein synthesis until thepresent enzyme converts it into asparaginyl-tRNA(Asn) (aspartyl-tRNA(Asp) is not a substrate for this reaction).Ammonia or asparagine can substitute for the preferred substrateglutamine.','ATP + L-aspartyl-tRNA(Asn) + L-glutamine + H(2)O = ADP + phosphate +L-asparaginyl-tRNA(Asn) + L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.7','Glutaminyl-tRNA synthase (glutamine-hydrolyzing)','Glu-AdT','NULL','In systems lacking discernible EC 6.1.1.18, glutaminyl-tRNA(Gln) isformed by a two-enzyme system.In the first step, a nondiscriminating ligase (EC 6.1.1.24)mischarges tRNA(Gln) with glutamate, forming glutamyl-tRNA(Gln).The glutamyl-tRNA(Gln) is not used in protein synthesis until thepresent enzyme converts it into glutaminyl-tRNA(Gln) (glutamyl-tRNA(Glu) is not a substrate for this reaction).Ammonia or asparagine can substitute for the preferred substrateglutamine.','ATP + L-glutamyl-tRNA(Gln) + L-glutamine = ADP + phosphate +L-glutaminyl-tRNA(Gln) + L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.8','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.9','Hydrogenobyrinic acid a,c-diamide synthase (glutamine-hydrolyzing)','NULL','NULL','This step in the aerobic biosynthesis of cobalamin generateshydrogenobyrinic acid a,c-diamide, the substrate required byEC 6.6.1.2, which adds cobalt to the macrocycle.','2 ATP + hydrogenobyrinic acid + 2 L-glutamine + 2 H(2)O = 2 ADP +2 phosphate + hydrogenobyrinic acid a,c-diamide + 2 L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.10','Adenosylcobyric acid synthase (glutamine-hydrolyzing)','5-deoxy-5-adenosylcobyrinic-acid-a,c-diamide:L-glutamine amido-ligase','Magnesium.','NH(3) can act instead of glutamine.This enzyme catalyzes the four-step amidation sequence from cobyrinicacid a,c-diamide to cobyric acid via the formation of cobyrinic acidtriamide, tetraamide and pentaamide intermediates.','4 ATP + adenosylcobyrinic acid a,c-diamide + 4 L-glutamine + 4 H(2)O =4 ADP + 4 phosphate + adenosylcobyric acid + 4 L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.3.5.11','Cobyrinate a,c-diamide synthase (glutamine-hydrolyzing)','Cobyrinic acid a,c-diamide synthetase','NULL','This enzyme is the first glutamine amidotransferase that participatesin the anaerobic (early cobalt insertion) biosynthetic pathway ofadenosylcobalamin, and catalyzes the ATP-dependent synthesis ofcobyrinate a,c-diamide from cobyrinate using either L-glutamine orammonia as the nitrogen source.It is proposed that the enzyme first catalyzes the amidation of thec-carboxylate, and then the intermediate is released into solutionand binds to the same catalytic site for the amidation of thea-carboxylate.The Km for ammonia is substantially higher than that for L-glutamine.','2 ATP + cobyrinate + 2 L-glutamine + 2 H(2)O = 2 ADP + 2 phosphate +cobyrinate a,c-diamide + 2 L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.4.1.1','Pyruvate carboxylase','Pyruvic carboxylase','Biotin; Zinc or manganese.','The animal enzyme requires acetyl-CoA.','ATP + pyruvate + HCO(3)(-) = ADP + phosphate + oxaloacetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.4.1.2','Acetyl-CoA carboxylase','NULL','Biotin.','Also catalyzes transcarboxylation; the plant enzyme also carboxylatespropanoyl-CoA and butanoyl-CoA.','ATP + acetyl-CoA + HCO(3)(-) = ADP + phosphate + malonyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.4.1.3','Propionyl-CoA carboxylase','PCCase','Biotin.','Also carboxylates butanoyl-CoA and catalyzes transcarboxylation.','ATP + propanoyl-CoA + HCO(3)(-) = ADP + phosphate + (S)-methylmalonyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.4.1.4','Methylcrotonoyl-CoA carboxylase','Beta-methylcrotonyl-CoA carboxylase','Biotin.','NULL','ATP + 3-methylcrotonoyl-CoA + HCO(3)(-) = ADP + phosphate +3-methylglutaconyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.4.1.5','Geranoyl-CoA carboxylase','Geranyl-CoA carboxylase','Biotin.','Also carboxylates dimethylpropenoyl-CoA and farnesoyl-CoA.','ATP + geranoyl-CoA + HCO(3)(-) = ADP + phosphate + 3-(4-methylpent-3-en-1-yl)pent-2-enedioyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.4.1.6','Acetone carboxylase','NULL','Magnesium.','The enzyme from Xanthobacter sp. strain Py2 also carboxylates butan-2-one to 3-oxopentanoate.','Acetone + CO(2) + ATP + 2 H(2)O = acetoacetate + AMP + 2 phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.4.1.7','2-oxoglutarate carboxylase','Carboxylating factor for ICDH','Biotin; Magnesium.','It was originally thought that this enzyme was a promoting factor forthe carboxylation of 2-oxoglutarate by EC 1.1.1.41, but this hassince been disproved.The product of the reaction is unstable and is quickly converted intoisocitrate by the action of EC 1.1.1.41.','ATP + 2-oxoglutarate + HCO(3)(-) = ADP + phosphate + oxalosuccinate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.4.1.8','Acetophenone carboxylase','NULL','NULL','The enzyme is involved in anaerobic degradation of ethylbenzene.No activity with acetone, butanone, 4-hydroxy-acetophenone or4-amino-acetophenone.','2 ATP + acetophenone + HCO(3)(-) + H(2)O + H(+) = 2 ADP + 2 phosphate +3-oxo-3-phenylpropanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.5.1.1','DNA ligase (ATP)','DNA joinase','NULL','Catalyzes the formation of a phosphodiester at the site of a single-strand break in duplex DNA.RNA can also act as substrate, to some extent.','ATP + (deoxyribonucleotide)(n) + (deoxyribonucleotide)(m) = AMP +diphosphate + (deoxyribonucleotide)(n+m).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.5.1.2','DNA ligase (NAD(+))','DNA joinase','NULL','Catalyzes the formation of a phosphodiester at the site of a single-strand break in duplex DNA.RNA can also act as substrate, to some extent.','NAD(+) + (deoxyribonucleotide)(n) + (deoxyribonucleotide)(m) = AMP +beta-nicotinamide D-ribonucleotide + (deoxyribonucleotide)(n+m).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.5.1.3','RNA ligase (ATP)','Polyribonucleotide synthase (ATP)','NULL','Converts linear RNA to a circular form by transfer of the5-phosphate to the 3-hydroxy terminus.','ATP + (ribonucleotide)(n) + (ribonucleotide)(m) = AMP + diphosphate +(ribonucleotide)(n+m).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.5.1.4','RNA 3-terminal-phosphate cyclase (ATP)','RNA cyclase','NULL','Adenosine 5-(gamma-thio)triphosphate can act instead of ATP (cf.EC 6.5.1.5).','ATP + RNA 3-terminal-phosphate = AMP + diphosphate + RNA terminal-2,3-cyclic-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.5.1.5','RNA 3-terminal-phosphate cyclase (GTP)','RNA-3-phosphate cyclase (GTP)','NULL','Cf. EC 6.5.1.4.','GTP + RNA 3-terminal-phosphate = GMP + diphosphate + RNA terminal-2,3-cyclic-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.6.1.1','Magnesium chelatase','Magnesium-chelatase','NULL','This is the first committed step of chlorophyll biosynthesis and is abranchpoint of two major routes in the tetrapyrrole pathway.','ATP + protoporphyrin IX + Mg(2+) + H(2)O = ADP + phosphate +Mg-protoporphyrin IX + 2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 6.6.1.2','Cobaltochelatase','Hydrogenobyrinic acid a,c-diamide cobaltochelatase','NULL','This enzyme, which forms part of the aerobic cobalamin biosynthesispathway, is a type I chelatase, being heterotrimeric and ATP-dependent.It comprises two components, one of which corresponds to CobN and theother is composed of two polypeptides, specified by cobS and cobT inPseudomonas denitrificans, and named CobST.Hydrogenobyrinic acid is a very poor substrate.ATP can be replaced by dATP or CTP but the reaction proceeds moreslowly.CobN exhibits a high affinity for hydrogenobyrinic acid a,c-diamide.The oligomeric protein CobST possesses at least one sulfhydryl groupthat is essential for ATP-binding.Once the Co(2+) is inserted, the next step in the pathway ensuresthat the cobalt is ligated securely by reducing Co(II) to Co(I); thisstep is carried out by EC 1.16.8.1.','ATP + hydrogenobyrinic acid a,c-diamide + Co(2+) + H(2)O = ADP +phosphate + cob(II)yrinic acid a,c-diamide + H(2).', 'NULL', 'NULL');
